import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './ProfessorEnrollments.css';

const ProfessorEnrollments = () => {
  const [pendingEnrollments, setPendingEnrollments] = useState([]);
  const [courses, setCourses] = useState([]);
  const [courseId, setCourseId] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();
  const token = localStorage.getItem('token');
  const [memberId, setMemberId] = useState(null); // ÍµêÏàò memberId

  useEffect(() => {
    if (!token) {
      alert("Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
      navigate('/login');
      return;
    }

    // localStorageÏóêÏÑú memberId Í∞ÄÏ†∏Ïò§Í∏∞ (ÏóÜÏúºÎ©¥ Î∞±ÏóîÎìúÏóêÏÑú Î∂àÎü¨Ïò§Í∏∞)
    const storedMemberId = localStorage.getItem('memberId'); // Ï†ÄÏû•Îêú memberId Í∞ÄÏ†∏Ïò§Í∏∞
    if (storedMemberId) {
      setMemberId(storedMemberId); // Ï†ÄÏû•Îêú memberId ÏÇ¨Ïö©
    } else {
      fetchMemberId(); // Î∞±ÏóîÎìúÏóêÏÑú memberIdÎ•º Í∞ÄÏ†∏Ïò§Îäî Ìï®Ïàò
    }

    fetchCourses(); // Í∞ïÏùò Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞
  }, [token, navigate]);

  useEffect(() => {
    // ÍµêÏàòÏùò ID Ï∂îÏ∂ú (JWT ÌÜ†ÌÅ∞ÏóêÏÑú)
    const parsedToken = token ? JSON.parse(atob(token.split('.')[1])) : {};
    setMemberId(parsedToken.username); // ÍµêÏàò IDÎ•º set

    // Í∞ïÏùò Î™©Î°ù Í∞ÄÏ†∏Ïò§Í∏∞
    fetchCourses();
  }, [token]);

  useEffect(() => {
    // courseIdÍ∞Ä ÏûàÏúºÎ©¥ Ìï¥Îãπ Í∞ïÏùòÏùò ÏàòÌóòÏÉù Î™©Î°ùÏùÑ Î∂àÎü¨Ïò¥
    if (courseId) {
      console.log("üöÄ Fetching pending enrollments for courseId:", courseId);
      fetchPendingEnrollments(courseId);
    } 
    // ÍµêÏàòÏùò IDÍ∞Ä ÏûàÏùÑ Îïå, ÍµêÏàòÏùò ÏàòÌóòÏÉù Î™©Î°ùÏùÑ Î∂àÎü¨Ïò¥
    else if (memberId) {
      console.log("üöÄ Fetching pending enrollments for professor memberId:", memberId);
      fetchPendingEnrollments(memberId);
    }
  }, [courseId, memberId]);

  // Î∞±ÏóîÎìúÏóêÏÑú memberId Í∞ÄÏ†∏Ïò§Í∏∞
  const fetchMemberId = async () => {
    try {
      const response = await axios.get('http://localhost:8090/api/users/me', {
        headers: { Authorization: `Bearer ${token}` }
      });
      const fetchedMemberId = response.data.id;
      setMemberId(fetchedMemberId);
      localStorage.setItem('memberId', fetchedMemberId); // memberIdÎ•º localStorageÏóê Ï†ÄÏû•
    } catch (error) {
      console.error("ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ Í∞ÄÏ†∏Ïò§Í∏∞ Ïã§Ìå®:", error);
      alert("ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥Î•º Î∂àÎü¨Ïò§ÏßÄ Î™ªÌñàÏäµÎãàÎã§. Îã§Ïãú Î°úÍ∑∏Ïù∏Ìï¥Ï£ºÏÑ∏Ïöî.");
      navigate('/login');
    }
  };

  useEffect(() => {
    if (courseId) {
      console.log("üöÄ Fetching pending enrollments for courseId:", courseId);
      fetchPendingEnrollments(courseId); // Í∞ïÏùòÎ•º ÏÑ†ÌÉùÌñàÏúºÎ©¥ Ìï¥Îãπ Í∞ïÏùòÏùò ÏàòÌóòÏÉù Î™©Î°ùÏùÑ Î∂àÎü¨Ïò¥
    } else if (memberId) {
      console.log("üöÄ Fetching pending enrollments for professor memberId:", memberId);
      fetchPendingEnrollments(memberId); // ÍµêÏàòÏùò IDÎ°ú ÏàòÌóòÏÉù Î™©Î°ùÏùÑ Î∂àÎü¨Ïò¥
    }
  }, [courseId, memberId]);

  const fetchCourses = async () => {
    try {
      const response = await axios.get('http://localhost:8090/api/courses', {
        headers: { Authorization: `Bearer ${token}` }
      });
      console.log("üöÄ Fetched courses:", response.data);
      setCourses(response.data);
    } catch (error) {
      console.error("Í∞ïÏùò Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:", error);
    }
  };

  const fetchPendingEnrollments = async (id) => {
    try {
      const url = courseId
        ? `http://localhost:8090/api/enrollments/course/${courseId}/pending-enrollments`
        : `http://localhost:8090/api/enrollments/member/${id}/pending-enrollments`;

      console.log(`üöÄ Fetching enrollments from: ${url}`);

      const response = await axios.get(url, {
        headers: { Authorization: `Bearer ${token}` }
      });

      console.log("üöÄ Response Data:", response.data);
      setPendingEnrollments(response.data);
    } catch (error) {
      console.error("ÏàòÌóòÏÉù Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:", error);
    } finally {
      setLoading(false);
    }
  };

  const handleApproval = async (enrollmentId, status) => {
    try {
      // ÏäπÏù∏/Í±∞Ï†à ÏÉÅÌÉúÏóê ÎßûÍ≤å API Ìò∏Ï∂ú
      const response = await axios.put(`http://localhost:8090/api/enrollments/${enrollmentId}/${status === "approve" ? "accept" : "reject"}`, {}, {
        headers: { Authorization: `Bearer ${token}` }
      });

      // ÏÉÅÌÉúÏóê Îî∞Î•∏ ÏïåÎ¶º
      alert(`ÏàòÍ∞ï Ïã†Ï≤≠ ${status === "approve" ? "ÏäπÏù∏" : "Í±∞Ï†à"} ÏôÑÎ£å!`);

      // ÏäπÏù∏/Í±∞Ï†àÎêú Ïã†Ï≤≠ Ìï≠Î™© Ï†úÍ±∞
      setPendingEnrollments(pendingEnrollments.filter(e => e.id !== enrollmentId));
    } catch (error) {
      alert(`ÏàòÍ∞ï Ïã†Ï≤≠ ${status === "approve" ? "ÏäπÏù∏" : "Í±∞Ï†à"} Ïã§Ìå®`);
      console.error("Error in approval/rejection", error);
    }
  };

const getStatusLabel = (status) => {
  switch (status) {
    case "APPROVED":
      return <span className="status approved">ÏäπÏù∏</span>;
    case "REJECTED":
      return <span className="status rejected">Í±∞Ï†à</span>;
    default:
      return <span className="status pending">ÎåÄÍ∏∞Ï§ë</span>;
  }
};
 // ÏûêÏã†Ïùò Í∞ïÏùòÎßå ÌïÑÌÑ∞ÎßÅ
 const filteredCourses = courses.filter(course => course.professorId === memberId);
return (
  <div className="professor-enrollments-container">
    <h2>üìã ÏàòÍ∞ï Ïã†Ï≤≠ ÏäπÏù∏ ÌéòÏù¥ÏßÄ</h2>

    <select onChange={(e) => setCourseId(e.target.value)} value={courseId || ""}>
      <option value="">Í∞ïÏùòÎ•º ÏÑ†ÌÉùÌïòÏÑ∏Ïöî</option>
      {courses.map(course => (
        <option key={course.id} value={course.id}>
          {course.name}
        </option>
      ))}
    </select>

    {pendingEnrollments.length === 0 ? (
      <p>ÏäπÏù∏ ÎåÄÍ∏∞ Ï§ëÏù∏ Ïã†Ï≤≠Ïù¥ ÏóÜÏäµÎãàÎã§.</p>
    ) : (
      <ul className="enrollment-list">
        {pendingEnrollments.map((enrollment) => (
          <li key={enrollment.id} className="enrollment-item">
            <p>üë§ ÌïôÏÉù: {enrollment.member ? enrollment.member.name : 'Ï†ïÎ≥¥ ÏóÜÏùå'}</p>
            <p>üìö Í∞ïÏùò: {enrollment.course ? enrollment.course.name : 'Ï†ïÎ≥¥ ÏóÜÏùå'}</p>
            <p>ÏÉÅÌÉú: {getStatusLabel(enrollment.status)}</p>
            <button onClick={() => handleApproval(enrollment.id, "approve")}>‚úÖ ÏäπÏù∏</button>
            <button onClick={() => handleApproval(enrollment.id, "reject")}>‚ùå Í±∞Ï†à</button>
          </li>
        ))}
      </ul>
    )}
  </div>
);
}

export default ProfessorEnrollments;
