import React, { useState, useEffect } from 'react';
import axios from 'axios';

import TeamEvaluation from './TeamEvaluation';
import SurveyForm from './SurveyForm';
import LectureSurvey from './LectureSurvey';
import './SurveyPage.css';

function SurveyPage() {
    const [activeTab, setActiveTab] = useState('lecture');
    const [ setLectureSurveys] = useState([]);
    const [ setGeneralSurveys] = useState([]);
    const token = localStorage.getItem("token");

    useEffect(() => {
        if (!token) return;

        const fetchSurveys = async () => {
            try {
                const lectureResponse = await axios.get('/api/surveys/lecture', {
                    headers: { Authorization: `Bearer ${token}` }
                });
                const generalResponse = await axios.get('/api/surveys/general', {
                    headers: { Authorization: `Bearer ${token}` }
                });

                setLectureSurveys(lectureResponse.data);
                setGeneralSurveys(generalResponse.data);
            } catch (error) {
                console.error("üö® ÏÑ§Î¨∏ Îç∞Ïù¥ÌÑ∞ Î∂àÎü¨Ïò§Í∏∞ Ïò§Î•ò:", error);
            }
        };

        fetchSurveys();
    }, [setGeneralSurveys, setLectureSurveys, token]);

    return (
        <div className="dashboard">
            
            <main className="main-content">
                <h2>ÏÑ§Î¨∏ ÌéòÏù¥ÏßÄ</h2>
                <button onClick={() => setActiveTab('lecture')}>Í∞ïÏùò ÌèâÍ∞Ä</button>
                <button onClick={() => setActiveTab('team')}>ÌåÄÏõê ÌèâÍ∞Ä</button>
                <button onClick={() => setActiveTab('survey')}>ÏùºÎ∞ò ÏÑ§Î¨∏</button>

                {activeTab === 'lecture' && <LectureSurvey />}
                {activeTab === 'team' && <TeamEvaluation />}
                {activeTab === 'survey' && <SurveyForm />}
            </main>
        </div>
    );
}

export default SurveyPage;
