import React, { useState, useEffect } from 'react';
import { Bar } from 'react-chartjs-2';
import {
    Chart as ChartJS,
    CategoryScale,
    LinearScale,
    BarElement,
    Title,
    Tooltip,
    Legend,
} from 'chart.js';
import axios from 'axios';
import './LearningStatus.css';
import { useParams } from 'react-router-dom';

// Chart.js Íµ¨ÏÑ± ÏöîÏÜå Îì±Î°ù
ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

function LearningStatus() {
    const { studentId } = useParams(); // URLÏóêÏÑú studentId Í∞ÄÏ†∏Ïò§Í∏∞
    const [studentCourses, setStudentCourses] = useState([]); // ÌïôÏÉùÏùò ÌïôÏäµ Îç∞Ïù¥ÌÑ∞
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState("");
    const token = localStorage.getItem("token"); // ‚úÖ JWT ÌÜ†ÌÅ∞ Í∞ÄÏ†∏Ïò§Í∏∞

    // ‚úÖ APIÏóêÏÑú ÌïôÏäµ ÌòÑÌô© Îç∞Ïù¥ÌÑ∞ Î∂àÎü¨Ïò§Í∏∞
    useEffect(() => {
        if (!token) {
            console.warn("‚ö†Ô∏è ÌÜ†ÌÅ∞Ïù¥ ÏóÜÏäµÎãàÎã§. Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
            setError("Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
            setLoading(false);
            return;
        }

        const fetchLearningStatus = async () => {
            try {
                const response = await axios.get(`/api/learning-status/${studentId}`, {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setStudentCourses(response.data.courses);
            } catch (error) {
                console.error("üö® ÌïôÏäµ Îç∞Ïù¥ÌÑ∞ Î∂àÎü¨Ïò§Í∏∞ Ïò§Î•ò:", error);
                setError("ÌïôÏäµ Îç∞Ïù¥ÌÑ∞Î•º Î∂àÎü¨Ïò§Îäî Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
            } finally {
                setLoading(false);
            }
        };

        fetchLearningStatus();
    }, [studentId, token]);

    if (loading) return <div>üì° ÌïôÏäµ Îç∞Ïù¥ÌÑ∞Î•º Î∂àÎü¨Ïò§Îäî Ï§ë...</div>;
    if (error) return <p className="error-message">{error}</p>;

    // ÌïôÏäµ ÏßÑÎèÑ Îç∞Ïù¥ÌÑ∞ Ï§ÄÎπÑ
    const progressData = {
        labels: studentCourses.map((course) => course.course_name),
        datasets: [
            {
                label: 'ÏßÑÎèÑÏú® (%)',
                data: studentCourses.map((course) => course.progress),
                backgroundColor: 'rgba(75, 192, 192, 0.6)',
                borderColor: 'rgba(75, 192, 192, 1)',
                borderWidth: 1,
            },
        ],
    };

    return (
        <div className="learning-status-dashboard">
            <main className="learning-status-content">
                <h2 className="learning-status-title">ÌïôÏäµ ÌòÑÌô©</h2>

                {/* ÌïôÏäµ ÏßÑÎèÑ Í∑∏ÎûòÌîÑ */}
                <div className="learning-status-chart-container">
                    <h3 className="learning-status-subtitle">Í∞ïÏ¢åÎ≥Ñ ÌïôÏäµ ÏßÑÎèÑ</h3>
                    {studentCourses.length > 0 ? (
                        <Bar data={progressData} options={{ responsive: true, plugins: { legend: { position: 'top' } } }} />
                    ) : (
                        <p>Îì±Î°ùÎêú ÌïôÏäµ Îç∞Ïù¥ÌÑ∞Í∞Ä ÏóÜÏäµÎãàÎã§.</p>
                    )}
                </div>

                {/* ÏÑ±Ï†Å Î∞è Ï∂úÍ≤∞ Ï†ïÎ≥¥ ÌÖåÏù¥Î∏î */}
                <h3 className="learning-status-subtitle">ÏÑ±Ï†Å Î∞è Ï∂úÍ≤∞ Ï†ïÎ≥¥</h3>
                <table className="learning-status-table">
                    <thead>
                        <tr>
                            <th>Í∞ïÏ¢åÎ™Ö</th>
                            <th>ÏßÑÎèÑÏú®</th>
                            <th>ÏÑ±Ï†Å</th>
                            <th>Ï∂úÍ≤∞</th>
                        </tr>
                    </thead>
                    <tbody>
                        {studentCourses.length > 0 ? (
                            studentCourses.map((course) => (
                                <tr key={course.course_id}>
                                    <td>{course.course_name}</td>
                                    <td>{course.progress}%</td>
                                    <td>{course.score || 'ÎØ∏Ï†ï'}</td>
                                    <td>{course.attendance}</td>
                                </tr>
                            ))
                        ) : (
                            <tr>
                                <td colSpan="4">Îì±Î°ùÎêú ÌïôÏäµ Îç∞Ïù¥ÌÑ∞Í∞Ä ÏóÜÏäµÎãàÎã§.</td>
                            </tr>
                        )}
                    </tbody>
                </table>
            </main>
        </div>
    );
}

export default LearningStatus;
