import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';

const AssignmentDetail = () => {
    const { courseId, weekNumber } = useParams();
    const [assignments, setAssignments] = useState([]);
    const [courseName, setCourseName] = useState("");
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(true);
    const [selectedFile, setSelectedFile] = useState(null);
    const [uploading, setUploading] = useState(false);
    const [isProfessor, setIsProfessor] = useState(false);
    const [isStudent, setIsStudent] = useState(false);
    const token = localStorage.getItem('token');
    const studentId = localStorage.getItem('studentId'); // 로그인 시 저장한 학번

    useEffect(() => {
        const fetchData = async () => {
            if (!token) {
                setError('로그인 정보가 없습니다.');
                setLoading(false);
                return;
            }
            try {
                // 과제 목록 불러오기
                const assignmentsResponse = await axios.get(
                    `http://localhost:8090/api/courses/${courseId}/weeks/${weekNumber}/assignments`,
                    { headers: { Authorization: `Bearer ${token}` } }
                );
                setAssignments(assignmentsResponse.data);

                // 강좌 정보 불러오기
                const courseResponse = await axios.get(
                    `http://localhost:8090/api/courses/${courseId}`,
                    { headers: { Authorization: `Bearer ${token}` } }
                );
                setCourseName(courseResponse.data.name);

                // 사용자 정보 불러와 역할 확인하기
                const userResponse = await axios.get(
                    'http://localhost:8090/api/users/me',
                    { headers: { Authorization: `Bearer ${token}` } }
                );
                if (userResponse.data.role === 'PROFESSOR') {
                    setIsProfessor(true);
                } else if (userResponse.data.role === 'STUDENT') {
                    setIsStudent(true);
                }
            } catch (err) {
                console.error('데이터를 불러오는 중 오류 발생:', err);
                setError('데이터를 불러오는 데 실패했습니다.');
            } finally {
                setLoading(false);
            }
        };

        fetchData();
    }, [courseId, weekNumber, token]);

    const handleFileChange = (e) => {
        setSelectedFile(e.target.files[0]);
    };

    const handleSubmitAssignment = async (assignmentId) => {
        if (!selectedFile) {
            alert('파일을 선택해주세요.');
            return;
        }
        setUploading(true);
        const formData = new FormData();
        formData.append('file', selectedFile);

        try {
            await axios.post(
                `http://localhost:8090/api/submissions/${assignmentId}/submit`,
                formData,
                {
                    headers: {
                        Authorization: `Bearer ${token}`,
                        'Content-Type': 'multipart/form-data',
                        studentId: studentId // 헤더에 학번 전달
                    }
                }
            );
            alert('과제가 성공적으로 제출되었습니다!');
        } catch (err) {
            console.error('과제 제출 실패:', err);
            alert('과제 제출에 실패했습니다. 다시 시도해주세요.');
        } finally {
            setUploading(false);
            setSelectedFile(null);
        }
    };

    if (loading) return <div>로딩 중...</div>;
    if (error) return <div style={{ color: 'red' }}>{error}</div>;

    return (
        <div>
            <h2>{courseName} 강좌의 {weekNumber}주차 과제</h2>
            {assignments.length > 0 ? (
                <ul>
                    {assignments.map((assignment) => (
                        <li key={assignment.id}>
                            <p>제목: {assignment.title}</p>
                            <p>설명: {assignment.description}</p>
                            <p>마감일: {new Date(assignment.dueDate).toLocaleDateString()}</p>
                            {isProfessor && (
    <div>
        {console.log(`평가 버튼 링크: /api/courses/${courseId}/weeks/${weekNumber}/assignments/${assignment.id}/grade`)}
        <Link to={`/api/courses/${courseId}/weeks/${weekNumber}/assignments/${assignment.id}/grade`}>
            <button>과제 평가하기</button>
        </Link>
    </div>
)}

                            {isStudent && (
                                <div>
                                    <input type="file" onChange={handleFileChange} />
                                    <button onClick={() => handleSubmitAssignment(assignment.id)} disabled={uploading}>
                                        {uploading ? '제출 중...' : '과제 제출'}
                                    </button>
                                </div>
                            )}
                        </li>
                    ))}
                </ul>
            ) : (
                <p>해당 주차에 과제가 없습니다.</p>
            )}
        </div>
    );
};

export default AssignmentDetail;
