import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';
import './QuizPage.css';

function QuizPage() {
    const { courseId, examId } = useParams();
    const [quizzes, setQuizzes] = useState([]);
    const [currentQuestion, setCurrentQuestion] = useState(0);
    const [answers, setAnswers] = useState({});
    const [score, setScore] = useState(null);
    const [quizCompleted, setQuizCompleted] = useState(false);
    const [errorMessage, setErrorMessage] = useState(null);
    const token = localStorage.getItem("token");
    const navigate = useNavigate();

    const fetchQuizzes = useCallback(async () => {
        if (!examId || !token) {
            console.error("시험 ID가 필요합니다.");
            return;
        }

        try {
            const response = await axios.get(`http://localhost:8090/api/exams/${examId}/questions`, {
                headers: { Authorization: `Bearer ${token}` }
            });
            setQuizzes(response.data);
        } catch (error) {
            console.error("🚨 퀴즈 데이터 불러오기 오류:", error);
        }
    }, [examId, token]);

    useEffect(() => {
        fetchQuizzes();
    }, [fetchQuizzes]);

    const handleAnswerChange = (selectedAnswer) => {
        setAnswers((prevAnswers) => ({
            ...prevAnswers,
            [quizzes[currentQuestion].id]: selectedAnswer,
        }));
    };

    const handleTextAnswerChange = (text) => {
        setAnswers((prevAnswers) => ({
            ...prevAnswers,
            [quizzes[currentQuestion].id]: text,
        }));
    };

    const handleNextQuestion = () => {
        if (currentQuestion < quizzes.length - 1) {
            setCurrentQuestion((prev) => prev + 1);
        } else {
            handleQuizSubmit();
        }
    };

    const handleQuizSubmit = useCallback(async () => {
        const allAnswered = quizzes.every((quiz) => answers[quiz.id] !== undefined);
        if (!allAnswered) {
            setErrorMessage("모든 질문에 답변을 입력해야 합니다.");
            return;
        }
    
        try {
            // 퀴즈 답안 제출
            const response = await axios.post(`http://localhost:8090/api/quizzes/submit`, {
                courseId: courseId,
                answers: answers
            }, {
                headers: { Authorization: `Bearer ${token}` }
            });
    
            // 퀴즈 채점 요청 (자동 채점)
            const submission = {
                studentId: studentId,  // 학생 ID
                answers: answers,      // 제출한 답안
                examId: examId         // 시험 ID
            };
    
            const gradingResponse = await axios.post('http://localhost:8090/api/grade/exam', submission, {
                headers: { Authorization: `Bearer ${token}` }
            });
    
            setScore(gradingResponse.data);  // 점수 설정
            setQuizCompleted(true);  // 퀴즈 완료 상태
            setErrorMessage(null);  // 오류 메시지 초기화
        } catch (error) {
            console.error("🚨 퀴즈 제출 오류:", error);
            setErrorMessage("퀴즈 제출 중 오류가 발생했습니다. 다시 시도해주세요.");
        }
    }, [courseId, answers, token, quizzes, studentId, examId]);
    

    return (
        <div className="dashboard">
            <main className="main-content">
                <h2>퀴즈</h2>

                {/* 퀴즈 진행 중 */}
                {quizzes.length > 0 && (
                    <div className="quiz-container">
                        <div className="quiz-navigation">
                            <ul>
                                {quizzes.map((quiz, index) => (
                                    <li
                                        key={quiz.id}
                                        onClick={() => setCurrentQuestion(index)}
                                        className={currentQuestion === index ? 'active' : ''}
                                    >
                                        {index + 1}
                                    </li>
                                ))}
                            </ul>
                        </div>

                        <div className="quiz-question" style={{ userSelect: 'none' }}>
                            <div className="quiz-item">
                                <h3>{quizzes[currentQuestion].questionText}</h3>
                                <div>
                                    {quizzes[currentQuestion].type === 'ESSAY' ? (
                                        <textarea
                                            rows="4"
                                            cols="50"
                                            value={answers[quizzes[currentQuestion].id] || ''}
                                            onChange={(e) => handleTextAnswerChange(e.target.value)}
                                            placeholder="답변을 입력하세요..."
                                        />
                                    ) : (
                                        quizzes[currentQuestion].answers.map((option, index) => (
                                            <label key={index} className="quiz-option">
                                                <input
                                                    type="radio"
                                                    name={`quiz-${quizzes[currentQuestion].id}`}
                                                    value={option}
                                                    onChange={() => handleAnswerChange(option)}
                                                    checked={answers[quizzes[currentQuestion].id] === option}
                                                />
                                                {option}
                                            </label>
                                        ))
                                    )}
                                </div>
                            </div>
                        </div>

                        <div className="quiz-footer">
                            <button onClick={handleNextQuestion} className="next-btn">
                                {currentQuestion < quizzes.length - 1 ? "다음 문제" : "퀴즈 제출"}
                            </button>
                        </div>
                    </div>
                )}

                {/* 퀴즈 완료 후 결과 표시 */}
                {quizCompleted && (
                    <div className="score-result">
                        <h3>퀴즈 완료! 당신의 점수: {score} / {quizzes.length}</h3>
                    </div>
                )}

                {/* 오류 메시지 표시 */}
                {errorMessage && <div className="error-message">{errorMessage}</div>}

                <button className="back-btn" onClick={() => navigate(`/courses/${courseId}`)}>뒤로가기</button>
            </main>
        </div>
    );
}

export default QuizPage;
