import { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import "./QnADetail.css";

const QnADetail = () => {
  const { courseId, qnaId } = useParams();
  const [qna, setQna] = useState(null);
  const [answers, setAnswers] = useState([]);
  const [answerContent, setAnswerContent] = useState("");
  const [editingAnswerId, setEditingAnswerId] = useState(null);
  const [editedContent, setEditedContent] = useState("");
  const [memberId, setMemberId] = useState(null);
  const token = localStorage.getItem("token");

  useEffect(() => {
    const fetchData = async () => {
      await fetchUserData();
      await fetchQnADetail();
      await fetchAnswers();
    };
    fetchData();
  }, [courseId, qnaId]);

  const fetchUserData = async () => {
    try {
      const response = await axios.get("http://localhost:8090/api/users/me", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setMemberId(response.data.id);
    } catch (error) {
      console.error("ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ Ï°∞Ìöå Ïã§Ìå®:", error);
    }
  };

  const fetchQnADetail = async () => {
    try {
      const response = await axios.get(`http://localhost:8090/courses/${courseId}/qna/${qnaId}`);
      setQna(response.data);
    } catch (error) {
      console.error("QnA Ï†ïÎ≥¥Î•º Î∂àÎü¨Ïò¨ Ïàò ÏóÜÏäµÎãàÎã§.", error.response || error);
    }
  };

  const fetchAnswers = async () => {
    try {
      const response = await axios.get(`http://localhost:8090/courses/${courseId}/qna/${qnaId}/answers`);
      setAnswers(response.data);
    } catch (error) {
      console.error("ÎãµÎ≥ÄÏùÑ Î∂àÎü¨Ïò¨ Ïàò ÏóÜÏäµÎãàÎã§.", error.response || error);
    }
  };

  const handleAnswerSubmit = async () => {
    if (!answerContent.trim()) {
      alert("ÎãµÎ≥Ä ÎÇ¥Ïö©ÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.");
      return;
    }
  
    try {
      const response = await axios.post(
        `http://localhost:8090/courses/${courseId}/qna/${qnaId}/answers`,
        { content: answerContent }, // ‚úÖ Î≥ÄÍ≤Ω: contentÎßå Í∞ùÏ≤¥Î°ú Í∞êÏã∏Í∏∞
        {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json",
            memberId: memberId,
          },
        }
      );
  
      setAnswers([...answers, response.data]);
      setAnswerContent("");
    } catch (error) {
      console.error("ÎãµÎ≥Ä Îì±Î°ù Ïã§Ìå®:", error.response || error);
    }
  };

  const handleDeleteAnswer = async (answerId) => {
    if (!window.confirm("Ï†ïÎßê ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå?")) return;

    try {
      await axios.delete(`http://localhost:8090/courses/${courseId}/qna/${qnaId}/answers/${answerId}`, {
        headers: { 
          Authorization: `Bearer ${token}`,
          memberId: memberId, // üîπ Ìó§ÎçîÏóê memberId Ï∂îÍ∞Ä
        },
      });
      setAnswers(answers.filter((answer) => answer.id !== answerId));
    } catch (error) {
      console.error("ÏÇ≠Ï†ú Ïã§Ìå®:", error.response || error);
    }
  };

  const handleEditAnswer = (answerId, content) => {
    setEditingAnswerId(answerId);
    setEditedContent(content);
  };

  const handleUpdateAnswer = async (answerId) => {
    if (!editedContent.trim()) {
      alert("ÏàòÏ†ïÌï† ÎÇ¥Ïö©ÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.");
      return;
    }

    try {
      await axios.put(
        `http://localhost:8090/courses/${courseId}/qna/${qnaId}/answers/${answerId}`,
        { content: editedContent }, // ÏöîÏ≤≠ Î≥∏Î¨∏ÏóêÏÑú memberId Ï†úÍ±∞
        { 
          headers: { 
            Authorization: `Bearer ${token}`, 
            "Content-Type": "application/json",
            memberId: memberId, // üîπ Ìó§ÎçîÏóê memberId Ï∂îÍ∞Ä
          } 
        }
      );

      setAnswers(
        answers.map((answer) => (answer.id === answerId ? { ...answer, content: editedContent } : answer))
      );
      setEditingAnswerId(null);
    } catch (error) {
      console.error("ÏàòÏ†ï Ïã§Ìå®:", error.response || error);
    }
  };

  if (!qna) return <p>Î°úÎî© Ï§ë...</p>;

  return (
    <div className="qna-detail">
      <h2>{qna.title}</h2>
      <p><strong>ÏûëÏÑ±Ïûê:</strong> {qna.author || "Ïïå Ïàò ÏóÜÏùå"}</p>
      <p><strong>ÏûëÏÑ±Ïùº:</strong> {new Date(qna.createdAt).toLocaleString()}</p>
      <hr />
      <p>{qna.content}</p>

      <h3>ÎãµÎ≥Ä</h3>
      {answers.length > 0 ? (
        answers.map((answer) => (
          <div key={answer.id} className="answer">
            {editingAnswerId === answer.id ? (
              <div>
                <textarea value={editedContent} onChange={(e) => setEditedContent(e.target.value)} />
                <button onClick={() => handleUpdateAnswer(answer.id)} className="save-btn">Ï†ÄÏû•</button>
                <button onClick={() => setEditingAnswerId(null)} className="cancel-btn">Ï∑®ÏÜå</button>
              </div>
            ) : (
              <div>
                <p><strong>{answer.authorName || "Ïïå Ïàò ÏóÜÏùå"}</strong>: {answer.content}</p>
                {Number(answer.authorId) === Number(memberId) && (
                  <div className="answer-buttons">
                    <button onClick={() => handleEditAnswer(answer.id, answer.content)} className="edit-btn">ÏàòÏ†ï</button>
                    <button onClick={() => handleDeleteAnswer(answer.id)} className="delete-btn">ÏÇ≠Ï†ú</button>
                  </div>
                )}
              </div>
            )}
          </div>
        ))
      ) : (
        <p>ÏïÑÏßÅ ÎãµÎ≥ÄÏù¥ ÏóÜÏäµÎãàÎã§.</p>
      )}

      <textarea
        value={answerContent}
        onChange={(e) => setAnswerContent(e.target.value)}
        placeholder="ÎãµÎ≥ÄÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî..."
        className="answer-input"
      />
      <button onClick={handleAnswerSubmit} className="submit-btn">ÎãµÎ≥Ä Îì±Î°ù</button>
    </div>
  );
};

export default QnADetail;
