import React, { useState, useEffect, useMemo } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function GeneralSurvey({ onComplete }) {
  const navigate = useNavigate();
  const [groupedSurveys, setGroupedSurveys] = useState({});
  const [selectedSurveyType, setSelectedSurveyType] = useState(null);
  const [selectedTimeGroup, setSelectedTimeGroup] = useState(null); // Ïó¨Í∏∞ÏÑúÎäî 'ÌÉÄÏù¥ÌãÄ Í∑∏Î£π'ÏúºÎ°ú ÏÇ¨Ïö©
  const [responses, setResponses] = useState({});
  const [submitted, setSubmitted] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [memberId, setMemberId] = useState(null);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const token = useMemo(() => localStorage.getItem("token"), []);

  const surveyTypeNames = {
    "GENERAL_SURVEY": "Ï†ïÍ∏∞ ÏÑ§Î¨∏Ï°∞ÏÇ¨",
    "MEAL_SURVEY": "Ï†ïÍ∏∞ Í∏âÏãù ÎßåÏ°±ÎèÑ",
    "ACADEMIC_SURVEY": "Ï†ïÍ∏∞ ÌïôÍµê ÏÑ§Î¨∏"
  };

  // ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ Í∞ÄÏ†∏Ïò§Í∏∞
  useEffect(() => {
    if (!token) {
      setError("Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
      setLoading(false);
      return;
    }
    const fetchUserInfo = async () => {
      try {
        const response = await axios.get('http://localhost:8090/api/users/me', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setMemberId(response.data.id);
      } catch (error) {
        alert("ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥Î•º Î∂àÎü¨Ïò¨ Ïàò ÏóÜÏäµÎãàÎã§. Îã§Ïãú Î°úÍ∑∏Ïù∏Ìï¥Ï£ºÏÑ∏Ïöî.");
        navigate('/login');
      }
    };
    fetchUserInfo();
  }, [token, navigate]);

  // ÏÑ§Î¨∏ Îç∞Ïù¥ÌÑ∞ Í∞ÄÏ†∏ÏôÄÏÑú ÌÉÄÏù¥ÌãÄÎ≥ÑÎ°ú Í∑∏Î£πÌôîÌïòÍ∏∞
  useEffect(() => {
    if (!token || !memberId) return;
    const fetchSurveys = async () => {
      try {
        const response = await axios.get('http://localhost:8090/api/surveys', {
          headers: { Authorization: `Bearer ${token}` }
        });
        console.log("survey data:", response.data);
        const grouped = response.data.reduce((acc, survey) => {
          // ÌäπÏ†ï surveyType Ï†úÏô∏ (ÌïÑÏöîÏãú Ï°∞Í±¥ ÏàòÏ†ï)
          if (survey.surveyType === "LECTURE_EVALUATION" || survey.surveyType === "TEAM_EVALUATION") {
            return acc;
          }
          if (!acc[survey.surveyType]) {
            acc[survey.surveyType] = {};
          }
          // ÌÉÄÏù¥ÌãÄÎ≥ÑÎ°ú Í∑∏Î£πÌôî (ÏãúÏûëÏãúÍ∞Ñ ÎåÄÏã† survey.title ÏÇ¨Ïö©)
          const titleKey = survey.title || "ÎØ∏Ï†ï";
          if (!acc[survey.surveyType][titleKey]) {
            acc[survey.surveyType][titleKey] = [];
          }
          acc[survey.surveyType][titleKey].push(survey);
          return acc;
        }, {});
        setGroupedSurveys(grouped);
        setLoading(false);
      } catch (error) {
        setError("ÏÑ§Î¨∏ Îç∞Ïù¥ÌÑ∞Î•º Î∂àÎü¨Ïò§Îäî Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
        setLoading(false);
      }
    };
    fetchSurveys();
  }, [token, memberId]);

  // ÏÑ§Î¨∏ Ïú†Ìòï ÏÑ†ÌÉù Ïãú
  const handleSelectSurveyType = (surveyType) => {
    setSelectedSurveyType(surveyType);
    setSelectedTimeGroup(null);
    setResponses({});
  };

  // ÌÉÄÏù¥ÌãÄ Í∑∏Î£π(ÏÑ§Î¨∏ Î™©Î°ù) ÏÑ†ÌÉù Ïãú
  const handleSelectTimeGroup = (groupKey) => {
    setSelectedTimeGroup(groupKey);
    setResponses({});
    setCurrentQuestionIndex(0);
  };

  // Ï†úÏ∂ú Ï≤òÎ¶¨ (ÏÑ†ÌÉùÌïú Í∑∏Î£πÏùò ÏÑ§Î¨∏Îßå Ï≤òÎ¶¨)
  const handleSubmit = async () => {
    const currentGroupSurveys = groupedSurveys[selectedSurveyType][selectedTimeGroup];
    if (Object.keys(responses).length !== currentGroupSurveys.length) {
      alert("Î™®Îì† ÏßàÎ¨∏Ïóê ÎãµÎ≥ÄÌï¥Ïïº Ìï©ÎãàÎã§!");
      return;
    }
  
    // Ï§ëÎ≥µ Ï≤¥ÌÅ¨
    for (let [questionId] of Object.entries(responses)) {
      try {
        await axios.post(
          "http://localhost:8090/api/survey-responses/check",
          { surveyId: questionId, memberId: memberId },
          { headers: { Authorization: `Bearer ${token}` } }
        );
      } catch (error) {
        if (error.response && error.response.status === 400) {
          alert(error.response.data);
          return;
        } else {
          alert("Ï§ëÎ≥µ Ï≤¥ÌÅ¨ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
          return;
        }
      }
    }
  
    const requestData = {
      memberId: memberId,
      responses: Object.entries(responses).map(([questionId, response]) => ({
        surveyId: questionId,
        response: response
      }))
    };
  
    try {
      await axios.post("http://localhost:8090/api/survey-responses/submit", requestData, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setSubmitted(true);
      alert("‚úÖ ÏÑ§Î¨∏Ïù¥ ÏÑ±Í≥µÏ†ÅÏúºÎ°ú Ï†úÏ∂úÎêòÏóàÏäµÎãàÎã§!");
      if (onComplete) onComplete();
    } catch (error) {
      alert("ÏÑ§Î¨∏ Ï†úÏ∂ú Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
    }
  };

  // ÏÑ§Î¨∏ Ïú†Ìòï Î™©Î°ùÏúºÎ°ú ÎèåÏïÑÍ∞ÄÍ∏∞
  const handleBackToSurveyType = () => {
    if (onComplete) {
      onComplete();
    } else {
      setSelectedSurveyType(null);
      setSelectedTimeGroup(null);
      setResponses({});
    }
  };

  // ÌÉÄÏù¥ÌãÄ Í∑∏Î£π(ÏÑ§Î¨∏ Î™©Î°ù) ÌôîÎ©¥ÏúºÎ°ú ÎèåÏïÑÍ∞ÄÍ∏∞
  const handleBackToGroupList = () => {
    setSelectedTimeGroup(null);
    setResponses({});
  };

  if (loading) return <p>üì° ÏÑ§Î¨∏ Îç∞Ïù¥ÌÑ∞Î•º Î∂àÎü¨Ïò§Îäî Ï§ë...</p>;
  if (error) return <p className="error-message">{error}</p>;
  if (submitted) {
    return (
      <div className="survey-container">
        <p>üéâ ÏÑ§Î¨∏Ïù¥ Ï†úÏ∂úÎêòÏóàÏäµÎãàÎã§! Í∞êÏÇ¨Ìï©ÎãàÎã§.</p>
        <button onClick={handleBackToSurveyType}>Î™©Î°ùÏúºÎ°ú ÎèåÏïÑÍ∞ÄÍ∏∞</button>
      </div>
    );
  }

  // ÏÑ§Î¨∏ Ïú†Ìòï ÏÑ†ÌÉù ÌôîÎ©¥ (LectureSurveyÏôÄ Ïú†ÏÇ¨Ìïú Ïä§ÌÉÄÏùº)
  if (!selectedSurveyType) {
    return (
      <div className="survey-container">
        <h2>ÏÑ§Î¨∏ Ïú†Ìòï ÏÑ†ÌÉù</h2>
        <div className="course-selection">
          {Object.keys(groupedSurveys).map((type) => (
            <div key={type} className="course-item">
              <span>{surveyTypeNames[type] || type}</span>
              <button onClick={() => handleSelectSurveyType(type)}>ÏÑ§Î¨∏ ÏÑ†ÌÉù</button>
            </div>
          ))}
        </div>
      </div>
    );
  }

  // ÏÑ†ÌÉùÌïú ÏÑ§Î¨∏ Ïú†Ìòï ÎÇ¥ ÌÉÄÏù¥ÌãÄ Í∑∏Î£π(ÏÑ§Î¨∏ Î™©Î°ù) ÌôîÎ©¥ (LectureSurvey Ïä§ÌÉÄÏùº)
  if (selectedSurveyType && !selectedTimeGroup) {
    const groupKeys = Object.keys(groupedSurveys[selectedSurveyType]);
    return (
      <div className="survey-container">
        <h2>{surveyTypeNames[selectedSurveyType] || selectedSurveyType} ÏÑ§Î¨∏ Î™©Î°ù</h2>
        <div className="course-selection">
          {groupKeys.map((groupKey) => (
            <div key={groupKey} className="course-item">
              <span>{groupKey} ({groupedSurveys[selectedSurveyType][groupKey].length}Î¨∏Ìï≠)</span>
              <button onClick={() => handleSelectTimeGroup(groupKey)}>Ìï¥Îãπ ÏÑ§Î¨∏ ÏãúÏûë</button>
            </div>
          ))}
        </div>
        <button onClick={handleBackToSurveyType}>ÏÑ§Î¨∏ Ïú†Ìòï Î™©Î°ùÏúºÎ°ú ÎèåÏïÑÍ∞ÄÍ∏∞</button>
      </div>
    );
  }

  // ÏÑ†ÌÉùÌïú ÌÉÄÏù¥ÌãÄ Í∑∏Î£πÏùò ÏÑ§Î¨∏ ÏßàÎ¨∏ ÌôîÎ©¥ (LectureSurveyÏôÄ Ïú†ÏÇ¨Ìïú Îã®Ïùº ÏßàÎ¨∏ Ïù∏ÌÑ∞ÌéòÏù¥Ïä§)
  if (selectedSurveyType && selectedTimeGroup) {
    const currentGroupSurveys = groupedSurveys[selectedSurveyType][selectedTimeGroup];
    const currentQuestion = currentGroupSurveys[currentQuestionIndex];
    return (
      <div className="survey-container">
        <h2>{surveyTypeNames[selectedSurveyType] || selectedSurveyType} ÏÑ§Î¨∏</h2>
        <h3>{selectedTimeGroup}</h3>
        <div className="survey-item">
          <p>{currentQuestion.title}</p>
          <div>
            {currentQuestion.options.split(',').map(option => (
              <label key={option}>
                <input
                  type="radio"
                  name={`question-${currentQuestion.id}`}
                  value={option}
                  checked={responses[currentQuestion.id] === option}
                  onChange={() =>
                    setResponses(prev => ({ ...prev, [currentQuestion.id]: option }))
                  }
                />
                {option}
              </label>
            ))}
          </div>
        </div>
        <div className="survey-navigation">
          <button onClick={() => setCurrentQuestionIndex(prev => prev - 1)} disabled={currentQuestionIndex === 0}>
            Ïù¥Ï†Ñ
          </button>
          <button onClick={() => setCurrentQuestionIndex(prev => prev + 1)} disabled={currentQuestionIndex === currentGroupSurveys.length - 1}>
            Îã§Ïùå
          </button>
        </div>
        {currentQuestionIndex === currentGroupSurveys.length - 1 && (
          <button onClick={handleSubmit}>Ï†úÏ∂ú</button>
        )}
        <button onClick={handleBackToGroupList}>Î™©Î°ùÏúºÎ°ú ÎèåÏïÑÍ∞ÄÍ∏∞</button>
      </div>
    );
  }

  return null;
}

export default GeneralSurvey;
