import React, { useEffect, useState } from "react";
import axios from "axios";
import "./StudentManagePage.css"; // ✅ CSS 파일 불러오기

const StudentManagePage = () => {
    const [students, setStudents] = useState([]);
    const [openCourses, setOpenCourses] = useState({}); // ✅ 어떤 강좌가 열려 있는지 상태 관리
    const [error, setError] = useState(""); // 에러 메시지를 위한 상태 추가
    const token = localStorage.getItem('token'); // 로컬 스토리지에서 토큰 가져오기

    useEffect(() => {
        // 토큰이 없는 경우 처리
        if (!token) {
            setError("로그인이 필요합니다.");
            return;
        }

        // 인증 토큰을 헤더에 포함시켜 요청
        axios.get("http://localhost:8090/api/student-manage/approved", {
            headers: {
                Authorization: `Bearer ${token}`  // JWT 토큰을 헤더에 추가
            }
        })
            .then(response => {
                setStudents(response.data);
                setError(""); // 요청이 성공하면 에러 상태를 초기화
            })
            .catch(error => {
                // 오류 응답에 대한 추가적인 처리
                console.error("Error fetching students:", error.response || error.message);
                if (error.response) {
                    // 서버에서 반환된 에러 메시지 처리
                    setError(`오류 발생: ${error.response.data.message || '학생 목록을 가져오는 데 실패했습니다.'}`);
                } else {
                    // 서버 연결 자체에 문제가 있을 경우
                    setError("서버와의 연결에 문제가 있습니다.");
                }
            });
    }, [token]);

    // ✅ 강좌 클릭 시 열림/닫힘 토글
    const toggleCourse = (courseName) => {
        setOpenCourses(prevState => ({
            ...prevState,
            [courseName]: !prevState[courseName]
        }));
    };

    // ✅ 강좌별 그룹화 (강좌명 기준으로 학생 분류)
    const groupedStudents = students.reduce((acc, student) => {
        if (!acc[student.courseName]) acc[student.courseName] = [];
        acc[student.courseName].push(student);
        return acc;
    }, {});

    // ✅ 학생을 출석률 내림차순으로 정렬하는 함수
    const sortStudentsByAttendance = (studentsList) => {
        return studentsList.sort((a, b) => b.attendanceRate - a.attendanceRate);
    };

    return (
        <div className="student-manage-container">
            <h2>수강생 관리</h2>
            {/* 에러 메시지 표시 */}
            {error && <div className="error-message">{error}</div>}
            <div className="course-list">
                {Object.keys(groupedStudents).map((courseName, index) => (
                    <div key={index} className="course-item">
                        {/* ✅ 강좌명 클릭 시 학생 리스트 열림/닫힘 */}
                        <div 
                            className="course-header" 
                            onClick={() => toggleCourse(courseName)}
                        >
                            {courseName}
                            <span className={`arrow ${openCourses[courseName] ? "open" : ""}`}>&#9662;</span>
                        </div>
                        {openCourses[courseName] && (
                            <div className="student-list">
                                <table>
                                    <thead>
                                        <tr>
                                            <th>학번</th>
                                            <th>학생 이름</th>
                                            <th>출석률</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {/* ✅ 출석률 내림차순으로 학생 리스트 정렬 */}
                                        {sortStudentsByAttendance(groupedStudents[courseName]).map((student, idx) => (
                                            <tr key={idx}>
                                                <td>{student.studentId}</td>
                                                <td>{student.studentName}</td>
                                                <td>{student.attendanceRate}%</td>
                                            </tr>
                                        ))}
                                    </tbody>
                                </table>
                            </div>
                        )}
                    </div>
                ))}
            </div>
        </div>
    );
};

export default StudentManagePage;
