import { useRef, useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import AddContentForm from "./AddContentForm";
import AddAnnouncement from "./AddAnnouncement";
import AddAssignment from "./AddAssignment";
import CreateBoard from "./CreateBoard";
import { Link } from "react-router-dom";
import "./CourseDetail.css";

const VideoPlayer = ({ videoUrl, studentId, courseId, week, token }) => {
  const videoRef = useRef(null);
  const [isChecked, setIsChecked] = useState(false);
  const [lastSavedProgress, setLastSavedProgress] = useState(0);
  const progressSaveInterval = 1; // 1ì´ˆë§ˆë‹¤ ì§„ë„ìœ¨ ì €ì¥
  const ATTENDANCE_THRESHOLD = 90; // ì¶œì„ ì¸ì • ê¸°ì¤€ ì‹œì²­ë¥  (90%)
  
  useEffect(() => {
    // ì´ˆê¸° ì¶œì„ ìƒíƒœ í™•ì¸
    const checkAttendanceStatus = async () => {
      try {
        const response = await axios.get(
          `http://localhost:8090/api/attendance/status?studentId=${studentId}`,
          { headers: { Authorization: `Bearer ${token}` } }
        );
        
        // courseIdê°€ í‚¤ì¸ ë§µì—ì„œ ì¶œì„ ìƒíƒœë¥¼ í™•ì¸
        if (response.data[courseId] === true) {
          setIsChecked(true);
        }
      } catch (error) {
        console.error("ì¶œì„ ìƒíƒœ í™•ì¸ ì‹¤íŒ¨", error);
      }
    };
    
    // ì´ì „ ì‹œì²­ ì§„ë„ìœ¨ í™•ì¸
    const checkProgressStatus = async () => {
      try {
        const response = await axios.get(
          `http://localhost:8090/api/attendance/progress/status?studentId=${studentId}`,
          { headers: { Authorization: `Bearer ${token}` } }
        );
        
        // courseIdê°€ í‚¤ì¸ ë§µì—ì„œ ì§„ë„ìœ¨ í™•ì¸
        if (response.data[courseId]) {
          setLastSavedProgress(response.data[courseId]);
          
          // ë™ì˜ìƒ ì‹œê°„ ì„¤ì • (ë¹„ë””ì˜¤ê°€ ë¡œë“œëœ í›„ì— ì‹¤í–‰)
          if (videoRef.current) {
            const duration = videoRef.current.duration;
            if (!isNaN(duration)) {
              videoRef.current.currentTime = (response.data[courseId] / 100) * duration;
            }
          }
        }
      } catch (error) {
        console.error("ì§„ë„ìœ¨ í™•ì¸ ì‹¤íŒ¨", error);
      }
    };
    
    checkAttendanceStatus();
    checkProgressStatus();
  }, [studentId, courseId, token]);

  // ë¹„ë””ì˜¤ ë©”íƒ€ë°ì´í„° ë¡œë“œ ì™„ë£Œ í›„ ì§„ë„ìœ¨ì— ë”°ë¼ ì˜ìƒ ìœ„ì¹˜ ì„¤ì •
  const handleLoadedMetadata = () => {
    if (videoRef.current && lastSavedProgress > 0) {
      const duration = videoRef.current.duration;
      videoRef.current.currentTime = (lastSavedProgress / 100) * duration;
    }
  };
  
  // ì£¼ê¸°ì ìœ¼ë¡œ ì§„ë„ìœ¨ ì €ì¥ (10ì´ˆë§ˆë‹¤)
  useEffect(() => {
    const intervalId = setInterval(() => {
      saveCurrentProgress();
    }, progressSaveInterval * 100);
    
    return () => clearInterval(intervalId);
  }, []);
  
  // í˜„ì¬ ì§„ë„ìœ¨ ì €ì¥
  const saveCurrentProgress = async () => {
    if (!videoRef.current) return;
    
    const video = videoRef.current;
    const watchedPercentage = (video.currentTime / video.duration) * 100;
    
    // ì´ë¯¸ ì¶œì„ì´ ì™„ë£Œë˜ì—ˆê±°ë‚˜ ì§„ë„ìœ¨ì´ 0ì¸ ê²½ìš° ì €ì¥í•˜ì§€ ì•ŠìŒ
    if (isChecked || watchedPercentage === 0) return;
    
    try {
      // ì§„ë„ìœ¨ ì €ì¥ API í˜¸ì¶œ (ì´ API ì—”ë“œí¬ì¸íŠ¸ëŠ” Spring ì»¨íŠ¸ë¡¤ëŸ¬ì— ì—†ìœ¼ë¯€ë¡œ ìƒëµí•˜ê±°ë‚˜ ìˆ˜ì • í•„ìš”)
      console.log("ì§„ë„ìœ¨ ì €ì¥: ", watchedPercentage);
      setLastSavedProgress(watchedPercentage);
    } catch (error) {
      console.error("ì§„ë„ìœ¨ ì €ì¥ ì‹¤íŒ¨", error);
    }
  };
  
  // ì˜ìƒ ì‹œì²­ ì¤‘ ì²˜ë¦¬
  const handleTimeUpdate = async () => {
    if (!videoRef.current) return;
    
    const video = videoRef.current;
    const watchedPercentage = (video.currentTime / video.duration) * 100;
    
    // ì¶œì„ ì™„ë£Œ ì¡°ê±´ í™•ì¸ (90% ì´ìƒ ì‹œì²­ && ì•„ì§ ì¶œì„ ì²´í¬ ì•ˆí•¨)
    if (watchedPercentage >= ATTENDANCE_THRESHOLD && !isChecked) {
      try {
        const response = await axios.post(
          "http://localhost:8090/api/attendance/course",
          { 
            studentId, 
            courseId, 
            watchedPercentage 
          },
          { headers: { Authorization: `Bearer ${token}` } }
        );
        
        if (response.data === "ì¶œì„ ì™„ë£Œ") {
          setIsChecked(true);
        }
      } catch (error) {
        console.error("ê°•ì˜ ì¶œì„ ì²´í¬ ì‹¤íŒ¨", error);
      }
    }
  };

  // ìˆ˜ë™ ì¶œì„ ì²´í¬ (ê°•ì˜ ë‹¨ìœ„)
  const handleCourseAttendance = async () => {
    try {
      const response = await axios.post(
        "http://localhost:8090/api/attendance/course",
        { 
          studentId, 
          courseId, 
          watchedPercentage: 100 
        },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      
      if (response.data === "ì¶œì„ ì™„ë£Œ") {
        setIsChecked(true);
      }
    } catch (error) {
      console.error("ê°•ì˜ ì¶œì„ ì²´í¬ ì‹¤íŒ¨", error);
    }
  };

  return (
    <div className="video-player-container">
      <video
        ref={videoRef}
        controls
        onTimeUpdate={handleTimeUpdate}
        onLoadedMetadata={handleLoadedMetadata}
        width="640"
        height="360"
        className="main-video"
      >
        <source src={videoUrl} type={`video/${videoUrl?.split('.').pop().toLowerCase()}`} />
        ë¸Œë¼ìš°ì €ê°€ ë¹„ë””ì˜¤ íƒœê·¸ë¥¼ ì§€ì›í•˜ì§€ ì•ŠìŠµë‹ˆë‹¤.
      </video>
      
      <div className="attendance-status">
        <div className="progress-info">
          í˜„ì¬ ì§„ë„ìœ¨: {lastSavedProgress.toFixed(2)}%
        </div>
        <label className="attendance-label">
          <input type="checkbox" checked={isChecked} readOnly />
          ì¶œì„ ì™„ë£Œ {isChecked ? 'âœ“' : ''}
        </label>
        {!isChecked && (
          <button 
            className="attendance-button" 
            onClick={handleCourseAttendance}
          >
            ìˆ˜ë™ ì¶œì„ ì²´í¬
          </button>
        )}
      </div>
    </div>
  );
};

const CourseDetailPage = () => {
  const { courseId } = useParams();
  const navigate = useNavigate();
  const [course, setCourse] = useState(null);
  const [weeks, setWeeks] = useState([]);
  const [contents, setContents] = useState({});
  const [announcements, setAnnouncements] = useState({});
  const [assignments, setAssignments] = useState({});
  const [boards, setBoards] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activeWeek, setActiveWeek] = useState(null);
  const [activeGoalWeek, setActiveGoalWeek] = useState(null);
  const [editingDescription, setEditingDescription] = useState(false);
  const [newDescription, setNewDescription] = useState("");
  const [showBoardForm, setShowBoardForm] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');
  const [memberId, setMemberId] = useState(null);
  const token = localStorage.getItem("token");
  const [role, setRole] = useState(null);

  useEffect(() => {
    if (!courseId) return;
  
    const fetchCourseDetails = async () => {
      try {
        const courseResponse = await axios.get(
          `http://localhost:8090/api/courses/${courseId}`, 
          { headers: { Authorization: `Bearer ${token}` } }
        );
  
        setCourse(courseResponse.data);
        setNewDescription(courseResponse.data.description || "");
        localStorage.setItem("courseId", courseId);

        const storedMemberId = localStorage.getItem('studentId');
        if (storedMemberId) {
          setMemberId(storedMemberId);
        } else {
          const userResponse = await axios.get(
            'http://localhost:8090/api/users/me', 
            { headers: { Authorization: `Bearer ${token}` } }
          );
          setMemberId(userResponse.data.memberId);
        }
  
        const contentResponse = await axios.get(
          `http://localhost:8090/api/courses/${courseId}/weeks/contents`, 
          { headers: { Authorization: `Bearer ${token}` } }
        );
  
        setContents(contentResponse.data || {});
   
      } catch (error) {
        console.error("âŒ ê°•ì˜ ìƒì„¸ ì •ë³´ ë¶ˆëŸ¬ì˜¤ê¸° ì˜¤ë¥˜:", error);
      } finally {
        setLoading(false);
      }
    };
  
    fetchCourseDetails();
  }, [courseId, token]);

  useEffect(() => {
    const fetchUserRole = async () => {
      try {
        const response = await axios.get(
          'http://localhost:8090/api/users/me', 
          { headers: { Authorization: `Bearer ${token}` } }
        );
        setRole(response.data.role);
      } catch (error) {
        console.error("âŒ ì‚¬ìš©ì ì—­í•  ë¶ˆëŸ¬ì˜¤ê¸° ì˜¤ë¥˜:", error);
      }
    };
  
    if (token) {
      fetchUserRole();
    }
  }, [token]);

  useEffect(() => {
    if (!courseId || weeks.length === 0) return;

    const fetchAnnouncements = async () => {
      const announcementsData = {};

      for (let week of weeks) {
        try {
          // Consistent API path format with /api/ prefix
          const response = await axios.get(
            `http://localhost:8090/api/courses/${courseId}/weeks/${week.weekNumber}/announcements`,
            { headers: { Authorization: `Bearer ${token}` } }
          );
          announcementsData[week.weekNumber] = response.data || [];
        } catch (error) {
          announcementsData[week.weekNumber] = [];
        }
      }
      setAnnouncements(announcementsData);
    };

    fetchAnnouncements();
  }, [weeks, courseId, token]);

  useEffect(() => {
    if (!courseId) return;
    if (!token) {
      console.error("ğŸ“Œ í† í°ì´ ì—†ìŠµë‹ˆë‹¤. ë¡œê·¸ì¸ ìƒíƒœë¥¼ í™•ì¸í•˜ì„¸ìš”.");
      return;
    }
  
    const fetchWeeksAndBoards = async () => {
      try {
        console.log("ğŸ“Œ API ìš”ì²­ ì‹œì‘! í† í°:", token);
  
        // âœ… ì£¼ì°¨ ë°ì´í„° ê°€ì ¸ì˜¤ê¸°
        const weeksResponse = await axios.get(
          `http://localhost:8090/api/courses/${courseId}/weeks`,
          { headers: { Authorization: `Bearer ${token}` } }
        );
  
        console.log("ğŸ“Œ ì£¼ì°¨ ë°ì´í„° ì‘ë‹µ:", weeksResponse.data); // ğŸ” ì‘ë‹µ í™•ì¸  
  
        const weeksData = weeksResponse.data || [];
        if (!Array.isArray(weeksData) || weeksData.length === 0) {
          console.warn("âš ï¸ ì£¼ì°¨ ë°ì´í„°ê°€ ë¹„ì–´ ìˆìŠµë‹ˆë‹¤.");
          return;
        }
        
        setWeeks(weeksData);
  
        // âœ… ì£¼ì°¨ë³„ ê²Œì‹œíŒ ë°ì´í„° ê°€ì ¸ì˜¤ê¸°
        const boardData = {};
        for (const week of weeksData) {
          if (!week.weekNumber) {
            console.warn(`âš ï¸ ì£¼ì°¨ ë°ì´í„°ì— weekNumberê°€ ì—†ìŠµë‹ˆë‹¤.`, week);
            continue;
          }
  
          try {
            console.log(`ğŸ“Œ ${week.weekNumber}ì£¼ì°¨ ê²Œì‹œíŒ ìš”ì²­ ì‹œì‘!`);
            const response = await axios.get(
              `http://localhost:8090/weekly-board/course/${courseId}/week/${week.weekNumber}/boards`,
              { headers: { Authorization: `Bearer ${token}` } }
            );
  
            console.log(`ğŸ“Œ ${week.weekNumber}ì£¼ì°¨ ê²Œì‹œíŒ ì‘ë‹µ:`, response.data); // ğŸ” ì‘ë‹µ í™•ì¸  
            // ë°ì´í„° ê²€ì¦
            if (!response.data) {
              console.warn(`âš ï¸ ${week.weekNumber}ì£¼ì°¨ ê²Œì‹œíŒ ì‘ë‹µì´ ë¹„ì–´ ìˆìŠµë‹ˆë‹¤.`);
              boardData[week.weekNumber] = [];
            } else {
              boardData[week.weekNumber] = response.data;
            }
  
          } catch (error) {
            console.error(`âŒ ${week.weekNumber}ì£¼ì°¨ ê²Œì‹œíŒ ë¶ˆëŸ¬ì˜¤ê¸° ì‹¤íŒ¨:`, error);
            boardData[week.weekNumber] = [];
          }
        }
  
        setBoards(boardData);
