import { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import "./PostEdit.css";  // ‚úÖ CSS ÌååÏùº Ï∂îÍ∞Ä

export function PostEdit() {
    const { postId, boardId } = useParams();
    const navigate = useNavigate();
    const token = localStorage.getItem("token");

    const [title, setTitle] = useState("");
    const [content, setContent] = useState("");
    const [memberId, setMemberId] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        if (!token) {
            alert("üö® Î°úÍ∑∏Ïù∏ Ï†ïÎ≥¥Í∞Ä ÏóÜÏäµÎãàÎã§.");
            navigate("/login");
            return;
        }

        if (isNaN(postId) || isNaN(boardId)) {
            alert("üö® ÏûòÎ™ªÎêú Ï†ëÍ∑ºÏûÖÎãàÎã§!");
            navigate("/");
            return;
        }

        fetchUserData();
        fetchPostDetail();
    }, [token, postId, boardId]);

    const fetchUserData = async () => {
        try {
            const response = await axios.get("http://localhost:8090/api/users/me", {
                headers: { Authorization: `Bearer ${token}` },
            });
            setMemberId(response.data.id);
        } catch (error) {
            console.error("‚ùå ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ Ï°∞Ìöå Ïã§Ìå®:", error);
        }
    };

    const fetchPostDetail = async () => {
        try {
            const response = await axios.get(`http://localhost:8090/api/weekly-post/${postId}/${boardId}`, {
                headers: { Authorization: `Bearer ${token}` },
            });

            setTitle(response.data.post.title);
            setContent(response.data.post.content);
        } catch (error) {
            console.error("‚ùå Í≤åÏãúÍ∏Ä ÏÉÅÏÑ∏ Ï°∞Ìöå Ïã§Ìå®:", error);
            alert("Í≤åÏãúÍ∏ÄÏùÑ Î∂àÎü¨Ïò§Îäî Îç∞ Ïã§Ìå®ÌñàÏäµÎãàÎã§.");
            navigate(-1);
        } finally {
            setLoading(false);
        }
    };

    const handleUpdate = async () => {
        if (!title.trim() || !content.trim()) {
            alert("üö® Ï†úÎ™©Í≥º ÎÇ¥Ïö©ÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî!");
            return;
        }
        if (!boardId) {
            alert("üö® Í≤åÏãúÌåê Ï†ïÎ≥¥Î•º Î∂àÎü¨Ïò§ÏßÄ Î™ªÌñàÏäµÎãàÎã§!");
            return;
        }

        try {
            

            await axios.put(`http://localhost:8090/api/weekly-post/${postId}/${boardId}/edit`, {
                title,
                content,
                memberId,
            }, {
                headers: { Authorization: `Bearer ${token}` },
            });

            alert("‚úÖ Í≤åÏãúÍ∏ÄÏù¥ ÏàòÏ†ïÎêòÏóàÏäµÎãàÎã§!");
            navigate(`/api/weekly-post/${postId}/${boardId}`);
        } catch (error) {
            console.error("‚ùå Í≤åÏãúÍ∏Ä ÏàòÏ†ï Ïã§Ìå®:", error);
            alert("Í≤åÏãúÍ∏Ä ÏàòÏ†ï Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
        }
    };

    if (loading) return <div>‚è≥ Î°úÎî© Ï§ë...</div>;

    return (
        <div className="edit-container">
            <h2>üìù Í≤åÏãúÍ∏Ä ÏàòÏ†ï</h2>
            <input
                type="text"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                placeholder="Ï†úÎ™©"
            />
            <textarea
                value={content}
                onChange={(e) => setContent(e.target.value)}
                placeholder="ÎÇ¥Ïö©"
            />
            <div className="button-group">
                <button className="update-button" onClick={handleUpdate}>ÏàòÏ†ï ÏôÑÎ£å</button>
                <button className="cancel-button" onClick={() => navigate(-1)}>Ï∑®ÏÜå</button>
            </div>
        </div>
    );
}

export default PostEdit;
