import React, { useEffect, useState } from "react";
import axios from "axios";
import "./StudentManagePage.css"; // ✅ CSS 파일 불러오기

const StudentManagePage = () => {
    const [students, setStudents] = useState([]);
    const [openCourses, setOpenCourses] = useState({}); // ✅ 어떤 강좌가 열려 있는지 상태 관리
    const professorId = localStorage.getItem("studentId"); // 로컬 스토리지에서 교수 아이디 가져오기

    // 로컬 스토리지에서 professorId가 잘 가져와지는지 확인
    console.log("Professor ID from localStorage:", professorId);

    useEffect(() => {
        axios.get("http://localhost:8090/api/student-manage/approved")
            .then(response => {
                console.log("API response data:", response.data); // API 응답 데이터 확인

                // 데이터가 존재하는지 확인하고 필터링
                if (response.data && Array.isArray(response.data)) {
                    const filteredStudents = response.data.filter(student => {
                        // 교수 아이디를 비교하기 전에, student.professorId가 존재하는지 확인
                        if (student.professorId) {
                            // student.professorId와 professorId를 비교할 때, toString()을 사용하여 비교
                            return student.professorId.toString() === professorId.toString();
                        }
                        return false; // professorId가 없으면 해당 학생은 제외
                    });

                    console.log("Filtered students:", filteredStudents); // 필터된 학생 목록 확인
                    setStudents(filteredStudents);
                } else {
                    console.log("No valid student data found");
                }
            })
            .catch(error => console.error("Error fetching students:", error));
    }, [professorId]);

    // ✅ 강좌 클릭 시 열림/닫힘 토글
    const toggleCourse = (courseName) => {
        setOpenCourses(prevState => ({
            ...prevState,
            [courseName]: !prevState[courseName]
        }));
    };

    // ✅ 강좌별 그룹화 (강좌명 기준으로 학생 분류)
    const groupedStudents = students.reduce((acc, student) => {
        if (!acc[student.courseName]) acc[student.courseName] = [];
        acc[student.courseName].push(student);
        return acc;
    }, {});

    return (
        <div className="student-manage-container">
            <h2>수강생 관리</h2>
            <div className="course-list">
                {Object.keys(groupedStudents).map((courseName, index) => (
                    <div key={index} className="course-item">
                        {/* ✅ 강좌명 클릭 시 학생 리스트 열림/닫힘 */}
                        <div 
                            className="course-header" 
                            onClick={() => toggleCourse(courseName)}
                        >
                            {courseName}
                            <span className={`arrow ${openCourses[courseName] ? "open" : ""}`}>&#9662;</span>
                        </div>
                        {openCourses[courseName] && (
                            <div className="student-list">
                                <table>
                                    <thead>
                                        <tr>
                                            <th>학번</th>
                                            <th>학생 이름</th>
                                            <th>출석률</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {groupedStudents[courseName].map((student, idx) => (
                                            <tr key={idx}>
                                                <td>{student.studentId}</td>
                                                <td>{student.studentName}</td>
                                                <td>{student.attendanceRate}%</td>
                                            </tr>
                                        ))}
                                    </tbody>
                                </table>
                            </div>
                        )}
                    </div>
                ))}
            </div>
        </div>
    );
};

export default StudentManagePage;
