import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';
import './QuizPage.css';

function QuizPage() {
    const { courseId } = useParams();
    const [exams,quizzes, setQuizzes] = useState([]);
    const [currentQuestion, setCurrentQuestion] = useState(0);
    const [answers, setAnswers] = useState({});
    const [score, setScore] = useState(null);
    const [quizCompleted, setQuizCompleted] = useState(false);
    const [timeLeft, setTimeLeft] = useState(60);
    const [quizStarted, setQuizStarted] = useState(false);
    const [loading, setLoading] = useState(true);
    const [, setError] = useState("");
    const token = localStorage.getItem("token");
    const navigate = useNavigate();

    // ✅ 퀴즈 데이터 불러오기 (useCallback 적용)
    const fetchQuizzes = useCallback(async () => {
        if (!token) {
            setError("로그인이 필요합니다.");
            setLoading(false);
            return;
        }

        try {
            const response = await axios.get("/api/exams/${examId}/questions", {
                headers: { Authorization: `Bearer ${token}` }
            });
            setQuizzes(response.data);
        } catch (error) {
            console.error("🚨 퀴즈 데이터 불러오기 오류:", error);
            setError("퀴즈 데이터를 불러오는 중 오류가 발생했습니다.");
        } finally {
            setLoading(false);
        }
    }, [courseId, token]);

    // ✅ 퀴즈 자동 제출 (useCallback 적용)
    const handleQuizTimeout = useCallback(async () => {
        try {
            const response = await axios.post(`/api/quizzes/submit`, {
                courseId: courseId,
                answers: answers
            }, {
                headers: { Authorization: `Bearer ${token}` }
            });

            setScore(response.data.score);
            setQuizCompleted(true);
        } catch (error) {
            console.error("🚨 퀴즈 제출 오류:", error);
            setError("퀴즈 제출 중 오류가 발생했습니다.");
        }
    }, [courseId, answers, token]);

    // ✅ 퀴즈 데이터 로드
    useEffect(() => {
        fetchQuizzes();
    }, [fetchQuizzes]);

    // ✅ 타이머 관리 (useEffect 의존성 배열에 `handleQuizTimeout` 포함)
    useEffect(() => {
        if (quizStarted) {
            const timer = setInterval(() => {
                setTimeLeft((prevTime) => {
                    if (prevTime <= 1) {
                        clearInterval(timer);
                        handleQuizTimeout();
                        return 0;
                    }
                    return prevTime - 1;
                });
            }, 1000);

            return () => clearInterval(timer);
        }
    }, [quizStarted, handleQuizTimeout]);

    // ✅ 퀴즈 시작
    const startQuiz = () => {
        setQuizStarted(true);
        setTimeLeft(60);
        setScore(null);
        setCurrentQuestion(0);
        setAnswers({});
        setQuizCompleted(false);
    };

    const handleAnswerChange = (selectedAnswer) => {
        setAnswers((prevAnswers) => ({
            ...prevAnswers,
            [quizzes[currentQuestion].id]: selectedAnswer,
        }));
    };

    const handleNextQuestion = () => {
        if (currentQuestion < quizzes.length - 1) {
            setCurrentQuestion((prev) => prev + 1);
        } else {
            handleQuizTimeout();
        }
    };

    const handleBackToCourse = () => {
        navigate(`/student/courses/${courseId}`);
    };

    if (loading) return <div>📡 퀴즈 데이터 불러오는 중...</div>;

    return (
        <div className="dashboard">
            <main className="main-content">
                <h2>퀴즈</h2>

                {/* ✅ 퀴즈 시작 버튼 */}
                {!quizStarted && !quizCompleted && (
                    <button onClick={startQuiz} className="start-btn">
                        퀴즈 시작
                    </button>
                )}

                {/* ✅ 퀴즈 완료 후 결과 표시 */}
                {quizCompleted ? (
                    <div className="score-result">
                        <h3>퀴즈 완료! 당신의 점수: {score} / {quizzes.length}</h3>
                        <button className="back-btn" onClick={handleBackToCourse}>
                            강좌로 돌아가기
                        </button>
                    </div>
                ) : (
                    quizStarted && (
                        <div className="quiz-container">
                            <div className="quiz-navigation">
                                <ul>
                                    {quizzes.map((quiz, index) => (
                                        <li
                                            key={quiz.id}
                                            onClick={() => setCurrentQuestion(index)}
                                            className={currentQuestion === index ? 'active' : ''}
                                        >
                                            {index + 1}
                                        </li>
                                    ))}
                                </ul>
                            </div>

                            <div className="quiz-question" style={{ userSelect: 'none' }}>
                                <div className="quiz-item">
                                    <h3>{quizzes[currentQuestion].question}</h3>
                                    <div>
                                        {quizzes[currentQuestion].options.map((option, index) => (
                                            <label key={index} className="quiz-option">
                                                <input
                                                    type="radio"
                                                    name={`quiz-${quizzes[currentQuestion].id}`}
                                                    value={option}
                                                    onChange={() => handleAnswerChange(option)}
                                                    checked={answers[quizzes[currentQuestion].id] === option}
                                                />
                                                {option}
                                            </label>
                                        ))}
                                    </div>
                                </div>

                                <div className="quiz-timer">
                                    <span>남은 시간: {timeLeft}초</span>
                                </div>
                            </div>

                            <div className="quiz-footer">
                                <button onClick={handleNextQuestion} className="next-btn">
                                    {currentQuestion < quizzes.length - 1 ? "다음 문제" : "퀴즈 완료"}
                                </button>
                            </div>
                        </div>
                    )
                )}
                <button className="back-btn" onClick={handleBackToCourse}>뒤로가기</button>
            </main>
        </div>
    );
}

export default QuizPage;
