import React, { useState, useEffect, useMemo } from 'react';
import axios from 'axios';
import './TeamProjectHistory.css';

function TeamProjectHistory() {
  const [studentInfo, setStudentInfo] = useState(null);
  const [submissions, setSubmissions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  const token = localStorage.getItem("token"); // ‚úÖ ÌÜ†ÌÅ∞ Í∞ÄÏ†∏Ïò§Í∏∞

  // ‚úÖ ÌòÑÏû¨ Î°úÍ∑∏Ïù∏Ìïú Ïú†Ï†ÄÏùò Ï†úÏ∂ú ÎÇ¥Ïó≠Îßå ÌïÑÌÑ∞ÎßÅ
  const filteredSubmissions = useMemo(() => {
    return submissions.filter(submission => submission.studentId === studentInfo?.id);
  }, [submissions, studentInfo]);

  useEffect(() => {
    if (!token) {
      console.warn("‚ö†Ô∏è ÌÜ†ÌÅ∞Ïù¥ ÏóÜÏäµÎãàÎã§. Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
      setError("Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
      setLoading(false);
      return;
    }

    const fetchStudentData = async () => {
      try {
        // ‚úÖ ÌïôÏÉù Ï†ïÎ≥¥ Í∞ÄÏ†∏Ïò§Í∏∞
        const userResponse = await axios.get('/api/student/me', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setStudentInfo(userResponse.data);

        // ‚úÖ Ï†úÏ∂ú ÎÇ¥Ïó≠ Í∞ÄÏ†∏Ïò§Í∏∞
        const submissionsResponse = await axios.get('/api/team-projects/submissions', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setSubmissions(submissionsResponse.data);
      } catch (error) {
        console.error("‚ö†Ô∏è Îç∞Ïù¥ÌÑ∞ Î°úÎî© Ïã§Ìå®:", error);
        setError("Îç∞Ïù¥ÌÑ∞Î•º Î∂àÎü¨Ïò§Îäî Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
      } finally {
        setLoading(false);
      }
    };

    fetchStudentData();
  }, [token]);

  if (loading) {
    return (
      <div className="team-project-history">
        <h2>ÌåÄ ÌîÑÎ°úÏ†ùÌä∏ Ï†úÏ∂ú ÎÇ¥Ïó≠</h2>
        <p>üì° Îç∞Ïù¥ÌÑ∞ Î∂àÎü¨Ïò§Îäî Ï§ë...</p>
      </div>
    );
  }

  return (
    <div className="team-project-history">
      <h2>ÌåÄ ÌîÑÎ°úÏ†ùÌä∏ Ï†úÏ∂ú ÎÇ¥Ïó≠</h2>

      {error ? (
        <p className="error-message">{error}</p>
      ) : filteredSubmissions.length === 0 ? (
        <p>Ï†úÏ∂úÎêú ÌîÑÎ°úÏ†ùÌä∏Í∞Ä ÏóÜÏäµÎãàÎã§.</p>
      ) : (
        <table className="history-table">
          <thead>
            <tr>
              <th>ÌåÄÎ™Ö</th>
              <th>Ï†úÏ∂ú ÌååÏùº</th>
              <th>Ï†úÏ∂ú ÏãúÍ∞Ñ</th>
            </tr>
          </thead>
          <tbody>
            {filteredSubmissions.map((submission, index) => (
              <tr key={`${submission.team_name}-${index}`}>
                <td>{submission.team_name || 'ÌåÄÎ™Ö ÎØ∏ÏßÄÏ†ï'}</td>
                <td>
                  {submission.files.length > 0
                    ? submission.files.map((file) => (
                        <a key={file} href={`/uploads/${file}`} download>
                          {file}
                        </a>
                      ))
                    : <span>ÌååÏùº ÏóÜÏùå</span>} {/* ‚úÖ Îπà ÎÖ∏Îìú Î∞©ÏßÄ */}
                </td>
                <td>{new Date(submission.submittedAt).toLocaleString()}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default TeamProjectHistory;
