import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import { Link } from "react-router-dom";
import "./AllAnnouncement.css"
export function AllAnnouncement() {
    const { courseId: paramCourseId } = useParams(); // useParamsÏóêÏÑú Í∞ÄÏ†∏Ïò® courseId
    const [announcements, setAnnouncements] = useState([]);
    const [memberId, setMemberId] = useState(null);
    const [courseId, setCourseId] = useState(paramCourseId); // courseId ÏÉÅÌÉú Ï∂îÍ∞Ä
    const token = localStorage.getItem("token");

    useEffect(() => {
        if (token) {
            fetchUserData();
        }
    }, [token]);

    // ÌòÑÏû¨ Î°úÍ∑∏Ïù∏Ìïú ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ Ï°∞Ìöå
    const fetchUserData = async () => {
        try {
            const response = await axios.get("http://localhost:8090/api/users/me", {
                headers: { Authorization: `Bearer ${token}` },
            });
            setMemberId(response.data.id);
            fetchUserAnnouncements(response.data.id);
        } catch (error) {
            console.error("‚ùå ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ Ï°∞Ìöå Ïã§Ìå®:", error);
        }
    };

    // ÏÇ¨Ïö©ÏûêÍ∞Ä ÏûëÏÑ±Ìïú Í≥µÏßÄÏÇ¨Ìï≠ Ï°∞Ìöå
    const fetchUserAnnouncements = async (memberId) => {
        try {
            const response = await axios.get(`http://localhost:8090/${memberId}/announcements`, {
                headers: { Authorization: `Bearer ${token}` },
            });

            setAnnouncements(response.data);

            // üîπ ÎßåÏïΩ courseIdÍ∞Ä ÏóÜÏúºÎ©¥, Ï≤´ Î≤àÏß∏ Í≥µÏßÄÏÇ¨Ìï≠Ïùò courseIdÎ•º ÏÑ§Ï†ï
            if (!paramCourseId && response.data.length > 0) {
                setCourseId(response.data[0].courseId);
            }
        } catch (error) {
            console.error("‚ùå Í≥µÏßÄÏÇ¨Ìï≠ Ï°∞Ìöå Ïã§Ìå®:", error);
        }
    };

    return (
        <div className="announcement-list">
            <h1>üìÑ Ï†ÑÏ≤¥ Í≥µÏßÄÏÇ¨Ìï≠</h1>
            {announcements.length === 0 ? (
                <p>ÏûëÏÑ±Ìïú Í≥µÏßÄÏÇ¨Ìï≠Ïù¥ ÏóÜÏäµÎãàÎã§.</p>
            ) : (
                <ul>
                    {announcements.map((announcement) => (
                        <li key={announcement.id}>
                            <h3>Ï†úÎ™©: {announcement.title}</h3>
                            <p><strong>Í∞ïÏùò:</strong> {announcement.courseName}</p>
                            <p><strong>Ï£ºÏ∞®:</strong> {announcement.weekNumber}</p>
                            <Link to={`/courses/${announcement.courseId}/announcements/${announcement.id}`}>
                                     Í≥µÏßÄÏÇ¨Ìï≠ ÏÉÅÏÑ∏Î≥¥Í∏∞
                            </Link>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
}

export default AllAnnouncement;
