import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './TeamEvaluation.css';

const TeamEvaluation = () => {
    const [teamMembers, setTeamMembers] = useState([]);
    const [teamSurvey, setTeamSurvey] = useState(null);
    const [currentEvaluating, setCurrentEvaluating] = useState(null);
    const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
    const [responses, setResponses] = useState({});
    const [studentInfo, setStudentInfo] = useState(null);
    const token = localStorage.getItem("token");

    // ✅ 로그인한 학생 정보 가져오기
    useEffect(() => {
        if (!token) return;

        const fetchStudentInfo = async () => {
            try {
                const response = await axios.get(`/api/students/me`, {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setStudentInfo(response.data);
            } catch (error) {
                console.error("🚨 학생 정보 불러오기 오류:", error);
            }
        };

        fetchStudentInfo();
    }, [token]);

    // ✅ 팀원 목록 가져오기
    useEffect(() => {
        if (!token || !studentInfo) return;

        const fetchTeamMembers = async () => {
            try {
                const response = await axios.get(`/api/teams/${studentInfo.team_id}/members`, {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setTeamMembers(response.data);
            } catch (error) {
                console.error("🚨 팀원 데이터 불러오기 오류:", error);
            }
        };

        fetchTeamMembers();
    }, [studentInfo, token]);

    // ✅ 팀 평가 설문 가져오기
    useEffect(() => {
        if (!token || !studentInfo) return;

        const fetchSurvey = async () => {
            try {
                const response = await axios.get(`/api/surveys/team/${studentInfo.team_id}`, {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setTeamSurvey(response.data);
            } catch (error) {
                console.error("🚨 팀 평가 설문 불러오기 오류:", error);
            }
        };

        fetchSurvey();
    }, [studentInfo, token]);

    const handleResponseChange = (studentId, questionId, value) => {
        setResponses(prev => ({
            ...prev,
            [studentId]: {
                ...(prev[studentId] || {}),
                [questionId]: value
            }
        }));
    };

    const handleSubmit = async (studentId) => {
        try {
            await axios.post(`/api/surveys/team/submit`, {
                teamId: studentInfo.team_id,
                evaluatedStudentId: studentId,
                responses: responses[studentId]
            }, {
                headers: { Authorization: `Bearer ${token}` }
            });

            alert("✅ 팀원 평가가 성공적으로 제출되었습니다!");
        } catch (error) {
            console.error("🚨 팀 평가 제출 오류:", error);
            alert("팀 평가 제출 중 오류가 발생했습니다.");
        }
    };

    if (!teamSurvey) return <p>📡 팀 평가 설문을 불러오는 중...</p>;

    return (
        <div className="team-evaluation">
            <h3>팀원 평가</h3>
            {currentEvaluating === null ? (
                <ul className="team-list">
                    {teamMembers.map(member => (
                        <li key={member.student_id}>
                            {member.name}
                            <button className="start-btn" onClick={() => setCurrentEvaluating(member.student_id)}>
                                평가 시작
                            </button>
                        </li>
                    ))}
                </ul>
            ) : (
                <div>
                    <h4>{teamMembers.find(t => t.student_id === currentEvaluating).name} 평가</h4>
                    <p>{teamSurvey.questions[currentQuestionIndex].text}</p>
                    <input
                        type="text"
                        onChange={(e) => handleResponseChange(currentEvaluating, teamSurvey.questions[currentQuestionIndex].id, e.target.value)}
                    />
                    <button onClick={() => handleSubmit(currentEvaluating)}>제출</button>
                </div>
            )}
        </div>
    );
};

export default TeamEvaluation;
