import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import "./AnnouncementDetail.css"; // ‚úÖ Ïä§ÌÉÄÏùº Ï∂îÍ∞Ä

const AnnouncementDetail = () => {
  const { courseId, announcementId } = useParams();
  const [announcement, setAnnouncement] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const token = localStorage.getItem("token");
  const navigate = useNavigate();

  useEffect(() => {
    const fetchAnnouncement = async () => {
      try {
        const response = await axios.get(
          `http://localhost:8090/api/courses/${courseId}/announcements/${announcementId}`,
          { headers: { Authorization: `Bearer ${token}` } }
        );
        setAnnouncement(response.data);
      } catch (err) {
        setError("Í≥µÏßÄÏÇ¨Ìï≠ÏùÑ Î∂àÎü¨Ïò§Îäî Îç∞ Ïã§Ìå®ÌñàÏäµÎãàÎã§.");
      } finally {
        setLoading(false);
      }
    };

    fetchAnnouncement();
  }, [courseId, announcementId, token]);

  if (loading) return <p>üì° Î°úÎî© Ï§ë...</p>;
  if (error) return <p>‚ùå {error}</p>;

  return (
    <div className="announcement-detail">
      <h2>{announcement.title}</h2>
      <p>
        <strong>ÏûëÏÑ±Ïûê:</strong> {announcement.authorName}  &nbsp;
        <strong>ÏûëÏÑ±Ïùº:</strong> {new Date(announcement.createdAt).toLocaleDateString()}
        </p>
      <hr />
      <p>{announcement.content}</p>
      <button className="back-button" onClick={() => navigate(-1)}>Î™©Î°ù</button>
    </div>
  );
};

export default AnnouncementDetail;
