import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import StudentSidebar from '../StudentSidebar';
import './SurveyResult.css';

function SurveyResult() {
    const { courseId, surveyType } = useParams();
    const [survey, setSurvey] = useState(null);
    const [responses, setResponses] = useState({});
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState("");
    const token = localStorage.getItem("token");

    useEffect(() => {
        if (!token) {
            setError("Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
            setLoading(false);
            return;
        }

        const fetchSurveyResult = async () => {
            try {
                const response = await axios.get(`/api/surveys/${surveyType}/${courseId}/results`, {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setSurvey(response.data.survey);
                setResponses(response.data.responses);
            } catch (error) {
                console.error("üö® ÏÑ§Î¨∏ Í≤∞Í≥º Î∂àÎü¨Ïò§Í∏∞ Ïò§Î•ò:", error);
                setError("ÏÑ§Î¨∏ Í≤∞Í≥ºÎ•º Î∂àÎü¨Ïò§Îäî Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
            } finally {
                setLoading(false);
            }
        };

        fetchSurveyResult();
    }, [courseId, surveyType, token]);

    if (loading) return <p>üì° ÏÑ§Î¨∏ Îç∞Ïù¥ÌÑ∞Î•º Î∂àÎü¨Ïò§Îäî Ï§ë...</p>;
    if (error) return <p className="error-message">{error}</p>;
    if (!survey) return <p>ÏÑ§Î¨∏ÏùÑ Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§.</p>;

    return (
        <div className="dashboard">
            <StudentSidebar />
            <main className="main-content">
                <h2>{survey.title} ÏÑ§Î¨∏ Í≤∞Í≥º</h2>
                <div className="survey-result">
                    <h3>ÏùëÎãµ ÏöîÏïΩ</h3>
                    {survey.questions.map((question) => (
                        <div key={question.id} className="result-item">
                            <h4>{question.text}</h4>
                            <p>ÏùëÎãµ: {responses[question.id] || 'ÏùëÎãµ ÏóÜÏùå'}</p>
                        </div>
                    ))}
                    {responses.feedback && (
                        <div className="result-feedback">
                            <h3>Ï∂îÍ∞Ä ÏùòÍ≤¨</h3>
                            <p>{responses.feedback}</p>
                        </div>
                    )}
                </div>
            </main>
        </div>
    );
}

export default SurveyResult;
