import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import './TeamProjectSubmit.css';

function TeamProjectSubmit() {
  const [files, setFiles] = useState([]);
  const [deadline, setDeadline] = useState(null);
  const [teamName, setTeamName] = useState("Ïïå Ïàò ÏóÜÎäî ÌåÄ");
  const [teamId, setTeamId] = useState(null);
  const [teamProjectId, setTeamProjectId] = useState(null); // üî• ÎèôÏ†ÅÏúºÎ°ú ÏÑ§Ï†ï
  const [deadlinePassed, setDeadlinePassed] = useState(false);
  const [feedbackMessage, setFeedbackMessage] = useState("");
  const token = localStorage.getItem("token");

  // ÌåÄ ID Î∞è ÌîÑÎ°úÏ†ùÌä∏ ID Í∞ÄÏ†∏Ïò§Í∏∞
  useEffect(() => {
    if (!token) {
      console.warn("‚ö†Ô∏è ÌÜ†ÌÅ∞Ïù¥ ÏóÜÏäµÎãàÎã§. Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
      setFeedbackMessage("Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
      return;
    }

    const fetchTeamInfo = async () => {
      try {
        // üîç 1Ô∏è‚É£ Î°úÍ∑∏Ïù∏Ìïú ÌïôÏÉù Ï†ïÎ≥¥ Í∞ÄÏ†∏Ïò§Í∏∞
        const userResponse = await axios.get("http://localhost:8090/api/users/me", {
          headers: { Authorization: `Bearer ${token}` }
        });

        if (!userResponse.data || !userResponse.data.id) {
          console.warn("‚ö†Ô∏è ÌïôÏÉù Ï†ïÎ≥¥Î•º Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§.");
          setFeedbackMessage("ÌïôÏÉù Ï†ïÎ≥¥Î•º Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§.");
          return;
        }
        const studentId = userResponse.data.id;

        // üîç 2Ô∏è‚É£ ÌåÄÏõê Î™©Î°ùÏóêÏÑú ÌïôÏÉùÏù¥ ÏÜçÌïú ÌåÄ ID Ï∞æÍ∏∞
        const teamMembersResponse = await axios.get("http://localhost:8090/api/team-members", {
          headers: { Authorization: `Bearer ${token}` }
        });

        const studentTeam = teamMembersResponse.data.find(member => member.member_id === studentId);

        if (!studentTeam) {
          console.warn("‚ö†Ô∏è ÌïôÏÉùÏù¥ ÏÜçÌïú ÌåÄÏùÑ Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§.");
          setFeedbackMessage("ÌïôÏÉùÏù¥ ÏÜçÌïú ÌåÄÏù¥ ÏóÜÏäµÎãàÎã§.");
          return;
        }

        const studentTeamId = studentTeam.team_id;
        console.log(`‚úÖ ÌïôÏÉùÏù¥ ÏÜçÌïú ÌåÄ ID: ${studentTeamId}`);
        setTeamId(studentTeamId);

        // üîç 3Ô∏è‚É£ Ìï¥Îãπ ÌåÄÏùò ÌîÑÎ°úÏ†ùÌä∏ Ï†ïÎ≥¥ Í∞ÄÏ†∏Ïò§Í∏∞
        const projectsResponse = await axios.get(`http://localhost:8090/api/team-projects?teamId=${studentTeamId}`, {
          headers: { Authorization: `Bearer ${token}` }
        });

        if (!projectsResponse.data || projectsResponse.data.length === 0) {
          console.warn("‚ö†Ô∏è Ìï¥Îãπ ÌåÄÏùò ÌîÑÎ°úÏ†ùÌä∏Í∞Ä ÏóÜÏäµÎãàÎã§.");
          setFeedbackMessage("Ïù¥ ÌåÄÏóê Îì±Î°ùÎêú ÌîÑÎ°úÏ†ùÌä∏Í∞Ä ÏóÜÏäµÎãàÎã§.");
          return;
        }

        // üî• Ï≤´ Î≤àÏß∏ ÌîÑÎ°úÏ†ùÌä∏Ïùò Ï†ïÎ≥¥Î•º Í∞ÄÏ†∏Ïò¥
        const teamProject = projectsResponse.data[0];
        setTeamProjectId(teamProject.id);
        setTeamName(teamProject.projectName || "Ïïå Ïàò ÏóÜÎäî ÌåÄ");
        setDeadline(new Date(teamProject.deadline));

      } catch (error) {
        console.error("‚ö†Ô∏è ÌåÄ Ï†ïÎ≥¥ Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:", error);
        setFeedbackMessage("ÌåÄ Ï†ïÎ≥¥Î•º Î∂àÎü¨Ïò§Îäî Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
      }
    };

    fetchTeamInfo();
  }, [token]);

  useEffect(() => {
    if (deadline && new Date() > deadline) {
      setDeadlinePassed(true);
    }
  }, [deadline]);

  const handleFileChange = useCallback((e) => {
    const selectedFiles = Array.from(e.target.files);
    setFiles(prev => [...prev, ...selectedFiles]);
  }, []);

  const handleRemoveFile = useCallback((index) => {
    setFiles(prev => prev.filter((_, i) => i !== index));
  }, []);

  const handleSubmit = useCallback(async () => {
    if (files.length === 0) {
      setFeedbackMessage("ÌååÏùºÏùÑ ÏóÖÎ°úÎìúÌïòÏÑ∏Ïöî!");
      return;
    }
    if (deadlinePassed) {
      setFeedbackMessage("Ï†úÏ∂ú Í∏∞ÌïúÏù¥ ÏßÄÎÇ¨ÏäµÎãàÎã§.");
      return;
    }
    if (!teamId || !teamProjectId) {
      setFeedbackMessage("ÌåÄ Ï†ïÎ≥¥Í∞Ä Î∂ÄÏ°±Ìï©ÎãàÎã§.");
      return;
    }

    const formData = new FormData();
    formData.append("file", files[0]); // üî• Îã®Ïùº ÌååÏùº Ï†úÏ∂ú (Ïó¨Îü¨ ÌååÏùº ÏßÄÏõêÌïòÎ†§Î©¥ Î∞òÎ≥µÎ¨∏ ÏÇ¨Ïö©)

    try {
      await axios.post(
        `http://localhost:8090/api/team-project-submissions/${teamProjectId}/${teamId}/submit`,
        formData,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          }
        }
      );
      setFeedbackMessage("ÌîÑÎ°úÏ†ùÌä∏Í∞Ä ÏÑ±Í≥µÏ†ÅÏúºÎ°ú Ï†úÏ∂úÎêòÏóàÏäµÎãàÎã§!");
      setFiles([]);
    } catch (error) {
      console.error("üö® Ï†úÏ∂ú Ïã§Ìå®:", error);
      setFeedbackMessage("Ï†úÏ∂ú Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
    }
    setTimeout(() => setFeedbackMessage(""), 3000);
  }, [files, deadlinePassed, teamId, teamProjectId, token]);

  return (
    <div className="team-project-submit">
      <h2>ÌåÄ ÌîÑÎ°úÏ†ùÌä∏ Ï†úÏ∂ú ({teamName})</h2>
      {feedbackMessage && (
        <div className="feedback-message" role="alert">
          {feedbackMessage}
        </div>
      )}
      {deadlinePassed ? (
        <p className="deadline-message">Ï†úÏ∂ú Í∏∞ÌïúÏù¥ ÏßÄÎÇ¨ÏäµÎãàÎã§.</p>
      ) : (
        <>
          <label htmlFor="file-upload" className="visually-hidden">
            ÌååÏùº ÏóÖÎ°úÎìú
          </label>
          <input
            id="file-upload"
            type="file"
            onChange={handleFileChange}
            accept=".zip,.pdf,.docx,.pptx"
            multiple
            disabled={deadlinePassed}
          />
          <ul>
            {files.map((file, index) => (
              <li key={index}>
                {file.name}{' '}
                <button 
                  onClick={() => handleRemoveFile(index)} 
                  aria-label={`Remove ${file.name}`}
                >
                  ÏÇ≠Ï†ú
                </button>
              </li>
            ))}
          </ul>
          <button onClick={handleSubmit} disabled={deadlinePassed}>
            Ï†úÏ∂úÌïòÍ∏∞
          </button>
        </>
      )}
    </div>
  );
}

export default TeamProjectSubmit;
