import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './StudentEnroll.css';

const StudentEnroll = () => {
  const [courses, setCourses] = useState([]);  
  const [enrolledCourses, setEnrolledCourses] = useState([]); 
  const [loading, setLoading] = useState(true);  
  const [error, setError] = useState(null);  
  const [memberId, setMemberId] = useState(null);
  const navigate = useNavigate();
  const token = localStorage.getItem('token');

  const fetchEnrollments = useCallback(async () => {
    if (!memberId) return;
    try {
      const response = await axios.get(`http://localhost:8090/api/enrollments/member/${memberId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setEnrolledCourses(response.data);
    } catch (error) {
      console.error("‚ùå ÏàòÍ∞ï Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:", error);
    }
  }, [memberId, token]);

  useEffect(() => {
    if (!token) {
      alert("Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
      navigate('/login');
      return;
    }

    const fetchUserInfo = async () => {
      try {
        const response = await axios.get('http://localhost:8090/api/users/me', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setMemberId(response.data.id);
      } catch (error) {
        console.error("‚ùå ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ Í∞ÄÏ†∏Ïò§Í∏∞ Ïã§Ìå®:", error);
        alert("ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥Î•º Î∂àÎü¨Ïò¨ Ïàò ÏóÜÏäµÎãàÎã§. Îã§Ïãú Î°úÍ∑∏Ïù∏Ìï¥Ï£ºÏÑ∏Ïöî.");
        navigate('/login');
      }
    };
    
    const fetchCourses = async () => {
      try {
        const response = await axios.get('http://localhost:8090/api/courses', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setCourses(response.data);
      } catch (error) {
        setError("Í∞ïÏùò Î™©Î°ùÏùÑ Î∂àÎü¨Ïò§Îäî Îç∞ Ïã§Ìå®ÌñàÏäµÎãàÎã§.");
      } finally {
        setLoading(false);
      }
    };

    fetchUserInfo();
    fetchCourses();
    fetchEnrollments();
  }, [token, navigate, memberId, fetchEnrollments]);

  const handleEnroll = async (courseId) => {
    if (!token || !memberId) { 
      alert("Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
      navigate('/login');
      return;
    }
  
    try {
      await axios.post('http://localhost:8090/api/enrollments', {
        memberId: memberId,
        courseId: courseId
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });
  
      alert("ÏàòÍ∞ï Ïã†Ï≤≠ ÏôÑÎ£å!");
      fetchEnrollments();
    } catch (error) {
      console.error("‚ùå ÏàòÍ∞ï Ïã†Ï≤≠ Ïã§Ìå®:", error.response?.data || error.message);
      alert("ÏàòÍ∞ï Ïã†Ï≤≠ Ïã§Ìå®: " + (error.response?.data || error.message));
    }
  };
  
  // ÏàòÍ∞ï Ï∑®ÏÜå Í∏∞Îä• Ï∂îÍ∞Ä
  const handleCancelEnrollment = async (courseId) => {
    if (!token || !memberId) {
      alert("Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
      navigate('/login');
      return;
    }

    // ÌôïÏù∏ Îã§Ïù¥ÏñºÎ°úÍ∑∏ ÌëúÏãú
    const confirmCancel = window.confirm("Ï†ïÎßêÎ°ú ÏàòÍ∞ï Ïã†Ï≤≠ÏùÑ Ï∑®ÏÜåÌïòÏãúÍ≤†ÏäµÎãàÍπå?");
    if (!confirmCancel) return;

    try {
      // Ìï¥Îãπ Í∞ïÏùòÏùò enrollment Ï∞æÍ∏∞
      const enrollment = enrolledCourses.find(e => e && e.course && e.course.id === courseId);
      
      if (!enrollment) {
        alert("ÏàòÍ∞ï Ïã†Ï≤≠ Ï†ïÎ≥¥Î•º Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§.");
        return;
      }

      // ÏàòÍ∞ï Ïã†Ï≤≠ Ï∑®ÏÜå API Ìò∏Ï∂ú
      await axios.delete(`http://localhost:8090/api/enrollments/${enrollment.id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });

      alert("ÏàòÍ∞ï Ïã†Ï≤≠Ïù¥ Ï∑®ÏÜåÎêòÏóàÏäµÎãàÎã§.");
      fetchEnrollments(); // Î™©Î°ù ÏÉàÎ°úÍ≥†Ïπ®
    } catch (error) {
      console.error("‚ùå ÏàòÍ∞ï Ï∑®ÏÜå Ïã§Ìå®:", error.response?.data || error.message);
      alert("ÏàòÍ∞ï Ï∑®ÏÜå Ïã§Ìå®: " + (error.response?.data || error.message));
    }
  };

  const getEnrollmentStatus = (courseId) => {
    const enrollment = enrolledCourses.find(e => e && e.course && e.course.id === courseId);
    if (enrollment) {
      return enrollment.status; // PENDING, APPROVED, REJECTED
    }
    return null;
  };

  // Ìï¥Îãπ Í∞ïÏùòÏùò enrollment ID Í∞ÄÏ†∏Ïò§Í∏∞
  const getEnrollmentId = (courseId) => {
    const enrollment = enrolledCourses.find(e => e && e.course && e.course.id === courseId);
    return enrollment ? enrollment.id : null;
  };

  if (loading) return <div className="loading">Î°úÎî© Ï§ë...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="student-enroll-container">
      <h2>üìö ÏàòÍ∞ï Ïã†Ï≤≠ ÌéòÏù¥ÏßÄ</h2>
      {courses.length === 0 ? (
        <p>ÏàòÍ∞ï Í∞ÄÎä•Ìïú Í∞ïÏùòÍ∞Ä ÏóÜÏäµÎãàÎã§.</p>
      ) : (
        <ul className="course-list">
          {courses.map((course) => {
            const status = getEnrollmentStatus(course.id);
            const isEnrolled = status === "PENDING" || status === "APPROVED";

            return (
              <li key={course.id} className="course-item">
                <h3>{course.name}</h3>
                <p>üìÖ ÌïôÏäµ ÏãúÏûëÏùº: {course.startDate}</p>
                <p>‚è≥ ÌïôÏäµ Ï¢ÖÎ£åÏùº: {course.endDate}</p>
                <p>üîñ ÏÉÅÌÉú: {course.status === 'COMPLETED' ? 'Ïã†Ï≤≠ Í∞ÄÎä•' : 'ÎßàÍ∞êÎê®'}</p>
                
                {status && (
                  <p className={`${status.toLowerCase()}-text`}>
                    {status === "PENDING" ? "‚è≥ ÏàòÍ∞ï Ïã†Ï≤≠ Ï§ë" : "‚úÖ ÏàòÍ∞ï ÏôÑÎ£å"}
                  </p>
                )}
                
                {course.status === 'COMPLETED' && (
                  isEnrolled ? (
                    <button 
                      className="cancel-btn" 
                      onClick={() => handleCancelEnrollment(course.id)}
                    >
                      ÏàòÍ∞ï Ï∑®ÏÜå
                    </button>
                  ) : (
                    <button 
                      className="enroll-btn" 
                      onClick={() => handleEnroll(course.id)}
                    >
                      ÏàòÍ∞ï Ïã†Ï≤≠
                    </button>
                  )
                )}
              </li>
            );
          })}
        </ul>
      )}
    </div>
  );
};

export default StudentEnroll;
