import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './StudentEnroll.css';

const StudentEnroll = () => {
  const [courses, setCourses] = useState([]);  
  const [enrolledCourses, setEnrolledCourses] = useState([]); 
  const [loading, setLoading] = useState(true);  
  const [error, setError] = useState(null);  
  const [memberId, setMemberId] = useState(null);
  const navigate = useNavigate();
  const token = localStorage.getItem('token');

  const fetchEnrollments = useCallback(async () => {
    if (!memberId) return;
    try {
      const response = await axios.get(`http://localhost:8090/api/enrollments/member/${memberId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setEnrolledCourses(response.data);
    } catch (error) {
      console.error("❌ 수강 목록 불러오기 실패:", error);
    }
  }, [memberId, token]);

  useEffect(() => {
    if (!token) {
      alert("로그인이 필요합니다.");
      navigate('/login');
      return;
    }

    const fetchUserInfo = async () => {
      try {
        const response = await axios.get('http://localhost:8090/api/users/me', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setMemberId(response.data.id);
      } catch (error) {
        console.error("❌ 사용자 정보 가져오기 실패:", error);
        alert("사용자 정보를 불러올 수 없습니다. 다시 로그인해주세요.");
        navigate('/login');
      }
    };
    
    const fetchCourses = async () => {
      try {
        const response = await axios.get('http://localhost:8090/api/courses', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setCourses(response.data);
      } catch (error) {
        setError("강의 목록을 불러오는 데 실패했습니다.");
      } finally {
        setLoading(false);
      }
    };

    fetchUserInfo();
    fetchCourses();
    fetchEnrollments();
  }, [token, navigate, memberId, fetchEnrollments]);

  const handleEnroll = async (courseId) => {
    if (!token || !memberId) { 
      alert("로그인이 필요합니다.");
      navigate('/login');
      return;
    }
  
    try {
      await axios.post('http://localhost:8090/api/enrollments', {
        memberId: memberId,
        courseId: courseId
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });
  
      alert("수강 신청 완료!");
      fetchEnrollments();
    } catch (error) {
      console.error("❌ 수강 신청 실패:", error.response?.data || error.message);
      alert("수강 신청 실패: " + (error.response?.data || error.message));
    }
  };
  
  // 수강 취소 기능 추가
  const handleCancelEnrollment = async (courseId) => {
    if (!token || !memberId) {
      alert("로그인이 필요합니다.");
      navigate('/login');
      return;
    }

    // 확인 다이얼로그 표시
    const confirmCancel = window.confirm("정말로 수강 신청을 취소하시겠습니까?");
    if (!confirmCancel) return;

    try {
      // 해당 강의의 enrollment 찾기
      const enrollment = enrolledCourses.find(e => e && e.course && e.course.id === courseId);
      
      if (!enrollment) {
        alert("수강 신청 정보를 찾을 수 없습니다.");
        return;
      }

      // 수강 신청 취소 API 호출
      await axios.delete(`http://localhost:8090/api/enrollments/${enrollment.id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });

      alert("수강 신청이 취소되었습니다.");
      fetchEnrollments(); // 목록 새로고침
    } catch (error) {
      console.error("❌ 수강 취소 실패:", error.response?.data || error.message);
      alert("수강 취소 실패: " + (error.response?.data || error.message));
    }
  };

  const getEnrollmentStatus = (courseId) => {
    const enrollment = enrolledCourses.find(e => e && e.course && e.course.id === courseId);
    if (enrollment) {
      return enrollment.status; // PENDING, APPROVED, REJECTED
    }
    return null;
  };

  // 해당 강의의 enrollment ID 가져오기
  const getEnrollmentId = (courseId) => {
    const enrollment = enrolledCourses.find(e => e && e.course && e.course.id === courseId);
    return enrollment ? enrollment.id : null;
  };

  if (loading) return <div className="loading">로딩 중...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="student-enroll-container">
      <h2>📚 수강 신청 페이지</h2>
      {courses.length === 0 ? (
        <p>수강 가능한 강의가 없습니다.</p>
      ) : (
        <ul className="course-list">
          {courses.map((course) => {
            const status = getEnrollmentStatus(course.id);
            const isEnrolled = status === "PENDING" || status === "APPROVED";

            return (
              <li key={course.id} className="course-item">
                <h3>{course.name}</h3>
                <p>📅 학습 시작일: {course.startDate}</p>
                <p>⏳ 학습 종료일: {course.endDate}</p>
                <p>🔖 상태: {course.status === 'COMPLETED' ? '신청 가능' : '마감됨'}</p>
                
                {status && (
                  <p className={`${status.toLowerCase()}-text`}>
                    {status === "PENDING" ? "⏳ 수강 신청 중" : "✅ 수강 완료"}
                  </p>
                )}
                
                {course.status === 'COMPLETED' && (
                  isEnrolled ? (
                    <button 
                      className="cancel-btn" 
                      onClick={() => handleCancelEnrollment(course.id)}
                    >
                      수강 취소
                    </button>
                  ) : (
                    <button 
                      className="enroll-btn" 
                      onClick={() => handleEnroll(course.id)}
                    >
                      수강 신청
                    </button>
                  )
                )}
              </li>
            );
          })}
        </ul>
      )}
    </div>
  );
};

export default StudentEnroll;
