import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import axios from "axios";
import "./AddTeamMember.css";

function AddTeamMember() {
  const { state } = useLocation();
  const { courseId, courseName } = state || {};

  const [teams, setTeams] = useState([]);
  const [selectedTeam, setSelectedTeam] = useState("");
  const [members, setMembers] = useState([]);
  const [selectedMember, setSelectedMember] = useState("");
  const [role, setRole] = useState("ÌåÄÏõê");
  const [message, setMessage] = useState("");
  const [memberId, setMemberId] = useState(null);
  const token = localStorage.getItem("token");

  // üîπ ÌòÑÏû¨ Î°úÍ∑∏Ïù∏Ìïú ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ Í∞ÄÏ†∏Ïò§Í∏∞ (memberId ÌôïÏù∏)
  useEffect(() => {
    const fetchUserInfo = async () => {
      try {
        const response = await axios.get("http://localhost:8090/api/users/me", {
          headers: { Authorization: `Bearer ${token}` },
        });
        console.log("ÌòÑÏû¨ Î°úÍ∑∏Ïù∏Ìïú memberId:", response.data.id);
        setMemberId(response.data.id);
      } catch (error) {
        console.error("ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ Í∞ÄÏ†∏Ïò§Í∏∞ Ïã§Ìå®:", error);
      }
    };

    fetchUserInfo();
  }, [token]);

  // üîπ Í∞ïÏùò ID Í∏∞Î∞ò ÌåÄ Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞
  useEffect(() => {
    if (!courseId) return;

    const fetchTeams = async () => {
      try {
        const response = await axios.get(
          `http://localhost:8090/api/team?courseId=${courseId}`,
          { headers: { Authorization: `Bearer ${token}` } }
        );

        if (Array.isArray(response.data)) {
          setTeams(response.data);
          if (response.data.length > 0) {
            setSelectedTeam(response.data[0].id);
          }
        } else {
          setMessage("ÌåÄ Îç∞Ïù¥ÌÑ∞Î•º Ïò¨Î∞îÎ•¥Í≤å Î∂àÎü¨Ïò§ÏßÄ Î™ªÌñàÏäµÎãàÎã§.");
        }
      } catch (err) {
        console.error("ÌåÄ Ï†ïÎ≥¥ Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:", err);
        setMessage("ÌåÄ Ï†ïÎ≥¥Î•º Í∞ÄÏ†∏Ïò§Îäî Ï§ë Ïò§Î•ò Î∞úÏÉù.");
      }
    };

    fetchTeams();
  }, [courseId, token]);

  // üîπ ÏàòÍ∞ï Ïã†Ï≤≠Ìïú ÌïôÏÉù Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞ (courseId Í∏∞Î∞ò)
  useEffect(() => {
    if (!courseId) {
      console.warn("‚ö†Ô∏è courseIdÍ∞Ä ÏóÜÏùå, fetchMembers Ïã§Ìñâ Ïïà Ìï®.");
      return;
    }

    const fetchMembers = async () => {
      try {
        const response = await axios.get(
          `http://localhost:8090/api/enrollments/course/${courseId}/students`,
          { headers: { Authorization: `Bearer ${token}` } }
        );

        console.log("üìå ÏàòÍ∞ï Ïã†Ï≤≠Ìïú ÌïôÏÉù Î™©Î°ù:", response.data);

        // ÏàòÍ∞ï Ïã†Ï≤≠Ìïú ÌïôÏÉù Î™©Î°ù ÏÑ§Ï†ï
        setMembers(response.data);

        if (response.data.length > 0 && response.data[0].memberId) {
          setSelectedMember(response.data[0].memberId);
        }
      } catch (err) {
        console.error("‚ùå ÏàòÍ∞ï ÌïôÏÉù Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:", err);
      }
    };

    fetchMembers();
  }, [courseId, token]);

  // üîπ ÌåÄÏõê Ï∂îÍ∞Ä ÏöîÏ≤≠
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!selectedTeam) {
      setMessage("ÌåÄÏù¥ ÏÑ†ÌÉùÎêòÏßÄ ÏïäÏïòÏäµÎãàÎã§.");
      return;
    }

    try {
      const payload = {
        teamId: selectedTeam,
        memberId: selectedMember,
        role,
      };

      console.log("üì§ ÌåÄÏõê Ï∂îÍ∞Ä ÏöîÏ≤≠ Îç∞Ïù¥ÌÑ∞:", payload);

      await axios.post("http://localhost:8090/api/team-members", payload, {
        headers: { Authorization: `Bearer ${token}` },
      });

      setMessage("‚úÖ ÌåÄÏõêÏù¥ ÏÑ±Í≥µÏ†ÅÏúºÎ°ú Ï∂îÍ∞ÄÎêòÏóàÏäµÎãàÎã§!");
    } catch (error) {
      console.error("‚ùå ÌåÄÏõê Ï∂îÍ∞Ä Ïã§Ìå®:", error);
      setMessage("ÌåÄÏõê Ï∂îÍ∞Ä Ïã§Ìå®!");
    }
  };

  return (
    <div>
      <h2>ÌåÄÏõê Ï∂îÍ∞Ä - Í∞ïÏùò: {courseName || "Í∞ïÏùò Ï†ïÎ≥¥ ÏóÜÏùå"}</h2>
      {message && <p>{message}</p>}
      <form onSubmit={handleSubmit}>
        <label>
          ÌåÄ ÏÑ†ÌÉù:
          <select
            value={selectedTeam}
            onChange={(e) => setSelectedTeam(e.target.value)}
            required
          >
            {teams.length === 0 ? (
              <option value="">ÌåÄ ÏóÜÏùå</option>
            ) : (
              teams.map((team) => (
                <option key={team.id} value={team.id}>
                  {team.name}
                </option>
              ))
            )}
          </select>
        </label>
        <br />
        <label>
  Î©§Î≤Ñ ÏÑ†ÌÉù:
  <select
    value={selectedMember}
    onChange={(e) => setSelectedMember(e.target.value)}
    required
  >
    {members.length === 0 ? (
      <option value="">ÌïôÏÉù ÏóÜÏùå</option>
    ) : (
      members.map((member) => (
        <option key={member.memberId} value={member.memberId}>
          {member.memberName} 
        </option>
      ))
    )}
  </select>
</label>

        <br />
        <label>
          Ïó≠Ìï†:
          <select value={role} onChange={(e) => setRole(e.target.value)}>
            <option value="ÌåÄÏõê">ÌåÄÏõê</option>
            <option value="ÌåÄÏû•">ÌåÄÏû•</option>
          </select>
        </label>
        <br />
        <button type="submit" disabled={!selectedTeam || members.length === 0}>
          ÌåÄÏõê Ï∂îÍ∞Ä
        </button>
      </form>
    </div>
  );
}

export default AddTeamMember;
