import { useState, useEffect } from "react";
import { useParams, useNavigate, useLocation } from "react-router-dom";
import axios from "axios";

export function CreatePost() {
    const { boardId } = useParams(); // URL에서 boardId 가져오기
    const navigate = useNavigate();
    const location = useLocation();
    const token = localStorage.getItem("token"); // 토큰 가져오기
    const [title, setTitle] = useState("");
    const [content, setContent] = useState("");
    const [memberId, setMemberId] = useState(location.state?.memberId || null);

    // ✅ 로그인한 사용자 정보 가져오기
    const fetchUserData = async () => {
        try {
            const response = await axios.get("http://localhost:8090/api/users/me", {
                headers: { Authorization: `Bearer ${token}` },
                withCredentials: true, // 자격 증명 포함
            });
            setMemberId(response.data.id);
        } catch (error) {
            console.error("사용자 정보 조회 실패:", error);
            alert("사용자 정보 조회에 실패했습니다.");
        }
    };

    useEffect(() => {
        if (!memberId && token) {
            fetchUserData();
        }
    }, [token, memberId]);

    // ✅ 게시글 작성 요청
    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!memberId) {
            alert("로그인이 필요합니다.");
            return;
        }

        const requestData = { title, content, memberId };
        

        try {
            const response = await axios.post(
                `http://localhost:8090/api/weekly-post/create/${boardId}`, // boardId를 URL에 포함
                requestData, // 게시글 데이터
                {
                    headers: {
                        "Content-Type": "application/json", // JSON 형식으로 보내기
                        Authorization: `Bearer ${token}`, // 토큰 포함
                    },
                    withCredentials: true, // 로그인 상태 유지
                }
            );
            if (response.status === 200) {
                alert("게시글이 등록되었습니다.");
                // 게시글 등록 후 `PostList`로 이동
                navigate(`/board/${boardId}`); // boardId가 포함된 경로로 이동
            } else {
                alert("게시글 등록에 실패했습니다.");
            }
        } catch (error) {
            console.error("게시글 등록 실패:", error.response ? error.response.data : error.message);
            alert("게시글 등록에 실패했습니다.");
        }
    };

    return (
        <div className="p-4 border rounded shadow-lg bg-white max-w-lg mx-auto">
            <h1 className="text-2xl font-bold mb-4">게시글 작성</h1>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    className="w-full border p-2 mb-2"
                    placeholder="제목"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                    required
                />
                <textarea
                    className="w-full border p-2 mb-2"
                    placeholder="내용"
                    value={content}
                    onChange={(e) => setContent(e.target.value)}
                    required
                />
                <div className="flex justify-between mt-4">
                    <button
                        type="button"
                        className="bg-gray-400 text-white px-4 py-2 rounded"
                        onClick={() => navigate(`/board/${boardId}`)} // 취소 버튼 클릭 시 `PostList`로 이동
                    >
                        취소
                    </button>
                    <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded">
                        등록하기
                    </button>
                </div>
            </form>
        </div>
    );
}

export default CreatePost;
