  const { state } = useLocation();
  const { courseId, courseName } = state || {};

  const [teams, setTeams] = useState([]);
  const [selectedTeam, setSelectedTeam] = useState("");
  const [members, setMembers] = useState([]);
  const [selectedMember, setSelectedMember] = useState("");
  const [role, setRole] = useState("ÌåÄÏõê");
  const [message, setMessage] = useState("");
  const [memberId, setMemberId] = useState(null);
  // ÌòÑÏû¨ ÏÑ†ÌÉùÎêú ÌåÄÏùò Í∏∞Ï°¥ ÌåÄÏõê ÏÉÅÌÉú
  const [currentTeamMembers, setCurrentTeamMembers] = useState([]);
  const token = localStorage.getItem("token");

  // üîπ ÌòÑÏû¨ Î°úÍ∑∏Ïù∏Ìïú ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ Í∞ÄÏ†∏Ïò§Í∏∞ (memberId ÌôïÏù∏)
  useEffect(() => {
    const fetchUserInfo = async () => {
      try {
        const response = await axios.get("http://localhost:8090/api/users/me", {
          headers: { Authorization: `Bearer ${token}` },
        });
        console.log("ÌòÑÏû¨ Î°úÍ∑∏Ïù∏Ìïú memberId:", response.data.id);
        setMemberId(response.data.id);
      } catch (error) {
        console.error("ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ Í∞ÄÏ†∏Ïò§Í∏∞ Ïã§Ìå®:", error);
      }
    };

    fetchUserInfo();
  }, [token]);

  // üîπ Í∞ïÏùò ID Í∏∞Î∞ò ÌåÄ Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞
  useEffect(() => {
    if (!courseId) return;

    const fetchTeams = async () => {
      try {
        const response = await axios.get(
          `http://localhost:8090/api/team?courseId=${courseId}`,
          { headers: { Authorization: `Bearer ${token}` } }
        );

        if (Array.isArray(response.data)) {
          setTeams(response.data);
          if (response.data.length > 0) {
            setSelectedTeam(response.data[0].id);
          }
        } else {
          setMessage("ÌåÄ Îç∞Ïù¥ÌÑ∞Î•º Ïò¨Î∞îÎ•¥Í≤å Î∂àÎü¨Ïò§ÏßÄ Î™ªÌñàÏäµÎãàÎã§.");
        }
      } catch (err) {
        console.error("ÌåÄ Ï†ïÎ≥¥ Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:", err);
        setMessage("ÌåÄ Ï†ïÎ≥¥Î•º Í∞ÄÏ†∏Ïò§Îäî Ï§ë Ïò§Î•ò Î∞úÏÉù.");
      }
    };

    fetchTeams();
  }, [courseId, token]);

  // üîπ ÏàòÍ∞ï Ïã†Ï≤≠Ìïú ÌïôÏÉù Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞ (courseId Í∏∞Î∞ò)
  useEffect(() => {
    if (!courseId) {
      console.warn("‚ö†Ô∏è courseIdÍ∞Ä ÏóÜÏùå, fetchMembers Ïã§Ìñâ Ïïà Ìï®.");
      return;
    }

    const fetchMembers = async () => {
      try {
        const response = await axios.get(
          `http://localhost:8090/api/enrollments/course/${courseId}/students`,
          { headers: { Authorization: `Bearer ${token}` } }
        );

        console.log("üìå ÏàòÍ∞ï Ïã†Ï≤≠Ìïú ÌïôÏÉù Î™©Î°ù:", response.data);
        setMembers(response.data);

        if (response.data.length > 0 && response.data[0].memberId) {
          setSelectedMember(response.data[0].memberId);
        }
      } catch (err) {
        console.error("‚ùå ÏàòÍ∞ï ÌïôÏÉù Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:", err);
      }
    };

    fetchMembers();
  }, [courseId, token]);

  // üîπ ÏÑ†ÌÉùÎêú ÌåÄÏùò ÌòÑÏû¨ ÌåÄÏõê Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞
  useEffect(() => {
    if (!selectedTeam) return;
    const fetchTeamMembers = async () => {
      try {
        const response = await axios.get("http://localhost:8090/api/team-members", {
          headers: { Authorization: `Bearer ${token}` },
          params: { teamId: selectedTeam },
        });
        console.log("ÌòÑÏû¨ ÌåÄÏõê Î™©Î°ù:", response.data);
        setCurrentTeamMembers(response.data);
      } catch (error) {
        console.error("ÌåÄÏõê Ï†ïÎ≥¥ Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:", error);
      }
    };

    fetchTeamMembers();
  }, [selectedTeam, token]);

  // üîπ ÌåÄÏõê Ï∂îÍ∞Ä ÏöîÏ≤≠ (ÌåÄÏû• Ï§ëÎ≥µ Î∞è ÎèôÏùº Î©§Î≤Ñ Ï§ëÎ≥µ Ï∂îÍ∞Ä Î∞©ÏßÄ)
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!selectedTeam) {
      setMessage("ÌåÄÏù¥ ÏÑ†ÌÉùÎêòÏßÄ ÏïäÏïòÏäµÎãàÎã§.");
      return;
    }

    /// ÌòÑÏû¨ ÌåÄÏõê Î™©Î°ùÏóêÏÑú, member_idÏôÄ selectedMember(Ïà´Ïûê Î≥ÄÌôò)Î•º ÎπÑÍµê
if (currentTeamMembers.some(m => m.member_id === Number(selectedMember))) {
  setMessage("Ïù¥ÎØ∏ Ìï¥Îãπ ÌåÄÏóê Îì±Î°ùÎêú Î©§Î≤ÑÏûÖÎãàÎã§.");
  return;
}

    // Ïó≠Ìï†Ïù¥ ÌåÄÏû•Ïù∏ Í≤ΩÏö∞, Ìï¥Îãπ ÌåÄÏóê ÌåÄÏû•Ïù¥ ÏûàÎäîÏßÄ Ï≤¥ÌÅ¨
    if (
      role === "ÌåÄÏû•" &&
      currentTeamMembers.some(member => member.role === "ÌåÄÏû•" || member.roll === "ÌåÄÏû•")
    ) {
      setMessage("Ïù¥ÎØ∏ ÌåÄÏóê ÌåÄÏû•Ïù¥ Îì±Î°ùÎêòÏñ¥ ÏûàÏäµÎãàÎã§.");
      return;
    }

    try {
      const payload = {
        teamId: selectedTeam,
        memberId: selectedMember,
        role,
      };

      console.log("üì§ ÌåÄÏõê Ï∂îÍ∞Ä ÏöîÏ≤≠ Îç∞Ïù¥ÌÑ∞:", payload);

      await axios.post("http://localhost:8090/api/team-members", payload, {
        headers: { Authorization: `Bearer ${token}` },
      });

      setMessage("‚úÖ ÌåÄÏõêÏù¥ ÏÑ±Í≥µÏ†ÅÏúºÎ°ú Ï∂îÍ∞ÄÎêòÏóàÏäµÎãàÎã§!");
      // Ï∂îÍ∞Ä ÌõÑ, ÌòÑÏû¨ ÌåÄÏõê Î™©Î°ù Í∞±Ïã†
      const updatedResponse = await axios.get("http://localhost:8090/api/team-members", {
        headers: { Authorization: `Bearer ${token}` },
        params: { teamId: selectedTeam },
      });
      setCurrentTeamMembers(updatedResponse.data);
    } catch (error) {
      console.error("‚ùå ÌåÄÏõê Ï∂îÍ∞Ä Ïã§Ìå®:", error);
      setMessage("ÌåÄÏõê Ï∂îÍ∞Ä Ïã§Ìå®!");
    }
  };

  return (
    <div>
      <h2>ÌåÄÏõê Ï∂îÍ∞Ä - Í∞ïÏùò: {courseName || "Í∞ïÏùò Ï†ïÎ≥¥ ÏóÜÏùå"}</h2>
      {message && <p>{message}</p>}
      <form onSubmit={handleSubmit}>
        <label>
          ÌåÄ ÏÑ†ÌÉù:
          <select
            value={selectedTeam}
            onChange={(e) => setSelectedTeam(e.target.value)}
            required
          >
            {teams.length === 0 ? (
              <option value="">ÌåÄ ÏóÜÏùå</option>
            ) : (
              teams.map((team) => (
                <option key={team.id} value={team.id}>
                  {team.name}
