import { useRef, useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import AddContentForm from "./AddContentForm";
import AddAnnouncement from "./AddAnnouncement";
import AddAssignment from "./AddAssignment";
import CreateBoard from "./CreateBoard";
import { Link } from "react-router-dom";
import "./CourseDetail.css";

// Separate VideoPlayer component properly defined
const VideoPlayer = ({ videoUrl, studentId, courseId, weekNumber, token }) => {
  const videoRef = useRef(null);
  const [isChecked, setIsChecked] = useState(false);

  useEffect(() => {
    const checkAttendance = async () => {
      try {
        const response = await axios.get(
          `http://localhost:8090/api/attendance/${studentId}/${courseId}/${weekNumber}`,
          { headers: { Authorization: `Bearer ${token}` } }
        );
        if (response.data.attended) {
          setIsChecked(true);
        }
      } catch (error) {
        console.error("Ï∂úÏÑù Îç∞Ïù¥ÌÑ∞ Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®", error);
      }
    };
    checkAttendance();
  }, [studentId, courseId, weekNumber, token]);

  const handleTimeUpdate = async () => {
    if (!videoRef.current) return;

    const video = videoRef.current;
    const progress = (video.currentTime / video.duration) * 100;

    if (progress >= 90 && !isChecked) {
      try {
        await axios.post(
          "http://localhost:8090/api/attendance/check",
          { studentId, courseId, weekNumber },
          { headers: { Authorization: `Bearer ${token}` } }
        );
        setIsChecked(true);
      } catch (error) {
        console.error("Ï∂úÏÑù Ï≤¥ÌÅ¨ Ïã§Ìå®", error);
      }
    }
  };

  return (
    <div>
      <video 
        ref={videoRef} 
        controls 
        onTimeUpdate={handleTimeUpdate}
        width="640" 
        height="360"
      >
        <source src={videoUrl} type={`video/${videoUrl?.split('.').pop().toLowerCase()}`} />
        Î∏åÎùºÏö∞Ï†ÄÍ∞Ä ÎπÑÎîîÏò§ ÌÉúÍ∑∏Î•º ÏßÄÏõêÌïòÏßÄ ÏïäÏäµÎãàÎã§.
      </video>
      <div>
        <label>
          <input type="checkbox" checked={isChecked} readOnly />
          Ï∂úÏÑù ÏôÑÎ£å
        </label>
      </div>
    </div>
  );
};

const CourseDetailPage = () => {
  const { courseId } = useParams();
  const navigate = useNavigate();
  const [course, setCourse] = useState(null);
  const [weeks, setWeeks] = useState([]);
  const [contents, setContents] = useState({});
  const [announcements, setAnnouncements] = useState({});
  const [assignments, setAssignments] = useState({});
  const [boards, setBoards] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activeWeek, setActiveWeek] = useState(null);
  const [activeGoalWeek, setActiveGoalWeek] = useState(null);
  const [editingDescription, setEditingDescription] = useState(false);
  const [newDescription, setNewDescription] = useState("");
  const [showBoardForm, setShowBoardForm] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');
  const [memberId, setMemberId] = useState(null);
  const token = localStorage.getItem("token");
  const [role, setRole] = useState(null);

  useEffect(() => {
    if (!courseId) return;
  
    const fetchCourseDetails = async () => {
      try {
        const courseResponse = await axios.get(
          `http://localhost:8090/api/courses/${courseId}`, 
          { headers: { Authorization: `Bearer ${token}` } }
        );
  
        setCourse(courseResponse.data);
        setNewDescription(courseResponse.data.description || "");
  
        const storedMemberId = localStorage.getItem('memberId');
        if (storedMemberId) {
          setMemberId(storedMemberId);
        } else {
          const userResponse = await axios.get(
            'http://localhost:8090/api/users/me', 
            { headers: { Authorization: `Bearer ${token}` } }
          );
          setMemberId(userResponse.data.memberId);
        }
  
        const contentResponse = await axios.get(
          `http://localhost:8090/api/courses/${courseId}/weeks/contents`, 
          { headers: { Authorization: `Bearer ${token}` } }
        );
  
        setContents(contentResponse.data || {});
   
      } catch (error) {
        console.error("‚ùå Í∞ïÏùò ÏÉÅÏÑ∏ Ï†ïÎ≥¥ Î∂àÎü¨Ïò§Í∏∞ Ïò§Î•ò:", error);
      } finally {
        setLoading(false);
      }
    };
  
    fetchCourseDetails();
  }, [courseId, token]);

  useEffect(() => {
    const fetchUserRole = async () => {
      try {
        const response = await axios.get(
          'http://localhost:8090/api/users/me', 
          { headers: { Authorization: `Bearer ${token}` } }
        );
        setRole(response.data.role);
      } catch (error) {
        console.error("‚ùå ÏÇ¨Ïö©Ïûê Ïó≠Ìï† Î∂àÎü¨Ïò§Í∏∞ Ïò§Î•ò:", error);
      }
    };
  
    if (token) {
      fetchUserRole();
    }
  }, [token]);

  useEffect(() => {
    if (!courseId || weeks.length === 0) return;

    const fetchAnnouncements = async () => {
      const announcementsData = {};

      for (let week of weeks) {
        try {
          // Consistent API path format with /api/ prefix
          const response = await axios.get(
            `http://localhost:8090/api/courses/${courseId}/weeks/${week.weekNumber}/announcements`,
            { headers: { Authorization: `Bearer ${token}` } }
          );
          announcementsData[week.weekNumber] = response.data || [];
        } catch (error) {
          announcementsData[week.weekNumber] = [];
        }
      }
      setAnnouncements(announcementsData);
    };

    fetchAnnouncements();
  }, [weeks, courseId, token]);

  useEffect(() => {
    if (!courseId) return;
    if (!token) {
      console.error("üìå ÌÜ†ÌÅ∞Ïù¥ ÏóÜÏäµÎãàÎã§. Î°úÍ∑∏Ïù∏ ÏÉÅÌÉúÎ•º ÌôïÏù∏ÌïòÏÑ∏Ïöî.");
      return;
    }
  
    const fetchWeeksAndBoards = async () => {
      try {
        console.log("üìå API ÏöîÏ≤≠ ÏãúÏûë! ÌÜ†ÌÅ∞:", token);
  
        const weeksResponse = await axios.get(
          `http://localhost:8090/api/courses/${courseId}/weeks`,
          { headers: { Authorization: `Bearer ${token}` } }
        );
  
        console.log("üìå Ï£ºÏ∞® Îç∞Ïù¥ÌÑ∞ ÏùëÎãµ:", weeksResponse.data);
  
        const weeksData = weeksResponse.data || [];
        if (!Array.isArray(weeksData) || weeksData.length === 0) {
          console.warn("‚ö†Ô∏è Ï£ºÏ∞® Îç∞Ïù¥ÌÑ∞Í∞Ä ÎπÑÏñ¥ ÏûàÏäµÎãàÎã§.");
          return;
        }
        
        setWeeks(weeksData);
  
        const boardData = {};
        for (const week of weeksData) {
          if (!week.weekNumber) {
            console.warn(`‚ö†Ô∏è Ï£ºÏ∞® Îç∞Ïù¥ÌÑ∞Ïóê weekNumberÍ∞Ä ÏóÜÏäµÎãàÎã§.`, week);
            continue;
          }
  
          try {
            console.log(`üìå ${week.weekNumber}Ï£ºÏ∞® Í≤åÏãúÌåê ÏöîÏ≤≠ ÏãúÏûë!`);
            // Consistent API path format with /api/ prefix
            const response = await axios.get(
              `http://localhost:8090/api/weekly-board/course/${courseId}/week/${week.weekNumber}/boards`,
              { headers: { Authorization: `Bearer ${token}` } }
            );
  
            console.log(`üìå ${week.weekNumber}Ï£ºÏ∞® Í≤åÏãúÌåê ÏùëÎãµ:`, response.data);
            
            if (!response.data) {
              console.warn(`‚ö†Ô∏è ${week.weekNumber}Ï£ºÏ∞® Í≤åÏãúÌåê ÏùëÎãµÏù¥ ÎπÑÏñ¥ ÏûàÏäµÎãàÎã§.`);
              boardData[week.weekNumber] = [];
            } else {
              boardData[week.weekNumber] = response.data;
            }
  
          } catch (error) {
            console.error(`‚ùå ${week.weekNumber}Ï£ºÏ∞® Í≤åÏãúÌåê Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:`, error);
            boardData[week.weekNumber] = [];
          }
        }
  
        setBoards(boardData);
        console.log("üìå ÏµúÏ¢Ö boardData ÏÉÅÌÉú:", boardData);
  
      } catch (error) {
        console.error("‚ùå Ï£ºÏ∞®Î≥Ñ Îç∞Ïù¥ÌÑ∞ Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:", error);
      }
    };
  
    fetchWeeksAndBoards();
  }, [courseId, token]);
  
  useEffect(() => {
    console.log("üìå boards ÏÉÅÌÉú ÏóÖÎç∞Ïù¥Ìä∏Îê®:", boards);
  }, [boards]);

  useEffect(() => {
    if (!courseId || weeks.length === 0) return;

    const fetchAssignments = async () => {
      const assignmentsData = {};
      for (let i = 0; i < weeks.length; i++) {
        const week = weeks[i];
        const weekNumber = week.weekNumber;

        if (!weekNumber) continue;

        try {
          const response = await axios.get(
            `http://localhost:8090/api/courses/${courseId}/weeks/${weekNumber}/assignments`,
            { headers: { Authorization: `Bearer ${token}` } }
          );
          
          assignmentsData[weekNumber] = response.data || [];
        } catch (error) {
          assignmentsData[weekNumber] = [];
        }
      }

      setAssignments(assignmentsData);
    };

    fetchAssignments();
  }, [weeks, courseId, token]);

  // Add missing handleDescriptionChange and handleDescriptionSave functions
  const handleDescriptionChange = (e) => {
    setNewDescription(e.target.value);
  };

  const handleDescriptionSave = async () => {
    try {
      await axios.put(
        `http://localhost:8090/api/courses/${courseId}/description`,
        { description: newDescription },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setEditingDescription(false);
    } catch (error) {
      console.error("‚ùå Í∞ïÏùò ÏÑ§Î™Ö ÏóÖÎç∞Ïù¥Ìä∏ Ïò§Î•ò:", error);
    }
  };

  const toggleForm = (weekNumber) => {
    setActiveWeek((prevWeek) => (prevWeek === weekNumber ? null : weekNumber));
  };

  const toggleGoalForm = (weekNumber) => {
    setActiveGoalWeek((prevWeek) => (prevWeek === weekNumber ? null : weekNumber));
  };

  if (loading) {
    return <div className="loading">Î°úÎî© Ï§ë...</div>;
  }

  return (
    <div className="course-detail-container">
      <div className="course-header">
        <h2>{course?.name} - Í∞ïÏùòÏã§</h2>
        <p className="professor-name">Îã¥Îãπ ÍµêÏàò: {course?.professor || "Ï†ïÎ≥¥ ÏóÜÏùå"}</p>
      </div>

      {showBoardForm && <CreateBoard courseId={courseId} />}

      <div className="course-overview">
        <h3>Í∞ïÏùò Í∞úÏöî</h3>
        {editingDescription ? (
          <div>
            <textarea value={newDescription} onChange={handleDescriptionChange} rows="4" cols="50" />
            <button onClick={handleDescriptionSave}>Ï†ÄÏû•</button>
            <button onClick={() => setEditingDescription(false)}>Ï∑®ÏÜå</button>
          </div>
        ) : (
          <div>
            <p>{newDescription || "Í∞ïÏùò Í∞úÏöîÍ∞Ä Ï†úÍ≥µÎêòÏßÄ ÏïäÏïòÏäµÎãàÎã§."}</p>
            {role === 'PROFESSOR' && (
              <button onClick={() => setEditingDescription(true)}>‚öôÔ∏è</button>
            )}
          </div>
        )}
        <div className="overview-buttons">
          <button onClick={() => navigate(`/courses/${courseId}/announcements`)}>üì¢ Í≥µÏßÄÏÇ¨Ìï≠</button>
          <button onClick={() => navigate(`/courses/${courseId}/qna`)}>‚ùì Í∞ïÏ¢å Q&A</button>
        </div>
      </div>

      <div className="week-activities">
        <h3>Ï£ºÏ∞®Î≥Ñ ÌïôÏäµ ÌôúÎèô</h3>
        {weeks.length > 0 ? (
          <div className="weeks-container">
            {weeks.map((week) => {
              const weekContents = contents?.[week.weekNumber] || [];
              const weekAnnouncements = announcements?.[week.weekNumber] || [];
              const weekAssignments = assignments?.[week.weekNumber] || [];
             
              return (
                <div key={week.weekNumber} className="week-item">
                  <div className="week-header">
                    <h4>{week.weekNumber}Ï£ºÏ∞®</h4>
                    {role === 'PROFESSOR' && (
                      <button onClick={() => toggleGoalForm(week.weekNumber)} className="settings-btn">‚öôÔ∏è</button>
                    )}
                  </div>

                  {activeGoalWeek === week.weekNumber && (
                    <div className="goal-form-container">
                      <AddAnnouncement courseId={courseId} weekNumber={week.weekNumber} />
                    </div>
                  )}

                  {weekContents.length > 0 && (
                    <ul className="content-list">
                      {weekContents.map((content, index) => {
                        // ÌååÏùº ÌôïÏû•Ïûê Ï∂îÏ∂ú
                        const fileExtension = content?.filePath?.split('.').pop().toLowerCase();
                        const isVideo = ['mp4', 'webm', 'ogg'].includes(fileExtension);

                        return (
                          <li key={`week-${week.weekNumber}-content-${index}`} className="content-item">
                            {isVideo ? (
                              <VideoPlayer 
                                videoUrl={content.filePath}
                                studentId={memberId}
                                courseId={courseId}
                                weekNumber={week.weekNumber}
                                token={token}
                              />
                            ) : (
                              <a href={content.filePath} target="_blank" rel="noopener noreferrer">
                                {content?.fileName || "ÌååÏùº Ïù¥Î¶Ñ ÏóÜÏùå"}
                              </a>
                            )}
                          </li>
                        );
                      })}
                    </ul>
                  )}

                  <div className="assignment-section">
                    {weekAssignments.length > 0 ? (
                      <ul className="assignment-list">
                        {weekAssignments.map((assignment, index) => {
                          if (!assignment.id) {
                            console.error(`Í≥ºÏ†ú ${assignment.title}Ïóê idÍ∞Ä ÏóÜÏäµÎãàÎã§.`);
                            return null;
                          }

                          return (
                            <li key={`week-${week.weekNumber}-assignment-${index}`} className="assignment-item">
                              <Link to={`/api/courses/${courseId}/weeks/${week.weekNumber}/assignments/${assignment.id}?token=${token}`}>
                                <h4>üìå Í≥ºÏ†ú: {assignment.title || "Ï†úÎ™© ÏóÜÏùå"}</h4>
                              </Link>
                            </li>
                          );
                        })}
                      </ul>
                    ) : (
                      <p></p>
                    )}
                  </div>

                  {weekAnnouncements.length > 0 && (
                    <ul className="announcement-list">
                      {weekAnnouncements.map((announcement, index) => (
                        <li key={`week-${week.weekNumber}-announcement-${index}`} className="announcement-item">
                          <strong>{announcement.title || "Ï†úÎ™© ÏóÜÏùå"}</strong> - {announcement.content || "ÎÇ¥Ïö© ÏóÜÏùå"}
                        </li>
                      ))}
                    </ul>
                  )}

                  <ul>
                    {boards[week.weekNumber] && boards[week.weekNumber].length > 0 ? (
                      boards[week.weekNumber].map((board) => (
                        <li key={board.boardId}>
                          <Link to={`/board/${board.boardId}`} className="text-blue-500 underline">
                            Í≤åÏãúÌåê: {board.title}
                          </Link>
                        </li>
                      ))
                    ) : (
                      <li className="text-gray-500"></li>
                    )}
                  </ul>

                  <div className="week-buttons">
                    {role === 'PROFESSOR' && (
                      <button onClick={() => toggleForm(week.weekNumber)} className="toggle-btn">
                        {activeWeek === week.weekNumber ? "Îã´Í∏∞" : "ÏûêÎ£å Î∞è ÌôúÎèô Ï∂îÍ∞Ä"}
                      </button>
                    )}
                  </div>

                  {activeWeek === week.weekNumber && (
                    <div className="form-container">
                      <AddContentForm courseId={courseId} weekNumber={week.weekNumber} token={token} />
                      <AddAssignment courseId={courseId} weekNumber={week.weekNumber} token={token} />
                      <CreateBoard courseId={courseId} weekNumber={week.weekNumber} token={token} />
                    </div>
                  )}
                </div>
              );
            })}
          </div>
        ) : (
          <p>Ï£ºÏ∞®Î≥Ñ ÌïôÏäµ ÌôúÎèôÏù¥ ÏóÜÏäµÎãàÎã§.</p>
        )}
      </div>
    </div>  
  );
};

export default CourseDetailPage;
