import { useRef, useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const VideoDetailPage = ({ videoUrl, week }) => {
    const videoRef = useRef(null);
    const [isChecked, setIsChecked] = useState(false);
    const [lastSavedProgress, setLastSavedProgress] = useState(0);
    const progressSaveInterval = 5; // 5초마다 진도율 저장 (너무 자주 저장하면 서버 부하가 커질 수 있음)
    const ATTENDANCE_THRESHOLD = 90; // 출석 인정 기준 시청률 (90%)
    const [content, setContent] = useState(null);
    const token = localStorage.getItem('token');
    const studentId = localStorage.getItem('studentId');
    const courseId = localStorage.getItem('courseId');

    // 디버깅용 로그
    useEffect(() => {
      if (studentId) {
        console.log("studentId: ", studentId);
      } else {
        console.log("studentId가 localStorage에 없습니다.");
      }

      if (courseId) {
        console.log("courseId: ", courseId);
      } else {
        console.log("courseId가 localStorage에 없습니다.");
      }
    }, []);

    // 비디오 콘텐츠 로드
    useEffect(() => {
        const savedContent = sessionStorage.getItem("videoContent");
        if (savedContent) {
          setContent(JSON.parse(savedContent));
        }
    }, []);

    // 초기 출석 상태 및 진도율 확인
    useEffect(() => {
      const checkAttendanceStatus = async () => {
        try {
          const response = await axios.get(
            `http://localhost:8090/api/attendance/status?studentId=${studentId}`,
            { headers: { Authorization: `Bearer ${token}` } }
          );
          
          // courseId가 키인 맵에서 출석 상태를 확인
          if (response.data[courseId] === true) {
            setIsChecked(true);
          }
        } catch (error) {
          console.error("출석 상태 확인 실패", error);
        }
      };
    
      // 이전 시청 진도율 확인
      const checkProgressStatus = async () => {
        try {
          const response = await axios.get(
            `http://localhost:8090/api/attendance/progress/status?studentId=${studentId}`,
            { headers: { Authorization: `Bearer ${token}` } }
          );
          
          console.log("진도율 데이터:", response.data);
          
          // courseId가 키인 맵에서 진도율 확인
          if (response.data[courseId]) {
            const savedProgress = response.data[courseId];
            setLastSavedProgress(savedProgress);
            console.log("저장된 진도율 확인됨:", savedProgress);
          }
        } catch (error) {
          console.error("진도율 확인 실패", error);
        }
      };
      
      if (studentId && courseId) {
        checkAttendanceStatus();
        checkProgressStatus();
      }
    }, [studentId, courseId, token]);
  
    // 비디오 메타데이터 로드 완료 후 진도율에 따라 영상 위치 설정
    const handleLoadedMetadata = () => {
      if (videoRef.current && lastSavedProgress > 0) {
        const duration = videoRef.current.duration;
        const newTime = (lastSavedProgress / 100) * duration;
        console.log(`비디오 시간 설정: ${newTime}초 (진도율 ${lastSavedProgress}%)`);
        videoRef.current.currentTime = newTime;
      }
    };
    
    // 주기적으로 진도율 저장
    useEffect(() => {
      const intervalId = setInterval(() => {
        saveCurrentProgress();
      }, progressSaveInterval * 1000); // 밀리초 단위로 변환
      
      // 컴포넌트 언마운트 시 인터벌 클리어
      return () => clearInterval(intervalId);
    }, []);
    
    // 현재 진도율 저장
    const saveCurrentProgress = async () => {
      if (!videoRef.current || !studentId || !courseId) return;
      
      const video = videoRef.current;
      if (video.paused) return; // 동영상이 일시정지 상태면 저장하지 않음
      
      const watchedPercentage = (video.currentTime / video.duration) * 100;
      
      // 이미 출석이 완료되었거나 진도율 변화가 1% 미만인 경우 저장하지 않음
      if (isChecked || Math.abs(watchedPercentage - lastSavedProgress) < 1) return;
      
      try {
        // 진도율 저장 API 호출
        const response = await axios.post(
          "http://localhost:8090/api/attendance/progress/video",
          { 
            studentId, 
            videoId: courseId, // API는 videoId를 예상하지만 현재 코드에서는 courseId를 사용
            watchedPercentage 
          },
          { headers: { Authorization: `Bearer ${token}` } }
        );
        
        console.log("진도율 저장됨:", watchedPercentage.toFixed(2) + "%", response.data);
        setLastSavedProgress(watchedPercentage);
      } catch (error) {
        console.error("진도율 저장 실패", error);
      }
    };
    
    // 비디오 종료 시 진행률 100%로 저장
    const handleVideoEnded = async () => {
      try {
        await axios.post(
          "http://localhost:8090/api/attendance/progress/video",
          { 
            studentId, 
            videoId: courseId,
            watchedPercentage: 100 
          },
          { headers: { Authorization: `Bearer ${token}` } }
        );
        
        setLastSavedProgress(100);
        console.log("비디오 완료: 진도율 100% 저장됨");
        
        // 출석 체크가 안 되어 있으면 자동으로 체크
        if (!isChecked && content?.id) {
          handleCourseAttendance();
        }
      } catch (error) {
        console.error("최종 진도율 저장 실패", error);
      }
    };
    
    // 영상 시청 중 처리
    const handleTimeUpdate = async () => {
      if (!videoRef.current || !studentId || !courseId) return;
      
      const video = videoRef.current;
      const watchedPercentage = (video.currentTime / video.duration) * 100;
    
      // 시청률이 출석 인정 기준을 넘었고 아직 출석 체크가 안 되어 있을 때
      if (watchedPercentage >= ATTENDANCE_THRESHOLD && !isChecked) {
        if (!content || !content.id) {
          console.error("contentId가 존재하지 않습니다.");
          return;
        }
    
        try {
          const response = await axios.post(
            "http://localhost:8090/api/attendance/course",
            { 
              studentId, 
              courseId, 
              contentId: content.id,
              watchedPercentage 
            },
            { headers: { Authorization: `Bearer ${token}` } }
          );
    
          if (response.data === "출석 완료") {
            setIsChecked(true);
            console.log("출석 체크 완료");
          }
        } catch (error) {
          console.error("강의 출석 체크 실패", error);
        }
      }
    };
    
    // 수동 출석 체크 (강의 단위)
    const handleCourseAttendance = async () => {
      if (!content || !content.id) {
        console.error("contentId가 존재하지 않습니다.");
        return;
      }

      try {
        const response = await axios.post(
          "http://localhost:8090/api/attendance/course",
          { 
            studentId, 
            courseId, 
            contentId: content.id,
            watchedPercentage: 100 
          },
          { headers: { Authorization: `Bearer ${token}` } }
        );
        
        if (response.data === "출석 완료") {
          setIsChecked(true);
          console.log("수동 출석 체크 완료");
        }
      } catch (error) {
        console.error("강의 출석 체크 실패", error);
      }
    };

    if (!content) {
      return <p>로딩 중...</p>;
    }

    return (
      <div className="video-player-container">
        <video
          ref={videoRef}
          controls
          autoPlay
          onTimeUpdate={handleTimeUpdate}
          onLoadedMetadata={handleLoadedMetadata}
          onEnded={handleVideoEnded}
          width="640"
          height="360"
          className="main-video"
        >
          <source src={content?.filePath} />
          브라우저가 비디오 태그를 지원하지 않습니다.
        </video>
        
        <div className="attendance-status">
          <div className="progress-info">
            현재 진도율: {lastSavedProgress.toFixed(2)}%
          </div>
          <label className="attendance-label">
            <input type="checkbox" checked={isChecked} readOnly />
            출석 완료 {isChecked ? '✓' : ''}
          </label>
          {!isChecked && (
            <button 
              className="attendance-button" 
              onClick={handleCourseAttendance}
            >
              수동 출석 체크
            </button>
          )}
        </div>
      </div>
    );
};

export default VideoDetailPage;
