import React, { useState, useEffect } from 'react';
import { io } from 'socket.io-client';
import axios from 'axios';
import { v4 as uuidv4 } from 'uuid';
import './MessagePage.css';

const socket = io('http://localhost:5000');

const MessagePage = () => {
  const [member, setMember] = useState(null);
  const [messages, setMessages] = useState([]);
  const [inputText, setInputText] = useState('');
  const [recipient, setRecipient] = useState(null);
  const [members, setMembers] = useState([]);

  // Î°úÍ∑∏Ïù∏Ìïú ÏÇ¨Ïö©Ïûê Î∞è Ï†ÑÏ≤¥ Î©§Î≤Ñ Ï°∞Ìöå
  useEffect(() => {
    async function fetchMemberData() {
      try {
        const token = localStorage.getItem("token");
        const resMe = await axios.get('http://localhost:8090/api/users/me', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setMember(resMe.data);

        const resMembers = await axios.get('http://localhost:8090/api/chat-members', {
          headers: { Authorization: `Bearer ${token}` }
        });
        const otherMembers = resMembers.data.filter(m => m.studentId !== resMe.data.studentId);
        setMembers(otherMembers);
      } catch (error) {
        console.error("üö® Î©§Î≤Ñ Ï†ïÎ≥¥Î•º Î∂àÎü¨Ïò§Îäî Îç∞ Ïã§Ìå®ÌñàÏäµÎãàÎã§.", error);
      }
    }
    fetchMemberData();
  }, []);

  // Ï†ÑÏ≤¥ Î©îÏãúÏßÄ Ï°∞Ìöå
  useEffect(() => {
    async function fetchMessages() {
      if (!member) return;
      try {
        const token = localStorage.getItem("token");
        const response = await axios.get(`http://localhost:8090/api/chat/messages/${member.studentId}`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        console.log("ÏÑúÎ≤ÑÏóêÏÑú Î∞õÏùÄ Î©îÏãúÏßÄ:", response.data);
        setMessages(response.data);
      } catch (error) {
        console.error("üö® Ï±ÑÌåÖ ÎÇ¥Ïó≠ÏùÑ Î∂àÎü¨Ïò§ÏßÄ Î™ªÌñàÏäµÎãàÎã§.", error);
      }
    }
    fetchMessages();
  }, [member]);
  
  // Ïã§ÏãúÍ∞Ñ Î©îÏãúÏßÄ ÏàòÏã†
  useEffect(() => {
    const handleMessageReceive = (msg) => {
      setMessages(prev => {
        if (prev.some(m => m.id === msg.id)) {
          console.warn("‚ö†Ô∏è Ï§ëÎ≥µ Î©îÏãúÏßÄ Í∞êÏßÄ - Ï∂îÍ∞Ä Ïïà Ìï®", msg);
          return prev;
        }
        return [...prev, msg];
      });
    };

    socket.on('chat message', handleMessageReceive);
    return () => socket.off('chat message', handleMessageReceive);
  }, []);

  // Ïã§ÏãúÍ∞Ñ ÏùΩÏùå Ïù¥Î≤§Ìä∏ ÏàòÏã† (ÏÜ°Ïã†Ïûê Ï∏°)
  useEffect(() => {
    const handleReadMessage = ({ messageId, readerId }) => {
      console.log("ÏùΩÏùå Ïù¥Î≤§Ìä∏ ÏàòÏã†:", messageId, readerId);
      setMessages(prev =>
        prev.map(m => String(m.id) === String(messageId) ? { ...m, read: true } : m)
      );
    };

    socket.on('read message', handleReadMessage);
    return () => socket.off('read message', handleReadMessage);
  }, []);

  // Î©îÏãúÏßÄ Ï†ÑÏÜ° (ÏóîÌÑ∞ÌÇ§ Ìè¨Ìï®)
  const handleSend = async () => {
    const trimmedText = inputText.trim();
    if (!trimmedText || !recipient || !member) return;
    const newMsg = {
      sender: member.studentId,
      recipient: recipient.studentId,
      content: trimmedText
    };

    try {
      const token = localStorage.getItem("token");
      const response = await axios.post("http://localhost:8090/api/chat/send", newMsg, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMessages(prev => [...prev, { ...response.data, id: response.data.id || uuidv4() }]);
      socket.emit('chat message', response.data);
      setInputText('');
    } catch (error) {
      console.error("üö® Î©îÏãúÏßÄ Ï†ÑÏÜ° Ïã§Ìå®", error);
    }
  };

  // ÏÑ†ÌÉùÎêú ÎåÄÌôî ÏÉÅÎåÄÏôÄ Í¥ÄÎ†®Îêú Î©îÏãúÏßÄÎßå ÌïÑÌÑ∞ÎßÅ
  const conversationMessages = messages.filter(msg => {
    if (!member || !recipient) return false;
    return (
      (msg.sender === member.studentId && msg.recipient === recipient.studentId) ||
      (msg.sender === recipient.studentId && msg.recipient === member.studentId)
    );
  });

  // ÏàòÏã†Ïûê ÏûÖÏû•ÏóêÏÑú, Î©îÏãúÏßÄÎ•º ÏùΩÏóàÏùÑ Îïå Ï≤òÎ¶¨ÌïòÎäî Ìï®Ïàò
  const markMessageAsRead = (msg) => {
    if (!member || !recipient) return;
    const token = localStorage.getItem("token");
    axios.post(`http://localhost:8090/api/chat/messages/read/${msg.id}`, null, {
      headers: { Authorization: `Bearer ${token}` }
    })
    .then(() => {
      setMessages(prev =>
        prev.map(m => m.id === msg.id ? { ...m, read: true } : m)
      );
      socket.emit('read message', { messageId: msg.id, readerId: member.studentId });
    })
    .catch(error => console.error("ÏùΩÏùå Ï≤òÎ¶¨ Ïã§Ìå®", error));
  };

  // ÏàòÏã†Ïûê ÏûÖÏû•ÏóêÏÑú, ÎåÄÌôîÏ∞Ω Ïó¥Î¶¥ Îïå ÏûêÎèôÏúºÎ°ú ÏùΩÏùå Ï≤òÎ¶¨
  useEffect(() => {
    if (!member || !recipient) return;
    conversationMessages.forEach(msg => {
      if (msg.sender !== member.studentId && !msg.read) {
        markMessageAsRead(msg);
      }
    });
  }, [member, recipient, conversationMessages]);

  return (
    <div className="message-page-container">
      <div className="chat-container">
        {/* Ï¢åÏ∏°: Ïó∞ÎùΩÏ≤ò Î™©Î°ù */}
        <div className="contact-list">
          <h3>Ï™ΩÏßÄÌï®</h3>
          {members.map(m => {
            const unreadCount = messages.filter(msg => 
              msg.sender === m.studentId && !msg.read
            ).length;
            return (
              <div 
                key={m.studentId}
                className={`contact-item ${recipient && recipient.studentId === m.studentId ? 'selected' : ''}`}
                onClick={() => setRecipient(m)}
              >
                {m.name}
                {unreadCount > 0 && <span className="unread-badge">{unreadCount}</span>}
              </div>
            );
          })}
        </div>
        {/* Ïö∞Ï∏°: Ï±ÑÌåÖ ÏòÅÏó≠ */}
        <div className="chat-area">
          <div className="chat-header">
            {member && recipient ? `${member.name}Í≥º ${recipient.name}Ïùò ÎåÄÌôî` : "ÎåÄÌôîÎ•º ÏÑ†ÌÉùÌïòÏÑ∏Ïöî"}
          </div>
          <div className="chat-messages">
            {conversationMessages.map(msg => (
              <div key={msg.id || uuidv4()} className={`chat-message ${msg.sender === member?.studentId ? 'sent' : 'received'}`}>
                {msg.sender === member?.studentId && (
                  <div className="read-status">
                    {msg.read ? 'ÏùΩÏùå' : 'ÏïàÏùΩÏùå'}
                  </div>
                )}
                <div className="message-content">
                  <span>{msg.content}</span>
                </div>
                <div className="timestamp">
                  {msg.timestamp ? new Date(msg.timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }) : ''}
                </div>
              </div>
            ))}
          </div>
          <div className="chat-input">
            <input
              type="text"
              value={inputText}
              onChange={e => setInputText(e.target.value)}
              onKeyDown={e => {
                if (e.key === 'Enter') {
                  e.preventDefault();
                  handleSend();
                }
              }}
              placeholder="Î©îÏãúÏßÄÎ•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî..."
            />
            <button onClick={handleSend}>Ï†ÑÏÜ°</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default MessagePage;
