import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './SurveyPage.css';
import { useParams } from 'react-router-dom';

function SurveyForm({ onBack, onComplete }) {
    const { surveyId } = useParams(); // URL에서 surveyId 가져오기
    const [survey, setSurvey] = useState(null);
    const [responses, setResponses] = useState({});
    const [submitted, setSubmitted] = useState(false);
    const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState("");
    const token = localStorage.getItem("token"); // ✅ JWT 토큰 가져오기

    // ✅ API에서 설문 데이터 불러오기
    useEffect(() => {
        if (!token) {
            console.warn("⚠️ 토큰이 없습니다. 로그인이 필요합니다.");
            setError("로그인이 필요합니다.");
            setLoading(false);
            return;
        }

        const fetchSurvey = async () => {
            try {
                const response = await axios.get(`/api/surveys/general/${surveyId}`, {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setSurvey(response.data);
                setLoading(false);
            } catch (error) {
                console.error("🚨 설문 데이터 불러오기 오류:", error);
                setError("설문 데이터를 불러오는 중 오류가 발생했습니다.");
                setLoading(false);
            }
        };

        fetchSurvey();
    }, [surveyId, token]);

    // ✅ 기존 설문 응답 불러오기 (사용자가 이미 응답한 경우)
    useEffect(() => {
        if (!token) return;

        const fetchUserResponses = async () => {
            try {
                const response = await axios.get(`/api/surveys/general/${surveyId}/responses`, {
                    headers: { Authorization: `Bearer ${token}` }
                });

                if (response.data) {
                    setResponses(response.data);
                    setSubmitted(true);
                }
            } catch (error) {
                console.warn("⚠️ 기존 응답 데이터를 불러오지 못했습니다.", error);
            }
        };

        fetchUserResponses();
    }, [surveyId, token]);

    const handleResponseChange = (questionId, value) => {
        setResponses(prev => ({ ...prev, [questionId]: value }));
    };

    const isAllAnswered = survey?.questions.every(q => {
        const response = responses[q.general_question_id];
        return response !== undefined && response !== null && response !== '' &&
               (!(q.type === "checkbox") || (Array.isArray(response) && response.length > 0));
    });

    const handleSubmit = async () => {
        if (!isAllAnswered) {
            alert("❌ 모든 질문에 답변해야 합니다!");
            return;
        }

        try {
            await axios.post(`/api/surveys/general/submit`, {
                surveyId: surveyId,
                responses: responses
            }, {
                headers: { Authorization: `Bearer ${token}` }
            });

            setSubmitted(true);
            alert("✅ 설문이 성공적으로 제출되었습니다!");
            if (onComplete) {
                onComplete();
            }
        } catch (error) {
            console.error("🚨 설문 제출 오류:", error);
            alert("설문 제출 중 오류가 발생했습니다.");
        }
    };

    if (loading) return <p>📡 설문 데이터를 불러오는 중...</p>;
    if (error) return <p className="error-message">{error}</p>;
    if (!survey) return <p>설문을 찾을 수 없습니다.</p>;

    const currentQuestion = survey.questions[currentQuestionIndex];
    const totalQuestions = survey.questions.length;

    return (
        <div className="survey-form">
            <h3>{survey.title}</h3>

            {submitted ? (
                <>
                    <p className="submit-result">🎉 설문이 제출되었습니다! 감사합니다.</p>
                    <button className="back-btn" onClick={onBack}>목록으로 돌아가기</button>
                </>
            ) : (
                <>
                    <div className="survey-item">
                        <p>{currentQuestion.text}</p>
                        {currentQuestion.type === "radio" ? (
                            <div>
                                {currentQuestion.options.map(option => (
                                    <label key={option} className="survey-option">
                                        <input
                                            type="radio"
                                            name={`question-${surveyId}-${currentQuestion.general_question_id}`}
                                            value={option}
                                            checked={responses[currentQuestion.general_question_id] === option}
                                            onChange={() => handleResponseChange(currentQuestion.general_question_id, option)}
                                        />
                                        {option}
                                    </label>
                                ))}
                            </div>
                        ) : currentQuestion.type === "text" ? (
                            <textarea
                                className="survey-option"
                                rows="3"
                                placeholder="의견을 입력하세요..."
                                value={responses[currentQuestion.general_question_id] || ''}
                                onChange={(e) => handleResponseChange(currentQuestion.general_question_id, e.target.value)}
                            ></textarea>
                        ) : null}
                    </div>

                    <div className="navigation-buttons">
                        <button 
                            className="nav-btn" 
                            onClick={() => setCurrentQuestionIndex(prev => prev - 1)} 
                            disabled={currentQuestionIndex === 0}
                        >
                            이전 질문
                        </button>

                        {currentQuestionIndex < totalQuestions - 1 ? (
                            <button 
                                className="nav-btn" 
                                onClick={() => setCurrentQuestionIndex(prev => prev + 1)}
                            >
                                다음 질문
                            </button>
                        ) : (
                            <button className="submit-btn" onClick={handleSubmit}>
                                제출
                            </button>
                        )}
                    </div>
                </>
            )}

            <button className="back-btn" onClick={onBack}>뒤로 가기</button>
        </div>
    );
}

export default SurveyForm;
