import React, { useEffect, useState } from "react";
import axios from "axios";
import "./StudentManagePage.css"; // ‚úÖ CSS ÌååÏùº Î∂àÎü¨Ïò§Í∏∞

const StudentManagePage = () => {
    const [students, setStudents] = useState([]);
    const [openCourses, setOpenCourses] = useState({}); // ‚úÖ Ïñ¥Îñ§ Í∞ïÏ¢åÍ∞Ä Ïó¥Î†§ ÏûàÎäîÏßÄ ÏÉÅÌÉú Í¥ÄÎ¶¨
    const [memberId, setMemberId] = useState(null); // ‚úÖ Î°úÍ∑∏Ïù∏Ìïú ÏÇ¨Ïö©ÏûêÏùò memberId

    useEffect(() => {
        const token = localStorage.getItem("token");
        if (!token) {
            console.error("üö® JWT ÌÜ†ÌÅ∞ ÏóÜÏùå. Î°úÍ∑∏Ïù∏ ÌïÑÏöî");
            return;
        }

        // ‚úÖ Î°úÍ∑∏Ïù∏Ìïú ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ Í∞ÄÏ†∏Ïò§Í∏∞
        axios.get("http://localhost:8090/api/users/me", {
            headers: { Authorization: `Bearer ${token}` }
        })
        .then(response => {
            setMemberId(response.data.id);
        })
        .catch(error => console.error("Error fetching user info:", error));
    }, []);

    useEffect(() => {
        if (memberId) {
            axios.get("http://localhost:8090/api/student-manage/approved", {
                headers: { Authorization: `Bearer ${localStorage.getItem("token")}` }
            })
            .then(response => {
                // ‚úÖ Î°úÍ∑∏Ïù∏Ìïú ÏÇ¨Ïö©ÏûêÍ∞Ä Îã¥ÎãπÌïòÎäî Í∞ïÏùòÎßå ÌïÑÌÑ∞ÎßÅ
                const filteredStudents = response.data.filter(student => student.studentId === memberId);
                setStudents(filteredStudents);
            })
            .catch(error => console.error("Error fetching students:", error));
        }
    }, [memberId]);

    // ‚úÖ Í∞ïÏ¢å ÌÅ¥Î¶≠ Ïãú Ïó¥Î¶º/Îã´Ìûò ÌÜ†Í∏Ä
    const toggleCourse = (courseName) => {
        setOpenCourses(prevState => ({
            ...prevState,
            [courseName]: !prevState[courseName]
        }));
    };

    // ‚úÖ Í∞ïÏ¢åÎ≥Ñ Í∑∏Î£πÌôî (Í∞ïÏ¢åÎ™Ö Í∏∞Ï§ÄÏúºÎ°ú ÌïôÏÉù Î∂ÑÎ•ò)
    const groupedStudents = students.reduce((acc, student) => {
        if (!acc[student.courseName]) acc[student.courseName] = [];
        acc[student.courseName].push(student);
        return acc;
    }, {});

    return (
        <div className="student-manage-container">
            <h2>ÏàòÍ∞ïÏÉù Í¥ÄÎ¶¨</h2>
            <div className="course-list">
                {Object.keys(groupedStudents).map((courseName, index) => (
                    <div key={index} className="course-item">
                        {/* ‚úÖ Í∞ïÏ¢åÎ™Ö ÌÅ¥Î¶≠ Ïãú ÌïôÏÉù Î¶¨Ïä§Ìä∏ Ïó¥Î¶º/Îã´Ìûò */}
                        <div 
                            className="course-header" 
                            onClick={() => toggleCourse(courseName)}
                        >
                            {courseName}
                            <span className={`arrow ${openCourses[courseName] ? "open" : ""}`}>&#9662;</span>
                        </div>
                        {openCourses[courseName] && (
                            <div className="student-list">
                                <table>
                                    <thead>
                                        <tr>
                                            <th>ÌïôÎ≤à</th>
                                            <th>ÌïôÏÉù Ïù¥Î¶Ñ</th>
                                            <th>Ï∂úÏÑùÎ•†</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {groupedStudents[courseName].map((student, idx) => (
                                            <tr key={idx}>
                                                <td>{student.studentId}</td>
                                                <td>{student.studentName}</td>
                                                <td>{student.attendanceRate}%</td>
                                            </tr>
                                        ))}
                                    </tbody>
                                </table>
                            </div>
                        )}
                    </div>
                ))}
            </div>
        </div>
    );
};

export default StudentManagePage;
