import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import "./StudentAttendance.css";

const StudentAttendance = () => {
    const { courseId, studentId } = useParams(); // ‚úÖ URLÏóêÏÑú courseId, studentId Ï∂îÏ∂ú
    const token = localStorage.getItem("token");
    
    const [attendanceData, setAttendanceData] = useState([]);
    
    useEffect(() => {
        console.log("‚úÖ StudentAttendanceÏóêÏÑú Î∞õÏùÄ Í∞í:", { courseId, studentId });
        
        if (!courseId || !studentId) {
            console.error("‚ùå Ïú†Ìö®ÌïòÏßÄ ÏïäÏùÄ courseId ÎòêÎäî studentId:", courseId, studentId);
            return;
        }
        
        axios.get(`http://localhost:8090/api/student-attendance/${courseId}/${studentId}`, {
            headers: { Authorization: `Bearer ${token}` },
        })
        .then(response => {
            console.log("üì¢ Ï∂úÏÑùÎ∂Ä Îç∞Ïù¥ÌÑ∞ (ÏàòÏ†ï ÌõÑ):", response.data);
            setAttendanceData(response.data);
        })
        .catch(error => console.error("‚ùå Ï∂úÏÑùÎ∂Ä Îç∞Ïù¥ÌÑ∞ Î°úÎî© Ïã§Ìå®:", error));
    }, [courseId, studentId]);
    
    return (
        <div className="attendance-container">
            <h2>Ï∂úÏÑùÎ∂Ä</h2>
            <table>
                <thead>
                    <tr>
                        <th>Ï£ºÏ∞®</th>
                        <th>Í∞ïÏùòÎ™Ö</th>
                        <th>Ï∂úÏÑù</th>
                    </tr>
                </thead>
                <tbody>
                    {attendanceData.map((lecture, index) => (
                        <tr key={index}>
                            <td>{lecture.weekId}Ï£ºÏ∞®</td>
                            <td>{lecture.fileName}</td>
                            <td>{lecture.present ? "‚úÖ" : "‚ùå"}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default StudentAttendance;
