import React, { useState, useEffect } from 'react';
import axios from 'axios';
import TeamProjectSubmit from './TeamProjectSubmit';
import TeamProjectHistory from './TeamProjectHistory';

import './TeamProject.css';

const TeamProject = () => {
  const [activeTab, setActiveTab] = useState('list');
  const [studentInfo, setStudentInfo] = useState(null);
  const [teamProjects, setTeamProjects] = useState([]);
  const [teamMembers, setTeamMembers] = useState([]);
  const [loading, setLoading] = useState(true);
  const token = localStorage.getItem("token");

  useEffect(() => {
    if (!token) {
      console.warn("‚ö†Ô∏è ÌÜ†ÌÅ∞Ïù¥ ÏóÜÏäµÎãàÎã§. Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
      return;
    }

    const fetchStudentData = async () => {
      try {
        const userResponse = await axios.get("http://localhost:8090/api/student/me", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setStudentInfo(userResponse.data);

        const projectResponse = await axios.get("http://localhost:8090/api/team-projects", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setTeamProjects(projectResponse.data);

        const teamResponse = await axios.get("http://localhost:8090/api/team-members", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setTeamMembers(teamResponse.data);
      } catch (error) {
        console.error("‚ö†Ô∏è Îç∞Ïù¥ÌÑ∞ Î°úÎî© Ïã§Ìå®:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchStudentData();
  }, [token]);

  if (loading) {
    return <div className="loading-screen">üì° Îç∞Ïù¥ÌÑ∞ Î∂àÎü¨Ïò§Îäî Ï§ë...</div>;
  }

  return (
    <div className="team-project-container">
      <main className="team-project-content">
        {/* ‚úÖ ÌÉ≠ ÎÑ§ÎπÑÍ≤åÏù¥ÏÖò */}
        <div className="team-project-tabs">
          <button 
            className={`tab-btn ${activeTab === 'list' ? 'active' : ''}`} 
            onClick={() => setActiveTab('list')}
          >
            ÌîÑÎ°úÏ†ùÌä∏ Î™©Î°ù
          </button>
          <button 
            className={`tab-btn ${activeTab === 'submit' ? 'active' : ''}`} 
            onClick={() => setActiveTab('submit')}
          >
            Ï†úÏ∂ú
          </button>
          <button 
            className={`tab-btn ${activeTab === 'history' ? 'active' : ''}`} 
            onClick={() => setActiveTab('history')}
          >
            Ï†úÏ∂ú ÎÇ¥Ïó≠
          </button>
        </div>

        {/* ‚úÖ ÌîÑÎ°úÏ†ùÌä∏ Î™©Î°ù ÌÉ≠ */}
        {activeTab === 'list' && (
          <div className="team-project-list">
            <h2>{studentInfo?.name}ÎãòÏùò ÌåÄ ÌîÑÎ°úÏ†ùÌä∏</h2>

            {teamProjects.length > 0 ? (
              teamProjects.map((project) => (
                <div key={project.team_id} className="team-project-card">
                  <h3>{project.project_name}</h3>
                  <p>ÎßàÍ∞êÏùº: <strong>{new Date(project.deadline).toLocaleDateString()}</strong></p>
                  <p>Ï†úÏ∂ú ÏÉÅÌÉú: 
                    <span className={project.status === 'Ï†úÏ∂ú ÏôÑÎ£å' ? 'status-submitted' : 'status-not-submitted'}>
                      {project.status || 'ÎØ∏Ï†úÏ∂ú'}
                    </span>
                  </p>
                </div>
              ))
            ) : (
              <div className="empty-message">üöÄ Îì±Î°ùÎêú ÌåÄ ÌîÑÎ°úÏ†ùÌä∏Í∞Ä ÏóÜÏäµÎãàÎã§.</div>
            )}

            {/* ‚úÖ ÌåÄÏõê Î™©Î°ù */}
            <h3>ÎÇ¥ ÌåÄÏõê</h3>
            <ul className="team-project-members">
              {teamMembers.length > 0 ? (
                teamMembers.map((member) => (
                  <li key={member.student_id}>üë§ {member.name}</li>
                ))
              ) : (
                <li className="empty-message">‚ö†Ô∏è ÌåÄÏõêÏù¥ ÏóÜÏäµÎãàÎã§.</li>
              )}
            </ul>
          </div>
        )}

        {/* ‚úÖ Ï†úÏ∂ú Î∞è Ï†úÏ∂ú ÎÇ¥Ïó≠ ÌÉ≠ */}
        {activeTab === 'submit' && <TeamProjectSubmit />}
        {activeTab === 'history' && <TeamProjectHistory />}
      </main>
    </div>
  );
};

export default TeamProject;
