import React, { useState, useEffect, useMemo, useCallback } from 'react';
import axios from 'axios';
import './TeamProjectSubmit.css';

function TeamProjectSubmit() {
  const [files, setFiles] = useState([]);
  const [deadline, setDeadline] = useState(null);
  const [teamName, setTeamName] = useState("Ïïå Ïàò ÏóÜÎäî ÌåÄ");
  const [deadlinePassed, setDeadlinePassed] = useState(false);
  const [feedbackMessage, setFeedbackMessage] = useState("");
  const token = localStorage.getItem("token"); // ‚úÖ JWT ÌÜ†ÌÅ∞ Í∞ÄÏ†∏Ïò§Í∏∞

  // ‚úÖ APIÏóêÏÑú ÌåÄ Ï†ïÎ≥¥ Í∞ÄÏ†∏Ïò§Í∏∞
  useEffect(() => {
    if (!token) {
      console.warn("‚ö†Ô∏è ÌÜ†ÌÅ∞Ïù¥ ÏóÜÏäµÎãàÎã§. Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
      setFeedbackMessage("Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
      return;
    }

    const fetchTeamInfo = async () => {
      try {
        const response = await axios.get('/api/student/team', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setTeamName(response.data.project_name || "Ïïå Ïàò ÏóÜÎäî ÌåÄ");
        setDeadline(new Date(response.data.deadline));
      } catch (error) {
        console.error("‚ö†Ô∏è ÌåÄ Ï†ïÎ≥¥ Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:", error);
        setFeedbackMessage("ÌåÄ Ï†ïÎ≥¥Î•º Î∂àÎü¨Ïò§Îäî Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
      }
    };

    fetchTeamInfo();
  }, [token]);

  useEffect(() => {
    if (deadline && new Date() > deadline) {
      setDeadlinePassed(true);
    }
  }, [deadline]);

  const handleFileChange = useCallback((e) => {
    const selectedFiles = Array.from(e.target.files);
    setFiles((prev) => [...prev, ...selectedFiles]);
  }, []);

  const handleRemoveFile = useCallback((index) => {
    setFiles((prev) => prev.filter((_, i) => i !== index));
  }, []);

  const handleSubmit = useCallback(async () => {
    if (files.length === 0) {
      setFeedbackMessage("ÌååÏùºÏùÑ ÏóÖÎ°úÎìúÌïòÏÑ∏Ïöî!");
      return;
    }
    if (deadlinePassed) {
      setFeedbackMessage("Ï†úÏ∂ú Í∏∞ÌïúÏù¥ ÏßÄÎÇ¨ÏäµÎãàÎã§.");
      return;
    }

    const formData = new FormData();
    files.forEach(file => formData.append("files", file));

    try {
      await axios.post("/api/team-projects/submit", formData, {
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "multipart/form-data"
        }
      });

      setFeedbackMessage("ÌîÑÎ°úÏ†ùÌä∏Í∞Ä ÏÑ±Í≥µÏ†ÅÏúºÎ°ú Ï†úÏ∂úÎêòÏóàÏäµÎãàÎã§!");
      setFiles([]);
    } catch (error) {
      console.error("üö® Ï†úÏ∂ú Ïã§Ìå®:", error);
      setFeedbackMessage("Ï†úÏ∂ú Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
    }

    setTimeout(() => setFeedbackMessage(""), 3000);
  }, [files, deadlinePassed, token]);

  return (
    <div className="team-project-submit">
      <h2>ÌåÄ ÌîÑÎ°úÏ†ùÌä∏ Ï†úÏ∂ú</h2>
      {feedbackMessage && (
        <div className="feedback-message" role="alert">
          {feedbackMessage}
        </div>
      )}
      {deadlinePassed ? (
        <p className="deadline-message">Ï†úÏ∂ú Í∏∞ÌïúÏù¥ ÏßÄÎÇ¨ÏäµÎãàÎã§.</p>
      ) : (
        <>
          <label htmlFor="file-upload" className="visually-hidden">
            ÌååÏùº ÏóÖÎ°úÎìú
          </label>
          <input
            id="file-upload"
            type="file"
            onChange={handleFileChange}
            accept=".zip,.pdf,.docx,.pptx"
            multiple
            disabled={deadlinePassed}
          />
          <ul>
            {files.map((file, index) => (
              <li key={index}>
                {file.name}{' '}
                <button 
                  onClick={() => handleRemoveFile(index)} 
                  aria-label={`Remove ${file.name}`}
                >
                  ÏÇ≠Ï†ú
                </button>
              </li>
            ))}
          </ul>
          <button onClick={handleSubmit} disabled={deadlinePassed}>
            Ï†úÏ∂úÌïòÍ∏∞
          </button>
        </>
      )}
    </div>
  );
}

export default TeamProjectSubmit;
