  const { state } = useLocation();
  const { courseId, courseName } = state || {};

  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [deadline, setDeadline] = useState("");
  const [message, setMessage] = useState("");
  const token = localStorage.getItem("token");

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post(
        "http://localhost:8090/api/teams",
        {
          name,
          description,
          deadline,
          courseId, // Í∞ïÏùò Ï†ïÎ≥¥ÎèÑ Ìï®Íªò Ï†ÑÏÜ°
        },
        {
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
        }
      );

      setMessage("‚úÖ ÌåÄÏù¥ ÏÑ±Í≥µÏ†ÅÏúºÎ°ú ÏÉùÏÑ±ÎêòÏóàÏäµÎãàÎã§!");
      console.log("üìå ÌåÄ ÏÉùÏÑ± ÏôÑÎ£å:", response.data);
    } catch (error) {
      setMessage("üö® ÌåÄ ÏÉùÏÑ± Ïã§Ìå®!");
      console.error("üö® Ïò§Î•ò Î∞úÏÉù:", error);
    }
  };

  return (
    <div>
      <h2>ÌåÄ ÏÉùÏÑ± - Í∞ïÏùò: {courseName || "Í∞ïÏùò Ï†ïÎ≥¥ ÏóÜÏùå"}</h2>
      {message && <p>{message}</p>}
      <form onSubmit={handleSubmit}>
        <label>
          ÌåÄ Ïù¥Î¶Ñ:
          <input
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </label>
        <br />
        <label>
          ÌåÄ ÏÑ§Î™Ö:
          <input
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
          />
        </label>
        <br />
        <label>
          ÎßàÍ∞êÏùº:
          <input
            type="datetime-local"
            value={deadline}
            onChange={(e) => setDeadline(e.target.value)}
            required
          />
        </label>
        <br />
        <button type="submit">ÌåÄ ÏÉùÏÑ±</button>
      </form>
    </div>
  );
}

export default CreateTeamForm;
