import React, { useEffect, useState } from "react";
import axios from "axios";

const StudentAttendance = ({ studentId }) => {
  const [attendanceData, setAttendanceData] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchAttendance = async () => {
      const token = sessionStorage.getItem("token"); // 토큰 가져오기
      if (!token) {
        console.error("토큰이 없습니다. 인증이 필요합니다.");
        setLoading(false);
        return;
      }

      try {
        const response = await axios.get("http://localhost:8090/api/attendance/student", {
          params: { studentId },
          headers: {
            Authorization: `Bearer ${token}`, // JWT 토큰 추가
          },
        });

        setAttendanceData(response.data);
      } catch (error) {
        console.error("출석 데이터를 불러오는 중 오류 발생:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchAttendance();
  }, [studentId]);

  if (loading) return <p>로딩 중...</p>;
  if (!attendanceData) return <p>출석 데이터가 없습니다.</p>;

  return (
    <div>
      <h2>출석부</h2>
      <table border="1">
        <thead>
          <tr>
            <th>강의 ID</th>
            <th>출석 여부</th>
            <th>진도율</th>
          </tr>
        </thead>
        <tbody>
          {Object.keys(attendanceData.attendance).map((courseId) => (
            <tr key={courseId}>
              <td>{courseId}</td>
              <td>{attendanceData.attendance[courseId] ? "출석 완료" : "미출석"}</td>
              <td>{attendanceData.progress[courseId]?.toFixed(2) || "0"}%</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default StudentAttendance;
