// LectureSurvey.jsx
import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import './LectureSurvey.css';
import { useNavigate } from 'react-router-dom';

function LectureSurvey({ onComplete }) {
  const navigate = useNavigate();
  const [survey, setSurvey] = useState([]);
  const [responses, setResponses] = useState({});
  const [submitted, setSubmitted] = useState(false);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [loading, setLoading] = useState(false);
  const [, setError] = useState("");
  const [enrolledCourses, setEnrolledCourses] = useState([]);
  const [memberId, setMemberId] = useState(null);
  const [selectedCourseId, setSelectedCourseId] = useState(null);
  const [surveyStarted, setSurveyStarted] = useState(false);
  const token = localStorage.getItem("token");

  useEffect(() => {
    if (!token) {
      setError("Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
      return;
    }

    const fetchUserInfo = async () => {
      try {
        const response = await axios.get('http://localhost:8090/api/users/me', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setMemberId(response.data.id);
      } catch (error) {
        console.error("ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ Í∞ÄÏ†∏Ïò§Í∏∞ Ïã§Ìå®:", error);
        alert("ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥Î•º Î∂àÎü¨Ïò¨ Ïàò ÏóÜÏäµÎãàÎã§. Îã§Ïãú Î°úÍ∑∏Ïù∏Ìï¥Ï£ºÏÑ∏Ïöî.");
        navigate('/login');
      }
    };

    fetchUserInfo();
  }, [token, navigate]);

  useEffect(() => {
    if (!memberId) return;

    const fetchEnrollments = async () => {
      try {
        const response = await axios.get(`http://localhost:8090/api/enrollments/member/${memberId}`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        setEnrolledCourses(response.data);
      } catch (error) {
        console.error("ÏàòÍ∞ï Ïã†Ï≤≠Ìïú Í∞ïÏùò Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:", error);
        setError("ÏàòÍ∞ï Ïã†Ï≤≠Ìïú Í∞ïÏùòÎ•º Î∂àÎü¨Ïò§Îäî Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
      }
    };

    fetchEnrollments();
  }, [memberId, token]);

  const startSurvey = async (courseId) => {
    setSelectedCourseId(courseId);
    setSurveyStarted(true);
    setLoading(true);
    setError("");
    setResponses({});

    try {
      const response = await axios.get(`http://localhost:8090/api/surveys/lecture/${courseId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setSurvey(response.data);
      setCurrentQuestionIndex(0);
    } catch (error) {
      console.error("ÏÑ§Î¨∏ Îç∞Ïù¥ÌÑ∞ ÏöîÏ≤≠ Ïò§Î•ò:", error);
      setError("ÏÑ§Î¨∏ Îç∞Ïù¥ÌÑ∞Î•º Î∂àÎü¨Ïò§Îäî Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
    }
    setLoading(false);
  };

  const handleResponseChange = useCallback((questionId, value) => {
    setResponses(prev => {
      const updatedResponses = { ...prev, [questionId]: value };
      console.log(`üìå ÏùëÎãµ Ï†ÄÏû•Îê® - ÏÑ§Î¨∏ ID: ${questionId}, ÏÑ†ÌÉùÍ∞í: ${value}`);
      console.log("üìå ÌòÑÏû¨ responses ÏÉÅÌÉú:", updatedResponses);
      return updatedResponses;
    });
  }, []);

  const handleSubmit = async () => {
    if (!survey.every(q => responses[q.id] !== undefined)) {
      alert("Î™®Îì† ÏßàÎ¨∏Ïóê ÎãµÎ≥ÄÌï¥Ïïº Ìï©ÎãàÎã§!");
      return;
    }

    // Ï§ëÎ≥µ Ï≤¥ÌÅ¨
    for (let [questionId] of Object.entries(responses)) {
      try {
        await axios.post(
          "http://localhost:8090/api/survey-responses/check",
          { surveyId: questionId, memberId: memberId },
          { headers: { Authorization: `Bearer ${token}` } }
        );
      } catch (error) {
        if (error.response && error.response.status === 400) {
          alert(error.response.data);
          return;
        } else {
          alert("Ï§ëÎ≥µ Ï≤¥ÌÅ¨ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
          return;
        }
      }
    }

    const requestData = {
      courseId: selectedCourseId,
      memberId: memberId,
      responses: Object.entries(responses).map(([questionId, response]) => ({
        surveyId: questionId,
        response: response
      }))
    };

    try {
      await axios.post("http://localhost:8090/api/survey-responses/submit", requestData, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setSubmitted(true);
      alert("ÏÑ§Î¨∏Ïù¥ ÏÑ±Í≥µÏ†ÅÏúºÎ°ú Ï†úÏ∂úÎêòÏóàÏäµÎãàÎã§!");
      if (onComplete) onComplete();
    } catch (error) {
      alert("ÏÑ§Î¨∏ Ï†úÏ∂ú Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
    }
  };

  // onBack ÎåÄÏã† ÎÇ¥Î∂Ä ÏÉÅÌÉú Ï¥àÍ∏∞Ìôî Ìï®Ïàò handleBack ÏÇ¨Ïö©
  const handleBack = () => {
    setSurveyStarted(false);
    setSubmitted(false);
    setResponses({});
    setCurrentQuestionIndex(0);
  };

  return (
    <div className="survey-container">
      <h2>Í∞ïÏùò ÏÑ§Î¨∏</h2>
      {!surveyStarted ? (
        <div className="course-selection">
          {enrolledCourses.map(course => (
            <div key={course.courseId} className="course-item">
              <span>{course.courseName}</span>
              <button onClick={() => startSurvey(course.courseId)}>ÏÑ§Î¨∏ ÏãúÏûë</button>
            </div>
          ))}
        </div>
      ) : (
        <>
          {submitted ? (
            <>
              <p className="submit-result">ÏÑ§Î¨∏Ïù¥ Ï†úÏ∂úÎêòÏóàÏäµÎãàÎã§! Í∞êÏÇ¨Ìï©ÎãàÎã§.</p>
              <button onClick={handleBack}>Î™©Î°ùÏúºÎ°ú ÎèåÏïÑÍ∞ÄÍ∏∞</button>
            </>
          ) : (
            <>
              {loading ? <p>ÏÑ§Î¨∏ Îç∞Ïù¥ÌÑ∞Î•º Î∂àÎü¨Ïò§Îäî Ï§ë...</p> : (
                <div className="survey-item">
                  <p>{survey[currentQuestionIndex]?.title}</p>
                  {survey[currentQuestionIndex]?.options ? (
                    survey[currentQuestionIndex].options.split(',').map(option => (
                      <label key={option} className="survey-option">
                        <input
                          type="radio"
                          name={`question-${survey[currentQuestionIndex].id}`}
                          value={option}
                          checked={responses[survey[currentQuestionIndex].id] === option}
                          onChange={() => handleResponseChange(survey[currentQuestionIndex].id, option)}
                        />
                        {option}
                      </label>
                    ))
                  ) : (
                    <p>ÏÑ†ÌÉùÏßÄÍ∞Ä ÏóÜÏäµÎãàÎã§.</p>
                  )}
                </div>
              )}

              <div className="survey-navigation">
                <button onClick={() => setCurrentQuestionIndex(prev => prev - 1)} disabled={currentQuestionIndex === 0}>
                  Ïù¥Ï†Ñ
                </button>
                <button onClick={() => setCurrentQuestionIndex(prev => prev + 1)} disabled={currentQuestionIndex === survey.length - 1}>
                  Îã§Ïùå
                </button>
              </div>

              {currentQuestionIndex === survey.length - 1 && (
                <button onClick={handleSubmit}>Ï†úÏ∂ú</button>
              )}
            </>
          )}
        </>
      )}
    </div>
  );
}

export default LectureSurvey;
