                                                              import React, { useState, useEffect } from 'react';
import axios from 'axios';
import "./AdminCoursePage.css"; // CSS ÌååÏùº Î∂àÎü¨Ïò§Í∏∞

const AdminCoursePage = () => {
  const [courses, setCourses] = useState([]);

  useEffect(() => {
    const fetchCourses = async () => {
      const token = localStorage.getItem('token');
      if (!token) {
        alert("Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
        return;
      }

      try {
        const response = await axios.get('http://localhost:8090/api/courses', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setCourses(response.data);
      } catch (error) {
        console.error("Error fetching courses", error);
        alert('Í∞ïÏùò Î™©Î°ùÏùÑ Í∞ÄÏ†∏Ïò§Îäî Îç∞ Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
      }
    };
    fetchCourses();
  }, []);

  const handleAcceptCourse = async (courseId) => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert("Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
      return;
    }

    try {
      const response = await axios.put(`http://localhost:8090/api/courses/${courseId}/approve`, null, {
        headers: { Authorization: `Bearer ${token}` }
      });
      if (response.status === 200) {
        alert("Í∞ïÏùò ÏàòÎùΩ ÏÑ±Í≥µ");
        setCourses(prevCourses =>
          prevCourses.map(course =>
            course.id === courseId ? { ...course, status: 'COMPLETED' } : course
          )
        );
      }
    } catch (error) {
      alert("Í∞ïÏùò ÏàòÎùΩ Ï§ë Ïò§Î•ò Î∞úÏÉù");
      console.error("Error accepting course", error);
    }
  };

  const handleRejectCourse = async (courseId) => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert("Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
      return;
    }

    try {
      const response = await axios.put(`http://localhost:8090/api/courses/${courseId}/cancel`, null, {
        headers: { Authorization: `Bearer ${token}` }
      });
      if (response.status === 200) {
        alert("Í∞ïÏùò Í±∞Ï†à ÏÑ±Í≥µ");
        setCourses(prevCourses =>
          prevCourses.map(course =>
            course.id === courseId ? { ...course, status: 'CANCELLED' } : course
          )
        );
      }
    } catch (error) {
      alert("Í∞ïÏùò Í±∞Ï†à Ï§ë Ïò§Î•ò Î∞úÏÉù");
      console.error("Error rejecting course", error);
    }
  };

  const handleDownload = async (fileUrl, fileName) => {
    const token = localStorage.getItem('token');
    if (!token) {
        alert("Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
        return;
    }

    try {
        const encodedFileUrl = encodeURIComponent(fileUrl);
        const response = await axios.get(`http://localhost:8090/api/courses/download?fileUrl=${encodedFileUrl}`, {
            headers: { Authorization: `Bearer ${token}` },
            responseType: 'blob',
        });

        const blob = new Blob([response.data], { type: response.headers['content-type'] });
        const link = document.createElement('a');
        link.href = URL.createObjectURL(blob);
        link.download = fileName || "downloaded_file";
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
    } catch (error) {
        console.error("ÌååÏùº Îã§Ïö¥Î°úÎìú Ï§ë Ïò§Î•ò Î∞úÏÉù:", error);
        alert("ÌååÏùº Îã§Ïö¥Î°úÎìúÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.");
    }
  };

  const getStatusLabel = (status) => {
    switch (status) {
      case "COMPLETED":
        return <span className="status completed">ÏäπÏù∏</span>;
      case "CANCELLED":
        return <span className="status cancelled">Í±∞Ï†à</span>;
      default:
        return <span className="status pending">ÎåÄÍ∏∞Ï§ë</span>;
    }
  };

  return (
    <div className="admin-container">
      <h2>Í∞ïÏùò Í¥ÄÎ¶¨</h2> 
      <div className="table-container"> {/* üî• ÌÖåÏù¥Î∏îÏùÑ Í∞êÏã∏Îäî div Ï∂îÍ∞Ä */}
        <table className="admin-table">
          <thead>
            <tr>
              <th>Ïã†Ï≤≠Ïûê(ÍµêÏàò)</th>
              <th>Í∞ïÏùòÎ™Ö</th>
              <th>Ïù¥ÏàòÍµ¨Î∂Ñ</th>
              <th>Í∞ïÏùò Í≥ÑÌöçÏÑú</th>
              <th>ÏÉÅÌÉú</th>
              <th>ÏàòÎùΩ</th>
              <th>Í±∞Ï†à</th>
            </tr>
          </thead>
          <tbody>
            {courses.filter(course => course.professor!== "Îã¥Îãπ ÍµêÏàòÍ∞Ä ÏßÄÏ†ïÎêòÏßÄ ÏïäÏïòÏäµÎãàÎã§.")
            .map(course => (
              <tr key={course.id}>
                <td>{course.professor || "ÎØ∏ÏßÄÏ†ï"}</td>
                <td>{course.name}</td>
                <td>{course.courseType || "ÎØ∏ÏßÄÏ†ï"}</td>
                <td>
                  {course.fileUrl ? (
                    <button className="download-btn" onClick={() => handleDownload(course.fileUrl, course.fileName)}>ÌååÏùº</button>
                  ) : (
                    "ÏóÜÏùå"
                  )}
                </td>
                <td>{getStatusLabel(course.status)}</td>
                <td>
                  <button className="approve-btn" onClick={() => handleAcceptCourse(course.id)}>Í∞ïÏùò ÏàòÎùΩ</button>
                </td>
                <td>
                  <button className="reject-btn" onClick={() => handleRejectCourse(course.id)}>Í∞ïÏùò Í±∞Ï†à</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div> {/* üî• Î∞ïÏä§ Îã´Í∏∞ */}
    </div>
  );
};

export default AdminCoursePage;
