import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import "./ExamPage.css";

const ExamPage = () => {
  const { examId } = useParams();
  const [exam, setExam] = useState(null);
  const [answers, setAnswers] = useState({});
  const [loading, setLoading] = useState(true);
  const [selectedQuestionId, setSelectedQuestionId] = useState(null);
  const [submitted, setSubmitted] = useState(false); // Ï†úÏ∂ú ÏÉÅÌÉú Ï∂îÍ∞Ä
  const [score, setScore] = useState(null); // Ï†êÏàò ÏÉÅÌÉú Ï∂îÍ∞Ä

  useEffect(() => {
    const fetchExam = async () => {
      try {
        const token = localStorage.getItem("token");
        const studentId = localStorage.getItem("studentId");
        if (!token || !studentId) {
          alert("Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
          return;
        }

        // Ï†úÏ∂ú ÏÉÅÌÉú ÌôïÏù∏
        const submissionStatusResponse = await axios.get(`http://localhost:8090/api/exams/${examId}/questions/submission-status`, {
          params: { studentId },
          headers: { Authorization: `Bearer ${token}` },
        });
        setSubmitted(submissionStatusResponse.data);

        // ÏãúÌóò Î¨∏Ï†ú Î∂àÎü¨Ïò§Í∏∞
        const examResponse = await axios.get(`http://localhost:8090/api/exams/${examId}/questions`, {
          headers: { Authorization: `Bearer ${token}` },
        });

        console.log("üî• ÏùëÎãµ Î∞õÏùÄ exam Îç∞Ïù¥ÌÑ∞:", examResponse.data);

        const examData = {
          ...examResponse.data,
          questions: examResponse.data,
        };
        setExam(examData);
      } catch (error) {
        console.error("ÏãúÌóò Ï†ïÎ≥¥Î•º Î∂àÎü¨Ïò§Îäî Ï§ë Ïò§Î•ò Î∞úÏÉù", error);
      } finally {
        setLoading(false);
      }
    };

    fetchExam();
  }, [examId]);

  const handleChange = (questionId, answer) => {
    setAnswers({ ...answers, [questionId]: answer });
  };

  const handleSubmit = async () => {
    try {
      const token = localStorage.getItem("token");
      const studentId = localStorage.getItem("studentId");
      if (!token || !studentId) {
        alert("Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
        return;
      }

      const response = await axios.post(
        `http://localhost:8090/api/exams/${examId}/questions/submit`,
        { studentId, answers },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      alert("ÎãµÏïàÏù¥ Ï†úÏ∂úÎêòÏóàÏäµÎãàÎã§.");
      console.log("Ï†úÏ∂úÌïú ÎãµÏïà:", response.data);
      setSubmitted(true); // Ï†úÏ∂ú ÏÉÅÌÉúÎ•º trueÎ°ú ÏÑ§Ï†ï

      // ÏûêÎèô Ï±ÑÏ†ê API Ìò∏Ï∂ú
      const gradingResponse = await axios.post(
        `http://localhost:8090/api/grade/exam`,
        { exam: { id: examId }, studentId, answers },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setScore(gradingResponse.data); // Ï†êÏàò ÏÑ§Ï†ï
    } catch (error) {
      console.error("ÎãµÏïàÏùÑ Ï†úÏ∂úÌïòÎäî Ï§ë Ïò§Î•ò Î∞úÏÉù:", error);
      alert("ÎãµÏïà Ï†úÏ∂ú Ïã§Ìå®: " + error.message);
    }
  };

  if (loading) {
    return <p>ÏãúÌóò Ï†ïÎ≥¥Î•º Î∂àÎü¨Ïò§Îäî Ï§ë...</p>;
  }

  if (!exam || !exam.questions || exam.questions.length === 0) {
    return <p>ÌòÑÏû¨ ÏãúÌóò Î¨∏Ï†úÍ∞Ä ÏóÜÏäµÎãàÎã§.</p>;
  }

  const questions = exam.questions || [];
  const selectedQuestion = questions.find(q => q.id === selectedQuestionId);

  return (
    <div className="exam-container">
      <div className="exam-sidebar">
        <h3>Î¨∏Ï†ú Î™©Î°ù</h3>
        {questions.map((question) => (
          <button
            key={question.id}
            onClick={() => setSelectedQuestionId(question.id)}
            className="question-button"
          >
            {question.id}
          </button>
        ))}
      </div>

      <div className="exam-content">
        <h2 className="exam-header">{exam.title}</h2>
        {selectedQuestion ? (
          <div className="question-container">
            <p className="question-text">{selectedQuestion.questionText}</p>
            <div className="options-container">
              {selectedQuestion.type === "MULTIPLE_CHOICE" ? (
                selectedQuestion.answers.map((option, index) => (
                  <label key={index} className="option-label">
                    <input
                      type="radio"
                      name={`question-${selectedQuestion.id}`}
                      value={option}
                      checked={answers[selectedQuestion.id] === option}
                      onChange={() => handleChange(selectedQuestion.id, option)}
                    />
                    {option}
                  </label>
                ))
              ) : (
                <textarea
                  className="essay-input"
                  placeholder="ÎãµÎ≥ÄÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî."
                  value={answers[selectedQuestion.id] || ""}
                  onChange={(e) => handleChange(selectedQuestion.id, e.target.value)}
                />
              )}
            </div>
          </div>
        ) : (
          <p>Î¨∏Ï†úÎ•º ÏÑ†ÌÉùÌïòÏÑ∏Ïöî.</p>
        )}
        <button onClick={handleSubmit} className="submit-button" disabled={submitted}>
          {submitted ? "Ï†úÏ∂ú ÏôÑÎ£å" : "Ï†úÏ∂ú"}
        </button>
        {score !== null && (
          <div className="score-container">
            <h3>Ï†êÏàò: {score}</h3>
          </div>
        )}
      </div>
    </div>
  );
};

export default ExamPage;
