import { useState, useEffect } from "react";
import axios from "axios";

const AddContentForm = ({ courseId, weekNumber, token }) => {
  const [videoFile, setVideoFile] = useState(null);
  const [errorMessage, setErrorMessage] = useState("");
  const [successMessage, setSuccessMessage] = useState("");
  const [memberId, setMemberId] = useState(null);
  const [contentList, setContentList] = useState([]);

  // üîπ ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ Í∞ÄÏ†∏Ïò§Í∏∞ (JWT ÌÜ†ÌÅ∞ ÏÇ¨Ïö©)
  useEffect(() => {
    if (token) {
      const fetchUserData = async () => {
        try {
          const userResponse = await axios.get("http://localhost:8090/api/users/me", {
            headers: { Authorization: `Bearer ${token}` },
          });
          setMemberId(userResponse.data.id);
        } catch (error) {
          console.error("ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ Ï°∞Ìöå Ïã§Ìå®:", error);
          setErrorMessage("ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥Î•º Í∞ÄÏ†∏Ïò§Îäî Îç∞ Ïã§Ìå®ÌñàÏäµÎãàÎã§.");
        }
      };

      fetchUserData();
    }
  }, [token]);

  // üîπ ÏΩòÌÖêÏ∏† Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞
  const fetchContentList = async () => {
    try {
      const response = await axios.get(
        `http://localhost:8090/api/courses/${courseId}/weeks/contents`, // ‚úÖ API URL ÏàòÏ†ï
        { headers: { Authorization: `Bearer ${token}` } }
      );

      const weekData = response.data[weekNumber] || [];
      setContentList(weekData);
    } catch (error) {
      console.error("ÏΩòÌÖêÏ∏† Î™©Î°ù Ï°∞Ìöå Ïã§Ìå®:", error);
      setErrorMessage("ÏΩòÌÖêÏ∏†Î•º Î∂àÎü¨Ïò§Îäî Îç∞ Ïã§Ìå®ÌñàÏäµÎãàÎã§.");
    }
  };

  // üìå Ï¥àÍ∏∞ Î†åÎçîÎßÅ Î∞è ÏóÖÎ°úÎìú ÌõÑ Î™©Î°ù Í∞±Ïã†
  useEffect(() => {
    fetchContentList();
  }, [courseId, weekNumber, token]);

  // üîπ ÎπÑÎîîÏò§ ÏóÖÎ°úÎìú Ìï®Ïàò
  const handleVideoUpload = async (e) => {
    e.preventDefault();
    setErrorMessage("");
    setSuccessMessage("");

    if (!videoFile) {
      setErrorMessage("ÏóÖÎ°úÎìúÌï† ÎπÑÎîîÏò§ ÌååÏùºÏùÑ ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî.");
      return;
    }

    if (!memberId) {
      setErrorMessage("Î°úÍ∑∏Ïù∏ Ï†ïÎ≥¥Í∞Ä ÏóÜÏäµÎãàÎã§.");
      return;
    }

    const formData = new FormData();
    formData.append("file", videoFile);
    formData.append("memberId", memberId); // ‚úÖ memberId Ï∂îÍ∞Ä

    try {
      const url = `http://localhost:8090/api/courses/${courseId}/weeks/${weekNumber}/content`;
      await axios.post(url, formData, {
        headers: {
          "Content-Type": "multipart/form-data",
          Authorization: `Bearer ${token}`,
        },
      });

      setSuccessMessage("‚úÖ ÎπÑÎîîÏò§ ÏóÖÎ°úÎìú ÏÑ±Í≥µ!");
      setVideoFile(null);
      fetchContentList();
    } catch (error) {
      console.error("‚ùå ÏóÖÎ°úÎìú Ïã§Ìå®:", error.response?.data);
      setErrorMessage(error.response?.data?.message || "ÏóÖÎ°úÎìú Ï§ë Ïò§Î•ò Î∞úÏÉù.");
    }
  };

  // üîπ ÏΩòÌÖêÏ∏† ÏÇ≠Ï†ú Ìï®Ïàò
  const handleDeleteContent = async (contentId) => {
    if (!contentId) {
      setErrorMessage("ÏÇ≠Ï†úÌï† ÏΩòÌÖêÏ∏† Ï†ïÎ≥¥Í∞Ä Ïò¨Î∞îÎ•¥ÏßÄ ÏïäÏäµÎãàÎã§.");
      return;
    }
  
    if (!memberId) {
      setErrorMessage("Î°úÍ∑∏Ïù∏ Ï†ïÎ≥¥Í∞Ä ÏóÜÏäµÎãàÎã§.");
      return;
    }
  
    if (!window.confirm("Ï†ïÎßêÎ°ú ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå?")) return;
  
    try {
      await axios.delete(`http://localhost:8090/api/courses/${courseId}/weeks/${weekNumber}/content/${contentId}?memberId=${memberId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
  
      setSuccessMessage("‚úÖ ÏΩòÌÖêÏ∏†Í∞Ä ÏÇ≠Ï†úÎêòÏóàÏäµÎãàÎã§.");
      fetchContentList(); // ÏÇ≠Ï†ú ÌõÑ Î™©Î°ù Í∞±Ïã†
    } catch (error) {
      console.error("‚ùå ÏÇ≠Ï†ú Ïã§Ìå®:", error.response?.data);
      setErrorMessage(error.response?.data?.message || "ÏÇ≠Ï†ú Ï§ë Ïò§Î•ò Î∞úÏÉù.");
    }
  };
  

  return (
    <div>
      <h3>{weekNumber}Ï£ºÏ∞® ÎπÑÎîîÏò§ ÏóÖÎ°úÎìú</h3>
      {errorMessage && <p style={{ color: "red" }}>{errorMessage}</p>}
      {successMessage && <p style={{ color: "green" }}>{successMessage}</p>}

      {/* üîπ ÎπÑÎîîÏò§ ÏóÖÎ°úÎìú Ìèº */}
      <form onSubmit={handleVideoUpload}>
        <input type="file" accept="video/*" onChange={(e) => setVideoFile(e.target.files[0])} />
        <button type="submit">ÎπÑÎîîÏò§ ÏóÖÎ°úÎìú</button>
      </form>

      {/* üîπ ÏΩòÌÖêÏ∏† Î™©Î°ù ÌëúÏãú */}
      <h4>ÏóÖÎ°úÎìúÎêú ÏΩòÌÖêÏ∏†</h4>
      {contentList.length === 0 ? (
        <p>Îì±Î°ùÎêú ÏΩòÌÖêÏ∏†Í∞Ä ÏóÜÏäµÎãàÎã§.</p>
      ) : (
        <ul>
          {contentList.map((content, index) => {
           
            return (
              <li key={index}>
                <a href={content.filePath} target="_blank" rel="noopener noreferrer">
                  {content.fileName}
                </a>{" "}
                <button
                  onClick={() => {
                    if (!content.id) {
                      console.error("ÏÇ≠Ï†úÌï† ÏΩòÌÖêÏ∏†Ïùò IDÍ∞Ä ÏóÜÏäµÎãàÎã§.", content);
                      setErrorMessage("ÏÇ≠Ï†úÌï† ÏΩòÌÖêÏ∏† Ï†ïÎ≥¥Í∞Ä Ïò¨Î∞îÎ•¥ÏßÄ ÏïäÏäµÎãàÎã§.");
                      return;
                    }
                    handleDeleteContent(content.id);
                  }}
                  className="btn-delete"
                >
                  ÏÇ≠Ï†ú
                </button>
              </li>
            );
          })}
        </ul>
      )}
    </div>
  );
};

export default AddContentForm;
