import React, { useEffect, useState } from "react";
import axios from "axios";

const StudentAttendance = () => {
  const [attendanceData, setAttendanceData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [studentId, setStudentId] = useState(null); // studentId 상태 추가
  const token = localStorage.getItem("token"); // 로그인 토큰을 로컬 스토리지에서 가져오기

  useEffect(() => {
    const storedStudentId = localStorage.getItem("studentId");
    const storedToken = localStorage.getItem("token");
    
    console.log("Stored studentId:", storedStudentId);  // 확인
    console.log("Stored token:", storedToken);          // 확인
  
    if (storedStudentId) {
      setStudentId(storedStudentId);
    } else {
      console.error("studentId가 로컬 스토리지에 없습니다.");
    }
  
    if (!storedToken) {
      console.error("token이 로컬 스토리지에 없습니다.");
    }
  }, []);
  

  useEffect(() => {
    const fetchAttendance = async () => {
      if (!studentId) {
        console.error("studentId가 없습니다.");
        return;
      }

      try {
        const response = await axios.get(`http://localhost:8090/api/attendance/student`, {
          params: { studentId },
          headers: { Authorization: `Bearer ${token}` },
        });
        setAttendanceData(response.data);
      } catch (error) {
        console.error("출석 데이터를 불러오는 중 오류 발생:", error);
      } finally {
        setLoading(false);
      }
    };

    if (token && studentId) {
      fetchAttendance();
    } else {
      console.error("로그인이 필요하거나 studentId가 없습니다.");
    }
  }, [studentId, token]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (!attendanceData) {
    return <div>출석 데이터가 없습니다.</div>;
  }

  return (
    <div>
      {/* 출석 데이터 렌더링 */}
      <h2>출석 정보</h2>
      <div>
        {Object.entries(attendanceData.attendance).map(([courseId, status]) => (
          <div key={courseId}>
            <h3>Course ID: {courseId}</h3>
            <p>출석 상태: {status ? "출석" : "결석"}</p>
          </div>
        ))}
      </div>
      <div>
        {Object.entries(attendanceData.progress).map(([courseId, progress]) => (
          <div key={courseId}>
            <h3>Course ID: {courseId}</h3>
            <p>진도율: {progress}%</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default StudentAttendance;
