import { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";
import './ExamList.css'; // CSS 파일 import

const ExamList = () => {
  const { courseId } = useParams(); // URL에서 courseId 가져오기
  const [exams, setExams] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();
  const [course, setCourse] = useState(null); // 강의 개요를 위한 상태 추가

  useEffect(() => {
    const fetchExams = async () => {
      try {
        const token = localStorage.getItem("token");
        if (!token) {
          console.error("❌ 토큰이 없습니다.");
          return;
        }

        const response = await axios.get("http://localhost:8090/api/exams", {
          headers: {
            "Authorization": `Bearer ${token}`
          }
        });

        console.log("✅ API 응답 데이터:", response.data);
        setExams(response.data || []);
      } catch (error) {
        console.error("❌ axios 요청 오류:", error.response?.data || error.message);
      } finally {
        setLoading(false);
      }
    };

    const fetchCourseDetails = async () => {
      try {
        const token = localStorage.getItem("token");
        const response = await axios.get(
          `http://localhost:8090/api/courses/${courseId}`, 
          { headers: { Authorization: `Bearer ${token}` } }
        );
        setCourse(response.data);
      } catch (error) {
        console.error("강의 개요 불러오기 실패", error);
      }
    };

    fetchExams();
    fetchCourseDetails();
  }, [courseId]);

  const handleExamSelect = (examId) => {
    navigate(`/exam/${examId}`);
  };

  if (loading) {
    return <p className="loading">로딩 중...</p>;
  }

  return (
    <div className="exam-list-container">
      <h1>시험 목록</h1>

      {/* 강의 개요 영역 추가 */}
      {course && (
        <div className="course-overview">
          <h3>강의 개요</h3>
          <p><strong>강의명:</strong> {course.name}</p>
          <p><strong>담당 교수:</strong> {course.professor || "정보 없음"}</p>
          <p><strong>설명:</strong> {course.description || "설명이 없습니다."}</p>

          {/* 개요 버튼 추가 */}
          <div className="overview-buttons">
            <button onClick={() => navigate(`/courses/${courseId}/announcements`)}>📢 공지사항</button>
            <button onClick={() => navigate(`/courses/${courseId}/qna`)}>❓ 강좌 Q&A</button>
            <button onClick={() => navigate(`/courses/${courseId}`)}>📚 내 강좌</button>
            <button onClick={() => navigate(`/student/studentattendance/${courseId}`)}>📝 출석 현황</button>
          </div>
        </div>
      )}

      <ul className="exam-list">
        {exams.map(exam => (
          <li key={exam.id} className="exam-item">
            <button onClick={() => handleExamSelect(exam.id)}>
              {exam.title}
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ExamList;
