import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './FileLibrary.css';


function FileLibrary() {
    const [searchTerm, setSearchTerm] = useState(''); // 검색어 상태
    const [files, setFiles] = useState([]); // 전체 파일 목록
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState("");

    const token = localStorage.getItem("token"); // ✅ JWT 토큰 가져오기

    // ✅ API에서 파일 목록 불러오기
    useEffect(() => {
        if (!token) {
            console.warn("⚠️ 토큰이 없습니다. 로그인이 필요합니다.");
            setError("로그인이 필요합니다.");
            setLoading(false);
            return;
        }

        const fetchFiles = async () => {
            try {
                const response = await axios.get('/api/uploaded_files', {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setFiles(response.data);
            } catch (error) {
                console.error("🚨 파일 데이터 불러오기 오류:", error);
                setError("파일 데이터를 불러오는 중 오류가 발생했습니다.");
            } finally {
                setLoading(false);
            }
        };

        fetchFiles();
    }, [token]);

    // ✅ 검색 기능 직접 구현
    const filteredFiles = files.filter(file =>
        file.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
        file.category.toLowerCase().includes(searchTerm.toLowerCase()) ||
        file.uploaded_by.toLowerCase().includes(searchTerm.toLowerCase())
    );

    if (loading) return <div>📡 파일 목록 불러오는 중...</div>;

    return (
        <div className="dashboard">
            

            <main className="main-content">
                <h2>자료 목록</h2>

                {error ? (
                    <p className="error-message">{error}</p>
                ) : (
                    <>
                        {/* 🔹 자료 검색 입력창 */}
                        <input
                            type="text"
                            placeholder="자료 검색"
                            value={searchTerm}
                            onChange={(e) => setSearchTerm(e.target.value)}
                            className="search-input"
                        />  

                        {/* 🔹 자료 목록 테이블 */}
                        <table className="file-table">
                            <thead>
                                <tr>
                                    <th>제목</th>
                                    <th>카테고리</th>
                                    <th>작성자</th>
                                    <th>업로드 날짜</th>
                                    <th>파일</th>
                                </tr>
                            </thead>
                            <tbody>
                                {filteredFiles.length > 0 ? (
                                    filteredFiles.map((file) => (
                                        <tr key={file.id}>
                                            <td>{file.title}</td>
                                            <td>{file.category}</td>
                                            <td>{file.uploaded_by}</td>
                                            <td>{new Date(file.upload_date).toLocaleString()}</td>
                                            <td>
                                                <a href={file.file_url} download>
                                                    다운로드
                                                </a>
                                            </td>
                                        </tr>
                                    ))
                                ) : (
                                    <tr>
                                        <td colSpan="5">검색 결과가 없습니다.</td>
                                    </tr>
                                )}
                            </tbody>
                        </table>
                    </>
                )}
            </main>
        </div>
    );
}

export default FileLibrary;
