import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

import './CourseList.css';

function CourseList() {
  const [courses, setCourses] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterStatus, setFilterStatus] = useState('');

  useEffect(() => {
    async function fetchCourses() {
      try {
        const token = localStorage.getItem("token");
        const response = await axios.get("http://localhost:8090/api/courses", {
          headers: { Authorization: `Bearer ${token}` }
        });
        setCourses(response.data);
      } catch (error) {
        console.error("강좌 데이터를 불러오는데 실패했습니다.", error);
      }
    }
    fetchCourses();
  }, []);

  const filteredCourses = courses.filter((course) => {
    const matchesSearch =
      course.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      course.description.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesFilter = filterStatus ? course.status === filterStatus : true;
    return matchesSearch && matchesFilter;
  });

  return (
    <div className="dashboard">
      
      <main className="main-content">
        <h2>내 강좌</h2>
        <input
          type="text"
          placeholder="강좌 검색 (이름 또는 설명)"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="search-input"
        />
        <select
          value={filterStatus}
          onChange={(e) => setFilterStatus(e.target.value)}
          className="filter-select"
        >
          <option value="">전체 상태</option>
          <option value="진행 중">진행 중</option>
          <option value="완료">완료</option>
          <option value="취소됨">취소됨</option>
        </select>
        <table>
          <thead>
            <tr>
              <th>강좌명</th>
              <th>설명</th>
              <th>학점</th>
              <th>상태</th>
              <th>자세히 보기</th>
            </tr>
          </thead>
          <tbody>
            {filteredCourses.length > 0 ? (
              filteredCourses.map((course) => (
                <tr key={course.id}>
                  <td>{course.name}</td>
                  <td>{course.description}</td>
                  <td>{course.credits}</td>
                  <td>{course.status}</td>
                  <td>
                  <Link to={`/courses/${course.id}`} className="detail-btn">
                      이동
                    </Link>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="5" style={{ textAlign: "center" }}>등록된 강좌가 없습니다.</td>
              </tr>
            )}
          </tbody>
        </table>
      </main>
    </div>
  );
}

export default CourseList;
