import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './StudentAttendance.css';

const StudentAttendance = () => {
  const { courseId } = useParams();
  const [attendanceData, setAttendanceData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [role, setRole] = useState('');
  const [studentId, setStudentId] = useState('');
  const token = localStorage.getItem('token');

  useEffect(() => {
    const fetchUserInfo = async () => {
      try {
        const response = await axios.get(
          'http://localhost:8090/api/users/me',
          { headers: { Authorization: `Bearer ${token}` } }
        );
        setRole(response.data.role);
        setStudentId(response.data.memberId);
      } catch (error) {
        console.error('사용자 정보 가져오기 오류:', error);
        setError('사용자 정보를 불러올 수 없습니다.');
      }
    };

    fetchUserInfo();
  }, [token]);

  useEffect(() => {
    if (!courseId || !role) return;
    
    const fetchAttendanceData = async () => {
      try {
        setLoading(true);
        let response;
        
        if (role === 'PROFESSOR') {
          // 교수인 경우 해당 강의의 모든 학생 출석 조회
          response = await axios.get(
            `http://localhost:8090/api/attendance/course/${courseId}/students`,
            { headers: { Authorization: `Bearer ${token}` } }
          );
          
          // 학생 ID를 키로 하는 객체를 배열로 변환
          const attendanceArray = Object.entries(response.data).map(([studentId, data]) => ({
            studentId,
            studentName: data.studentName || '이름 없음',
            isAttended: data.isAttended,
            progress: data.progress,
            lastUpdated: data.lastUpdated
          }));
          
          setAttendanceData(attendanceArray);
          
        } else if (role === 'STUDENT') {
          // 학생인 경우 자신의 모든 강의 출석 조회 또는 특정 강의 출석 조회
          if (courseId) {
            // 특정 강의의 출석 정보만 조회
            const statusResponse = await axios.get(
              `http://localhost:8090/api/attendance/status?studentId=${studentId}`,
              { headers: { Authorization: `Bearer ${token}` } }
            );
            
            const progressResponse = await axios.get(
              `http://localhost:8090/api/attendance/progress/status?studentId=${studentId}`,
              { headers: { Authorization: `Bearer ${token}` } }
            );
            
            const courseResponse = await axios.get(
              `http://localhost:8090/api/courses/${courseId}`,
              { headers: { Authorization: `Bearer ${token}` } }
            );
            
            const attendanceInfo = {
              courseId: courseId,
              courseName: courseResponse.data.name,
              isAttended: statusResponse.data[courseId] || false,
              progress: progressResponse.data[courseId] || 0
            };
            
            setAttendanceData([attendanceInfo]);
          } else {
            // 모든 강의 출석 정보 조회
            response = await axios.get(
              `http://localhost:8090/api/attendance/student/${studentId}/courses`,
              { headers: { Authorization: `Bearer ${token}` } }
            );
            
            // 강의 ID를 키로 하는 객체를 배열로 변환
            const attendanceArray = Object.entries(response.data).map(([courseId, data]) => ({
              courseId,
              courseName: data.courseName || '강의명 없음',
              isAttended: data.isAttended,
              progress: data.progress
            }));
            
            setAttendanceData(attendanceArray);
          }
        }
      } catch (error) {
        console.error('출석 데이터 가져오기 오류:', error);
        setError('출석 정보를 불러올 수 없습니다.');
      } finally {
        setLoading(false);
      }
    };

    fetchAttendanceData();
  }, [courseId, role, studentId, token]);

  if (loading) return <div className="loading">로딩 중...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="attendance-container">
      <h2>출석 현황</h2>
      
      {role === 'PROFESSOR' ? (
        <div className="professor-view">
          <h3>강의: {attendanceData.length > 0 ? attendanceData[0].courseName : courseId}</h3>
          <table className="attendance-table">
            <thead>
              <tr>
                <th>학생 ID</th>
                <th>학생 이름</th>
                <th>출석 상태</th>
                <th>진도율</th>
                <th>마지막 접속</th>
              </tr>
            </thead>
            <tbody>
              {attendanceData.map((student) => (
                <tr key={student.studentId}>
                  <td>{student.studentId}</td>
                  <td>{student.studentName}</td>
                  <td className={student.isAttended ? 'attended' : 'not-attended'}>
                    {student.isAttended ? '출석' : '미출석'}
                  </td>
                  <td>
                    <div className="progress-bar">
                      <div 
                        className="progress-fill"
                        style={{ width: `${student.progress}%` }}
                      ></div>
                      <span>{student.progress.toFixed(1)}%</span>
                    </div>
                  </td>
                  <td>{student.lastUpdated ? new Date(student.lastUpdated).toLocaleString() : '정보 없음'}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ) : (
        <div className="student-view">
          {courseId ? (
            // 특정 강의 출석 정보 표시
            <div className="course-attendance">
              <h3>{attendanceData[0]?.courseName || `강의 ID: ${courseId}`}</h3>
              <div className="attendance-status">
                <div className="status-card">
                  <h4>출석 상태</h4>
                  <div className={`status-indicator ${attendanceData[0]?.isAttended ? 'attended' : 'not-attended'}`}>
                    {attendanceData[0]?.isAttended ? '출석' : '미출석'}
                  </div>
                </div>
                <div className="status-card">
                  <h4>진도율</h4>
                  <div className="progress-bar">
                    <div 
                      className="progress-fill"
                      style={{ width: `${attendanceData[0]?.progress || 0}%` }}
                    ></div>
                    <span>{(attendanceData[0]?.progress || 0).toFixed(1)}%</span>
                  </div>
                  <p>출석 인정 기준: 90% 이상 시청</p>
                </div>
              </div>
            </div>
          ) : (
            // 모든 강의 출석 정보 표시
            <div className="all-courses-attendance">
              <h3>모든 강의 출석 현황</h3>
              <table className="attendance-table">
                <thead>
                  <tr>
                    <th>강의명</th>
                    <th>출석 상태</th>
                    <th>진도율</th>
                  </tr>
                </thead>
                <tbody>
                  {attendanceData.map((course) => (
                    <tr key={course.courseId}>
                      <td>{course.courseName}</td>
                      <td className={course.isAttended ? 'attended' : 'not-attended'}>
                        {course.isAttended ? '출석' : '미출석'}
                      </td>
                      <td>
                        <div className="progress-bar">
                          <div 
                            className="progress-fill"
                            style={{ width: `${course.progress}%` }}
                          ></div>
                          <span>{course.progress.toFixed(1)}%</span>
                        </div>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default StudentAttendance;
