import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './TeamProjectMembers.css';

function TeamProjectMembers() {
    const [teamMembers, setTeamMembers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState("");
    const token = localStorage.getItem("token"); // ‚úÖ JWT ÌÜ†ÌÅ∞ Í∞ÄÏ†∏Ïò§Í∏∞

    useEffect(() => {
        if (!token) {
            console.warn("‚ö†Ô∏è ÌÜ†ÌÅ∞Ïù¥ ÏóÜÏäµÎãàÎã§. Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
            setError("Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
            setLoading(false);
            return;
        }

        const fetchTeamMembers = async () => {
            try {
                const response = await axios.get('/api/team-members', {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setTeamMembers(response.data);
            } catch (error) {
                console.error("üö® ÌåÄÏõê Ï†ïÎ≥¥ Î∂àÎü¨Ïò§Í∏∞ Ïò§Î•ò:", error);
                setError("ÌåÄÏõê Ï†ïÎ≥¥Î•º Î∂àÎü¨Ïò§Îäî Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
            } finally {
                setLoading(false);
            }
        };

        fetchTeamMembers();
    }, [token]);

    if (loading) return <div>üì° ÌåÄÏõê Ï†ïÎ≥¥ Î∂àÎü¨Ïò§Îäî Ï§ë...</div>;

    return (
        <div className="team-project-members">
            <h2>ÌåÄÏõê Ï†ïÎ≥¥</h2>
            {error ? (
                <p className="error-message">{error}</p>
            ) : teamMembers.length === 0 ? (
                <p>Îì±Î°ùÎêú ÌåÄÏõêÏù¥ ÏóÜÏäµÎãàÎã§.</p>
            ) : (
                <table className="members-table">
                    <thead>
                        <tr>
                            <th>Ïù¥Î¶Ñ</th>
                            <th>Ïó≠Ìï†</th>
                        </tr>
                    </thead>
                    <tbody>
                        {teamMembers.map((member, index) => (
                            <tr key={member.id || index}>
                                <td>{member.name}</td>
                                <td>{member.role}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
}

export default TeamProjectMembers;
