import React, { useEffect, useState } from 'react';
import axios from 'axios';

const StudentAttendance = ({ studentId }) => {
  const [attendanceData, setAttendanceData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchAttendanceData = async () => {
      try {
        const response = await axios.get(`http://localhost:8090/api/attendance/student`);
        setAttendanceData(response.data);
      } catch (error) {
        setError("출석부 조회 실패: " + error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchAttendanceData();
  }, [studentId]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div>
      <h3>출석 상태</h3>
      <ul>
        {Object.entries(attendanceData.attendance).map(([courseId, isPresent]) => (
          <li key={courseId}>
            Course {courseId}: {isPresent ? '참석' : '불참'}
          </li>
        ))}
      </ul>

      <h3>진도율</h3>
      <ul>
        {Object.entries(attendanceData.progress).map(([courseId, progress]) => (
          <li key={courseId}>
            Course {courseId}: {progress}% 완료
          </li>
        ))}
      </ul>
    </div>
  );
};

export default StudentAttendance;
