import React, { useState, useEffect, useRef } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import AddContentForm from "./AddContentForm";
import AddAnnouncement from "./AddAnnouncement";
import AddAssignment from "./AddAssignment";
import CreateBoard from "./CreateBoard";
import { Link } from "react-router-dom";
import "./CourseDetail.css";

const CourseDetailPage = () => {
  const { courseId } = useParams();
  const navigate = useNavigate();
  const [course, setCourse] = useState(null);
  const [weeks, setWeeks] = useState([]);
  const [contents, setContents] = useState({});
  const [announcements, setAnnouncements] = useState({});
  const [assignments, setAssignments] = useState({});
  const [boards, setBoards] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activeWeek, setActiveWeek] = useState(null);
  const [activeGoalWeek, setActiveGoalWeek] = useState(null);
  const [editingDescription, setEditingDescription] = useState(false);
  const [newDescription, setNewDescription] = useState("");
  const [showBoardForm, setShowBoardForm] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');
  const [memberId, setMemberId] = useState(null);
  const token = localStorage.getItem("token");
  const [role, setRole] = useState(null);
  const [progressStatus, setProgressStatus] = useState({});
  const [attendanceStatus, setAttendanceStatus] = useState({});
  const videoRefs = useRef({});
  const { weekNumber } = useParams();  // URLì—ì„œ weekNumber ê°€ì ¸ì˜¤ê¸°
  useEffect(() => {
    if (!courseId) return;
  
    const fetchCourseDetails = async () => {
      try {
        const courseResponse = await axios.get(`http://localhost:8090/api/courses/${courseId}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
  
        setCourse(courseResponse.data);
        setNewDescription(courseResponse.data.description || "");
  
        const storedMemberId = localStorage.getItem('studentId');
        if (storedMemberId) {
          setMemberId(storedMemberId);
        } else {
          const userResponse = await axios.get('http://localhost:8090/api/users/me', {
            headers: { Authorization: `Bearer ${token}` },
          });
          setMemberId(userResponse.data.memberId);
        }
  
        const contentResponse = await axios.get(`http://localhost:8090/api/courses/${courseId}/weeks/contents`, {
          headers: { Authorization: `Bearer ${token}` },
        });
  
        setContents(contentResponse.data || {});
   
      } catch (error) {
        console.error("âŒ ê°•ì˜ ìƒì„¸ ì •ë³´ ë¶ˆëŸ¬ì˜¤ê¸° ì˜¤ë¥˜:", error);
      } finally {
        setLoading(false);
      }
    };
  
    fetchCourseDetails();
  }, [courseId, token]);

  useEffect(() => {
    const fetchUserRole = async () => {
      try {
        const response = await axios.get('http://localhost:8090/api/users/me', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setRole(response.data.role);
      } catch (error) {
        console.error("âŒ ì‚¬ìš©ì ì—­í•  ë¶ˆëŸ¬ì˜¤ê¸° ì˜¤ë¥˜:", error);
      }
    };
  
    if (token) {
      fetchUserRole();
    }
  }, [token]);

  // ì¶œì„ ìƒíƒœ ë° ì§„ë„ìœ¨ ì¡°íšŒ
  useEffect(() => {
    if (!memberId) return;

    const fetchAttendanceAndProgressStatus = async () => {
      try {
        // ì¶œì„ ìƒíƒœ ì¡°íšŒ
        const attendanceResponse = await axios.get(
          `http://localhost:8090/api/attendance/status?studentId=${memberId}`,
          { headers: { Authorization: `Bearer ${token}` } }
        );
        setAttendanceStatus(attendanceResponse.data || {});

        // ì§„ë„ìœ¨ ìƒíƒœ ì¡°íšŒ
        const progressResponse = await axios.get(
          `http://localhost:8090/api/attendance/progress/status?studentId=${memberId}`,
          { headers: { Authorization: `Bearer ${token}` } }
        );
        setProgressStatus(progressResponse.data || {});
      } catch (error) {
        console.error("âŒ ì¶œì„ ë° ì§„ë„ìœ¨ ìƒíƒœ ë¶ˆëŸ¬ì˜¤ê¸° ì˜¤ë¥˜:", error);
      }
    };

    fetchAttendanceAndProgressStatus();
  }, [memberId, token]);

  // ë‚˜ë¨¸ì§€ ê¸°ì¡´ useEffect í•¨ìˆ˜ë“¤...
  useEffect(() => {
    if (!courseId || weeks.length === 0) return;

    const fetchAnnouncements = async () => {
      const announcementsData = {};

      for (let week of weeks) {
        try {
          const response = await axios.get(
            `http://localhost:8090/courses/${courseId}/weeks/${week.weekNumber}/announcements`,
            { headers: { Authorization: `Bearer ${token}` } }
          );
          announcementsData[week.weekNumber] = response.data || [];
        } catch (error) {
          announcementsData[week.weekNumber] = [];
        }
      }
      setAnnouncements(announcementsData);
    };

    fetchAnnouncements();
  }, [weeks, courseId, token]);

  useEffect(() => {
    if (!courseId) return;
    if (!token) {
      console.error("ğŸ“Œ í† í°ì´ ì—†ìŠµë‹ˆë‹¤. ë¡œê·¸ì¸ ìƒíƒœë¥¼ í™•ì¸í•˜ì„¸ìš”.");
      return;
    }
  
    const fetchWeeksAndBoards = async () => {
      try {
        console.log("ğŸ“Œ API ìš”ì²­ ì‹œì‘! í† í°:", token);
  
        const weeksResponse = await axios.get(
          `http://localhost:8090/api/courses/${courseId}/weeks`,
          { headers: { Authorization: `Bearer ${token}` } }
        );
  
        console.log("ğŸ“Œ ì£¼ì°¨ ë°ì´í„° ì‘ë‹µ:", weeksResponse.data);
  
        const weeksData = weeksResponse.data || [];
        if (!Array.isArray(weeksData) || weeksData.length === 0) {
          console.warn("âš ï¸ ì£¼ì°¨ ë°ì´í„°ê°€ ë¹„ì–´ ìˆìŠµë‹ˆë‹¤.");
          return;
        }
        
        setWeeks(weeksData);
  
        const boardData = {};
        for (const week of weeksData) {
          if (!week.weekNumber) {
            console.warn(`âš ï¸ ì£¼ì°¨ ë°ì´í„°ì— weekNumberê°€ ì—†ìŠµë‹ˆë‹¤.`, week);
            continue;
          }
  
          try {
            console.log(`ğŸ“Œ ${week.weekNumber}ì£¼ì°¨ ê²Œì‹œíŒ ìš”ì²­ ì‹œì‘!`);
            const response = await axios.get(
              `http://localhost:8090/weekly-board/course/${courseId}/week/${week.weekNumber}/boards`,
              { headers: { Authorization: `Bearer ${token}` } }
            );
  
            console.log(`ğŸ“Œ ${week.weekNumber}ì£¼ì°¨ ê²Œì‹œíŒ ì‘ë‹µ:`, response.data);
            
            if (!response.data) {
              console.warn(`âš ï¸ ${week.weekNumber}ì£¼ì°¨ ê²Œì‹œíŒ ì‘ë‹µì´ ë¹„ì–´ ìˆìŠµë‹ˆë‹¤.`);
              boardData[week.weekNumber] = [];
            } else {
              boardData[week.weekNumber] = response.data;
            }
  
          } catch (error) {
            console.error(`âŒ ${week.weekNumber}ì£¼ì°¨ ê²Œì‹œíŒ ë¶ˆëŸ¬ì˜¤ê¸° ì‹¤íŒ¨:`, error);
            boardData[week.weekNumber] = [];
          }
        }
  
        setBoards(boardData);
        console.log("ğŸ“Œ ìµœì¢… boardData ìƒíƒœ:", boardData);
  
      } catch (error) {
        console.error("âŒ ì£¼ì°¨ë³„ ë°ì´í„° ë¶ˆëŸ¬ì˜¤ê¸° ì‹¤íŒ¨:", error);
      }
    };
  
    fetchWeeksAndBoards();
  }, [courseId, token]);
  
  useEffect(() => {
    console.log("ğŸ“Œ boards ìƒíƒœ ì—…ë°ì´íŠ¸ë¨:", boards);
  }, [boards]);

  useEffect(() => {
    if (!courseId || weeks.length === 0) return;

    const fetchAssignments = async () => {
      const assignmentsData = {};
      for (let i = 0; i < weeks.length; i++) {
        const week = weeks[i];
        const weekNumber = week.weekNumber;

        if (!weekNumber) continue;

        try {
          const response = await axios.get(
            `http://localhost:8090/api/courses/${courseId}/weeks/${weekNumber}/assignments`,
            { headers: { Authorization: `Bearer ${token}` } }
          );
          
          assignmentsData[weekNumber] = response.data || [];
        } catch (error) {
          assignmentsData[weekNumber] = [];
        }
      }

      setAssignments(assignmentsData);
    };

    fetchAssignments();
  }, [weeks, courseId, token]);

  const updateVideoProgress = async (contentId, watchedPercentage, courseId, weekNumber) => {
    // weekNumberê°€ ìœ íš¨í•œì§€ ì²´í¬
    if (!memberId || role === 'PROFESSOR' || !weekNumber || weekNumber === undefined || weekNumber === null) {
      console.error("âŒ ìœ íš¨í•˜ì§€ ì•Šì€ weekNumber:", weekNumber);
      return; // weekNumberê°€ ì—†ìœ¼ë©´ í•¨ìˆ˜ ì¢…ë£Œ
    }
  
    try {
      console.log("weekNumber:", weekNumber); // weekNumber ê°’ í™•ì¸ìš© ë¡œê·¸
  
      // ì¶œì„ ì²´í¬ API í˜¸ì¶œ
      await axios.post(
        `http://localhost:8090/api/attendance/content`, // weekNumberë¥¼ ì œëŒ€ë¡œ ì „ë‹¬
        {
          studentId: memberId,
          contentId: contentId,
          watchedPercentage: watchedPercentage
        },
        { headers: { Authorization: `Bearer ${token}` } }
      );
  
      // ë¡œì»¬ ìƒíƒœ ì—…ë°ì´íŠ¸
      setProgressStatus(prev => ({
        ...prev,
        [contentId]: watchedPercentage
      }));
  
      // ì¶œì„ ì™„ë£Œ ì¡°ê±´
      if (watchedPercentage >= 90) {
        setAttendanceStatus(prev => ({
          ...prev,
          [contentId]: true
        }));
      }
    } catch (error) {
      console.error("âŒ ë™ì˜ìƒ ì§„ë„ìœ¨ ì—…ë°ì´íŠ¸ ì˜¤ë¥˜:", error);
    }
  };
  


  // ë¹„ë””ì˜¤ ì‹œê°„ ì—…ë°ì´íŠ¸ ì²˜ë¦¬ í•¨ìˆ˜
  const handleTimeUpdate = (contentId, event) => {
    const video = event.target;
    const watchedPercentage = (video.currentTime / video.duration) * 100;
    
    // ì´ì „ì— ìƒì„±í•œ videoRefsë¥¼ ì‚¬ìš©í•˜ì—¬ ì²˜ë¦¬ ì¤‘ì¸ ìƒíƒœ í™•ì¸
    if (!videoRefs.current[contentId]) {
      videoRefs.current[contentId] = {
        lastUpdateTime: 0,
        isProcessing: false
      };
    }
    
    const videoRef = videoRefs.current[contentId];
    const currentTime = Date.now();
    
    // ì¼ì • ì‹œê°„(5ì´ˆ) ê°„ê²©ìœ¼ë¡œë§Œ ì—…ë°ì´íŠ¸í•˜ì—¬ ê³¼ë„í•œ API í˜¸ì¶œ ë°©ì§€
    if (!videoRef.isProcessing && currentTime - videoRef.lastUpdateTime > 5000) {
      videoRef.isProcessing = true;
      videoRef.lastUpdateTime = currentTime;
      
      updateVideoProgress(contentId, Math.round(watchedPercentage))
        .finally(() => {
          videoRef.isProcessing = false;
        });
    }
  };

  // ë™ì˜ìƒ ì¢…ë£Œ ì²˜ë¦¬ í•¨ìˆ˜
  const handleVideoEnded = (contentId) => {
    if (role === 'PROFESSOR') return;
    updateVideoProgress(contentId, 100);
  };

  const toggleForm = (weekNumber) => {
    setActiveWeek((prevWeek) => (prevWeek === weekNumber ? null : weekNumber));
  };

  const toggleGoalForm = (weekNumber) => {
    setActiveGoalWeek((prevWeek) => (prevWeek === weekNumber ? null : weekNumber));
  };

  // ì¶œì„ ìƒíƒœì— ë”°ë¥¸ ë°°ì§€ ë Œë”ë§ í•¨ìˆ˜
  const renderAttendanceBadge = (contentId) => {
    if (role === 'PROFESSOR') return null;
    
    if (attendanceStatus[contentId]) {
      return <span className="attendance-badge complete">ì¶œì„ ì™„ë£Œ</span>;
    } else if (progressStatus[contentId] && progressStatus[contentId] > 0) {
      return <span className="attendance-badge in-progress">ì§„í–‰ ì¤‘ ({Math.round(progressStatus[contentId])}%)</span>;
    } else {
      return <span className="attendance-badge not-started">ë¯¸ìˆ˜ê°•</span>;
    }
  };

  return (
    <div className="course-detail-container">
      <div className="course-header">
        <h2>{course?.name} - ê°•ì˜ì‹¤</h2>
        <p className="professor-name">ë‹´ë‹¹ êµìˆ˜: {course?.professor || "ì •ë³´ ì—†ìŒ"}</p>
      </div>

      {showBoardForm && <CreateBoard courseId={courseId} />}

      <div className="course-overview">
        <h3>ê°•ì˜ ê°œìš”</h3>
        <div className="overview-buttons">
          <button onClick={() => navigate(`/courses/${courseId}/announcements`)}>ğŸ“¢ ê³µì§€ì‚¬í•­</button>
          <button onClick={() => navigate(`/courses/${courseId}/qna`)}>â“ ê°•ì¢Œ Q&A</button>
        </div>
      </div>

      <div className="week-activities">
        <h3>ì£¼ì°¨ë³„ í•™ìŠµ í™œë™</h3>
        {weeks.length > 0 ? (
          <div className="weeks-container">
            {weeks.map((week) => {
              const weekContents = contents?.[week.weekNumber] || [];
              const weekAnnouncements = announcements?.[week.weekNumber] || [];
              const weekAssignments = assignments?.[week.weekNumber] || [];
             
              return (
                <div key={week.weekNumber} className="week-item">
                  <div className="week-header">
                    <h4>{week.weekNumber}ì£¼ì°¨</h4>
                    {role === 'PROFESSOR' && (
                    <button onClick={() => toggleGoalForm(week.weekNumber)} className="settings-btn">âš™ï¸</button>)}
                  </div>

                  {activeGoalWeek === week.weekNumber && (
                    <div className="goal-form-container">
                      <AddAnnouncement courseId={courseId} weekNumber={week.weekNumber} />
                    </div>
                  )}

                  {weekContents.length > 0 && (
                    <ul className="content-list">
                      {weekContents.map((content, index) => {
                        // íŒŒì¼ í™•ì¥ì ì¶”ì¶œ
                        const fileExtension = content?.filePath?.split('.').pop().toLowerCase();
                        const isVideo = ['mp4', 'webm', 'ogg'].includes(fileExtension);

                        return (
                          <li 
                            key={`week-${week.weekNumber}-content-${index}`} 
                            className="content-item"
                          >
                            {renderAttendanceBadge(content.id)}
                            
                            {isVideo ? (
                              <div className="video-container">
                                <video 
                                  width="640" 
                                  height="360" 
                                  controls
                                  onTimeUpdate={(e) => handleTimeUpdate(content.id, e)}
                                  onEnded={() => handleVideoEnded(content.id)}
                                >
                                  <source src={content.filePath} type={`video/${fileExtension}`} />
                                  ë¸Œë¼ìš°ì €ê°€ ë¹„ë””ì˜¤ íƒœê·¸ë¥¼ ì§€ì›í•˜ì§€ ì•ŠìŠµë‹ˆë‹¤.
                                </video>
                                <div className="progress-bar-container">
                                  <div 
                                    className="progress-bar" 
                                    style={{ width: `${progressStatus[content.id] || 0}%` }}
                                  ></div>
                                </div>
                              </div>
                            ) : (
                              <a href={content.filePath} target="_blank" rel="noopener noreferrer">
                                {content?.fileName || "íŒŒì¼ ì´ë¦„ ì—†ìŒ"}
                              </a>
                            )}
                          </li>
                        );
                      })}
                    </ul>
                  )}

                  <div className="assignment-section">
                    {weekAssignments.length > 0 ? (
                      <ul className="assignment-list">
                        {weekAssignments.map((assignment, index) => {
                          if (!assignment.id) {
                            console.error(`ê³¼ì œ ${assignment.title}ì— idê°€ ì—†ìŠµë‹ˆë‹¤.`);
                            return null;
                          }

                          return (
                            <li key={`week-${week.weekNumber}-assignment-${index}`} className="assignment-item">
                              <Link to={`/api/courses/${courseId}/weeks/${week.weekNumber}/assignments/${assignment.id}?token=${token}`}>
                                <h4>ğŸ“Œ ê³¼ì œ: {assignment.title || "ì œëª© ì—†ìŒ"}</h4>
                              </Link>
                            </li>
                          );
                        })}
                      </ul>
                    ) : (
                      <p></p>
                    )}
                  </div>

                  {weekAnnouncements.length > 0 && (
                    <ul className="announcement-list">
                      {weekAnnouncements.map((announcement, index) => (
                        <li key={`week-${week.weekNumber}-announcement-${index}`} className="announcement-item">
                          <strong>{announcement.title || "ì œëª© ì—†ìŒ"}</strong> - {announcement.content || "ë‚´ìš© ì—†ìŒ"}
                        </li>
                      ))}
                    </ul>
                  )}

                  <ul></ul>
                  <ul></ul>

                  {boards[week.weekNumber] && boards[week.weekNumber].length > 0 ? (
                    boards[week.weekNumber].map((board) => (
                      <li key={board.boardId}>
                        <Link to={`/board/${board.boardId}`} className="text-blue-500 underline">
                          ê²Œì‹œíŒ: {board.title}
                        </Link>
                      </li>
                    ))
                  ) : (
                    <li className="text-gray-500"></li>
                  )}

                  <div className="week-buttons">
                    {role === 'PROFESSOR' && (
                      <button onClick={() => toggleForm(week.weekNumber)} className="toggle-btn">
                        {activeWeek === week.weekNumber ? "ë‹«ê¸°" : "ìë£Œ ë° í™œë™ ì¶”ê°€"}
                      </button>
                    )}
                  </div>

                  {activeWeek === week.weekNumber && (
                    <div className="form-container">
                      <AddContentForm courseId={courseId} weekNumber={week.weekNumber} token={token} />
                      <AddAssignment courseId={courseId} weekNumber={week.weekNumber} token={token} />
                      <CreateBoard courseId={courseId} weekNumber={week.weekNumber} token={token} />
                    </div>
                  )}
                </div>
              );
            })}
          </div>
        ) : (
          <p>ì£¼ì°¨ë³„ í•™ìŠµ í™œë™ì´ ì—†ìŠµë‹ˆë‹¤.</p>
        )}
      </div>
    </div>  
  );
};

export default CourseDetailPage;
