import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const AnnouncementsList = () => {
  const { courseId } = useParams();
  const [announcements, setAnnouncements] = useState([]);
  const [token, setToken] = useState("");
  const [user, setUser] = useState(null);

  // ‚úÖ ÌÜ†ÌÅ∞ÏùÑ localStorageÏóêÏÑú Í∞ÄÏ†∏Ïò¥
  useEffect(() => {
    const storedToken = localStorage.getItem("token");
    if (storedToken) {
      setToken(storedToken);
    }
  }, []);

  useEffect(() => {
    if (!token) return;

    const fetchUserAndAnnouncements = async () => {
      try {
        // ‚úÖ ÌòÑÏû¨ Î°úÍ∑∏Ïù∏Ìïú ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ Í∞ÄÏ†∏Ïò§Í∏∞
        const userResponse = await axios.get("http://localhost:8090/api/users/me", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setUser(userResponse.data);
        console.log("‚úÖ ÌòÑÏû¨ Î°úÍ∑∏Ïù∏Ìïú ÏÇ¨Ïö©Ïûê:", userResponse.data);

        if (!courseId) return;

        // ‚úÖ Í≥µÏßÄÏÇ¨Ìï≠ Î∂àÎü¨Ïò§Í∏∞
        const response = await axios.get(`http://localhost:8090/courses/${courseId}/announcements`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        console.log("üì¢ Í≥µÏßÄÏÇ¨Ìï≠ API ÏùëÎãµ:", response.data);
        setAnnouncements(response.data);
      } catch (error) {
        console.error("Í≥µÏßÄÏÇ¨Ìï≠ Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:", error);
      }
    };

    fetchUserAndAnnouncements();
  }, [courseId, token]);

  return (
    <div>
      <h2>üì¢ Í≥µÏßÄÏÇ¨Ìï≠ Î™©Î°ù</h2>
     
      <table border="1" cellPadding="10">
        <thead>
          <tr>
            <th>Î≤àÌò∏</th>
            <th>Ï†úÎ™©</th>
            <th>ÏûëÏÑ±Ïûê</th>
            <th>ÏûëÏÑ±Ïùº</th>
          </tr>
        </thead>
        <tbody>
          {announcements.length > 0 ? (
            announcements.map((announcement, index) => (
              <tr key={announcement.id}>
                <td>{index + 1}</td>
                <td>{announcement.title || "Ï†úÎ™© ÏóÜÏùå"}</td>
                <td>{announcement.authorName || "ÏûëÏÑ±Ïûê ÏóÜÏùå"}</td>
                <td>{announcement.createdAt ? new Date(announcement.createdAt).toLocaleDateString() : "ÎÇ†Ïßú ÏóÜÏùå"}</td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="4">Í≥µÏßÄÏÇ¨Ìï≠Ïù¥ ÏóÜÏäµÎãàÎã§.</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default AnnouncementsList;
