import React, { useState } from "react";
import axios from "axios";
import "./UserForm.css";

function UserForm() {
  const [user, setUser] = useState({
    name: "",
    studentId: "",
    password: "",
    role: "STUDENT",
    birthDate: "",
    email: "",
  });

  const handleChange = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');

    axios
      .post("http://localhost:8090/api/users", user, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      })
      .then((response) => {
        alert("회원이 등록되었습니다.");
      })
      .catch((error) => {
        // 서버 오류 처리 (5xx)
        if (error.response && error.response.status >= 500 && error.response.status < 600) {
          alert("서버 오류가 발생했습니다. 나중에 다시 시도해주세요.");
        }
        // 클라이언트 오류 처리 (4xx)
        else if (error.response && error.response.status >= 400 && error.response.status < 500) {
          // 403: 권한 없음
          if (error.response.status === 403) {
            alert("권한이 없습니다. 관리자 계정으로 로그인하세요.");
          }
          // 400: 잘못된 요청
          else if (error.response.status === 400) {
            alert("입력한 정보에 오류가 있습니다. 다시 확인해주세요.");
          }
          // 404: 페이지를 찾을 수 없음
          else if (error.response.status === 404) {
            alert("요청한 페이지를 찾을 수 없습니다.");
          }
          // 401: 인증 실패
          else if (error.response.status === 401) {
            alert("인증 실패. 다시 로그인 해주세요.");
          }
          else {
            alert("회원 등록 중 오류가 발생했습니다.");
          }
        }
        // 네트워크 오류나 다른 오류 처리
        else if (error.request) {
          alert("네트워크 오류가 발생했습니다. 인터넷 연결을 확인해주세요.");
        }
        // 예상치 못한 오류 처리
        else {
          alert("알 수 없는 오류가 발생했습니다.");
        }
      });
  };


  return (
    <form className="user-form" onSubmit={handleSubmit}>

      <label>
        이름:
        <input type="text" name="name" value={user.name} onChange={handleChange} required />
      </label>
      <label>
        학번:
        <input type="text" name="studentId" value={user.studentId} onChange={handleChange} required />
      </label>
      <label>
        비밀번호:
        <input type="password" name="password" value={user.password} onChange={handleChange} required />
      </label>
      <label>
        역할:
        <select name="role" value={user.role} onChange={handleChange}>
          <option value="STUDENT">학생</option>
          <option value="PROFESSOR">교수</option>
        </select>
      </label>
      <label>
        생년월일:
        <input type="date" name="birthDate" value={user.birthDate} onChange={handleChange} />
      </label>
      <label>
        이메일:
        <input type="email" name="email" value={user.email} onChange={handleChange} />
      </label>
      <button type="submit">회원 등록</button>
    </form>
  );
}

export default UserForm;
