import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './LectureSurvey.css';
import { useParams } from 'react-router-dom';

function LectureSurvey({ onBack, onComplete }) {
    const { courseId } = useParams(); // URLÏóêÏÑú courseId Í∞ÄÏ†∏Ïò§Í∏∞
    const [survey, setSurvey] = useState([]);
    const [responses, setResponses] = useState({});
    const [submitted, setSubmitted] = useState(false);
    const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState("");
    const token = localStorage.getItem("token"); // ‚úÖ JWT ÌÜ†ÌÅ∞ Í∞ÄÏ†∏Ïò§Í∏∞

    // ‚úÖ APIÏóêÏÑú ÏÑ§Î¨∏ Îç∞Ïù¥ÌÑ∞ Î∂àÎü¨Ïò§Í∏∞
    useEffect(() => {
        if (!token) {
            console.warn("‚ö†Ô∏è ÌÜ†ÌÅ∞Ïù¥ ÏóÜÏäµÎãàÎã§. Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
            setError("Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
            setLoading(false);
            return;
        }

        const fetchSurvey = async () => {
            try {
                const response = await axios.get(`/api/surveys/${courseId}`, {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setSurvey(response.data.questions);
                setLoading(false);
            } catch (error) {
                console.error("üö® ÏÑ§Î¨∏ Îç∞Ïù¥ÌÑ∞ Î∂àÎü¨Ïò§Í∏∞ Ïò§Î•ò:", error);
                setError("ÏÑ§Î¨∏ Îç∞Ïù¥ÌÑ∞Î•º Î∂àÎü¨Ïò§Îäî Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
                setLoading(false);
            }
        };

        fetchSurvey();
    }, [courseId, token]);

    // ‚úÖ Í∏∞Ï°¥ ÏÑ§Î¨∏ ÏùëÎãµ Î∂àÎü¨Ïò§Í∏∞ (ÏÇ¨Ïö©ÏûêÍ∞Ä Ïù¥ÎØ∏ ÏùëÎãµÌïú Í≤ΩÏö∞)
    useEffect(() => {
        if (!token) return;

        const fetchUserResponses = async () => {
            try {
                const response = await axios.get(`/api/surveys/${courseId}/responses`, {
                    headers: { Authorization: `Bearer ${token}` }
                });

                if (response.data) {
                    setResponses(response.data);
                    setSubmitted(true);
                }
            } catch (error) {
                console.warn("‚ö†Ô∏è Í∏∞Ï°¥ ÏùëÎãµ Îç∞Ïù¥ÌÑ∞Î•º Î∂àÎü¨Ïò§ÏßÄ Î™ªÌñàÏäµÎãàÎã§.", error);
            }
        };

        fetchUserResponses();
    }, [courseId, token]);

    const handleResponseChange = (questionId, value) => {
        setResponses(prev => ({ ...prev, [questionId]: value }));
    };

    const handleSubmit = async () => {
        if (!isAllAnswered) {
            alert("‚ùå Î™®Îì† ÏßàÎ¨∏Ïóê ÎãµÎ≥ÄÌï¥Ïïº Ìï©ÎãàÎã§!");
            return;
        }

        try {
            await axios.post(`/api/surveys/submit`, {
                courseId: courseId,
                responses: responses
            }, {
                headers: { Authorization: `Bearer ${token}` }
            });

            setSubmitted(true);
            alert("‚úÖ ÏÑ§Î¨∏Ïù¥ ÏÑ±Í≥µÏ†ÅÏúºÎ°ú Ï†úÏ∂úÎêòÏóàÏäµÎãàÎã§!");
            if (onComplete) {
                onComplete();
            }
        } catch (error) {
            console.error("üö® ÏÑ§Î¨∏ Ï†úÏ∂ú Ïò§Î•ò:", error);
            alert("ÏÑ§Î¨∏ Ï†úÏ∂ú Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
        }
    };

    if (loading) return <p>üì° ÏÑ§Î¨∏ Îç∞Ïù¥ÌÑ∞Î•º Î∂àÎü¨Ïò§Îäî Ï§ë...</p>;
    if (error) return <p className="error-message">{error}</p>;
    if (!survey || survey.length === 0) return <p>Í∞ïÏùò ÏÑ§Î¨∏ÏùÑ Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§.</p>;

    const currentQuestion = survey[currentQuestionIndex];
    const totalQuestions = survey.length;

    const isAllAnswered = survey.every(q => {
        const response = responses[q.question_id];
        return response !== undefined && response !== null && response !== '' &&
               (!(q.type === "checkbox") || (Array.isArray(response) && response.length > 0));
    });

    return (
        <div className="survey-container">
            <h2>Í∞ïÏùò ÏÑ§Î¨∏</h2>
            {submitted ? (
                <>
                    <p className="submit-result">üéâ ÏÑ§Î¨∏Ïù¥ Ï†úÏ∂úÎêòÏóàÏäµÎãàÎã§! Í∞êÏÇ¨Ìï©ÎãàÎã§.</p>
                    <button className="back-btn" onClick={onBack}>Î™©Î°ùÏúºÎ°ú ÎèåÏïÑÍ∞ÄÍ∏∞</button>
                </>
            ) : (
                <>
                    <div className="survey-item">
                        <p>{currentQuestion.text}</p>
                        {currentQuestion.type === "radio" ? (
                            <div>
                                {currentQuestion.options.map(option => (
                                    <label key={option} className="survey-option">
                                        <input
                                            type="radio"
                                            name={`question-${currentQuestion.question_id}`}
                                            value={option}
                                            checked={responses[currentQuestion.question_id] === option}
                                            onChange={() => handleResponseChange(currentQuestion.question_id, option)}
                                        />
                                        {option}
                                    </label>
                                ))}
                            </div>
                        ) : currentQuestion.type === "checkbox" ? (
                            <div>
                                {currentQuestion.options.map(option => (
                                    <label key={option} className="survey-option">
                                        <input
                                            type="checkbox"
                                            value={option}
                                            onChange={(e) => {
                                                const prevValues = responses[currentQuestion.question_id] || [];
                                                const newValues = e.target.checked
                                                    ? [...prevValues, option]
                                                    : prevValues.filter(val => val !== option);
                                                handleResponseChange(currentQuestion.question_id, newValues);
                                            }}
                                            checked={responses[currentQuestion.question_id]?.includes(option) || false}
                                        />
                                        {option}
                                    </label>
                                ))}
                            </div>
                        ) : currentQuestion.type === "text" ? (
                            <textarea
                                rows="3"
                                placeholder="ÏùòÍ≤¨ÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî..."
                                value={responses[currentQuestion.question_id] || ''}
                                onChange={(e) => handleResponseChange(currentQuestion.question_id, e.target.value)}
                            ></textarea>
                        ) : null}
                    </div>

                    <div className="navigation-buttons">
                        <button className="nav-btn" onClick={() => setCurrentQuestionIndex(prev => prev - 1)} disabled={currentQuestionIndex === 0}>
                            Ïù¥Ï†Ñ ÏßàÎ¨∏
                        </button>
                        {currentQuestionIndex < totalQuestions - 1 ? (
                            <button className="nav-btn" onClick={() => setCurrentQuestionIndex(prev => prev + 1)}>
                                Îã§Ïùå ÏßàÎ¨∏
                            </button>
                        ) : (
                            <button className="submit-btn" onClick={handleSubmit}>
                                Ï†úÏ∂ú
                            </button>
                        )}
                    </div>
                </>
            )}
        </div>
    );
}

export default LectureSurvey;
