import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./ProfessorExamPage.css";

const ProfessorExamPage = () => {
  const [courses, setCourses] = useState([]);
  const [selectedCourseId, setSelectedCourseId] = useState("");
  const [exams, setExams] = useState([]);
  const [newExam, setNewExam] = useState({ title: "", description: "", date: "", total_points: "", examType: "Ï§ëÍ∞ÑÍ≥†ÏÇ¨" });
  const [editingExam, setEditingExam] = useState(null); // ÏàòÏ†ï Ï§ëÏù∏ ÏãúÌóò ID Ï†ÄÏû•
  const [updatedExam, setUpdatedExam] = useState({}); // ÏàòÏ†ïÌï† ÏãúÌóò Îç∞Ïù¥ÌÑ∞
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [professorId, setProfessorId] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchProfessorAndCourses = async () => {
      try {
        const token = localStorage.getItem("token");
        if (!token) {
          setError("Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
          return;
        }

        const userResponse = await axios.get("http://localhost:8090/api/users/me", {
          headers: { Authorization: `Bearer ${token}` },
        });

        if (!userResponse.data || !userResponse.data.id) {
          setError("ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥Î•º Í∞ÄÏ†∏Ïò¨ Ïàò ÏóÜÏäµÎãàÎã§.");
          return;
        }

        setProfessorId(userResponse.data.id);

        const coursesResponse = await axios.get("http://localhost:8090/api/courses/professor", {
          headers: { Authorization: `Bearer ${token}` },
        });

        setCourses(coursesResponse.data);
        if (coursesResponse.data.length > 0) {
          setSelectedCourseId(coursesResponse.data[0].id);
        }
      } catch (error) {
        console.error("ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ ÎòêÎäî Í∞ïÏùò Î™©Î°ù Í∞ÄÏ†∏Ïò§Í∏∞ Ïã§Ìå®:", error);
        setError("ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥Î•º Î∂àÎü¨Ïò§Îäî Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
      }
    };

    fetchProfessorAndCourses();
  }, []);

  const fetchExams = async () => {
    if (!selectedCourseId) return;

    try {
      setLoading(true);
      const token = localStorage.getItem("token");

      const response = await axios.get(`http://localhost:8090/api/exams?courseId=${selectedCourseId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      console.log("üìå Í∞ÄÏ†∏Ïò® ÏãúÌóò Î™©Î°ù:", response.data);
      setExams(response.data);
    } catch (error) {
      console.error("‚ùå ÏãúÌóò Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:", error.response ? error.response.data : error.message);
      setExams([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchExams();
  }, [selectedCourseId]);

  const handleAddExam = async () => {
    if (!selectedCourseId) {
      alert("ÏãúÌóòÏùÑ Ï∂îÍ∞ÄÌï† Í∞ïÏùòÎ•º ÏÑ†ÌÉùÌïòÏÑ∏Ïöî.");
      return;
    }

    if (!professorId) {
      alert("Î°úÍ∑∏Ïù∏Îêú ÍµêÏàòÏùò Ï†ïÎ≥¥Î•º Î∂àÎü¨Ïò¨ Ïàò ÏóÜÏäµÎãàÎã§.");
      return;
    }

    try {
      const token = localStorage.getItem("token");

      const response = await axios.post(
        `http://localhost:8090/api/exams/${professorId}/${selectedCourseId}`,
        {
          title: newExam.title,
          description: newExam.description,
          examDate: newExam.date,
          totalPoints: newExam.total_points ? parseFloat(newExam.total_points) : 0,
          courseId: selectedCourseId,
          examType: newExam.examType,
        },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      console.log("‚úÖ ÏãúÌóò ÏÉùÏÑ± ÏÑ±Í≥µ:", response.data);
      alert("ÏãúÌóòÏù¥ ÏÑ±Í≥µÏ†ÅÏúºÎ°ú ÏÉùÏÑ±ÎêòÏóàÏäµÎãàÎã§!");

      await fetchExams();

      setNewExam({ title: "", description: "", date: "", total_points: "", examType: "Ï§ëÍ∞ÑÍ≥†ÏÇ¨" });
    } catch (error) {
      console.error("‚ùå ÏãúÌóò Ï∂îÍ∞Ä Ïã§Ìå®:", error.response?.data || error.message);
      alert("ÏãúÌóò Ï∂îÍ∞ÄÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§. Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî.");
    }
  };

  const handleEditExam = async () => {
    if (!editingExam) return;

    try {
      const token = localStorage.getItem("token");

      await axios.put(
        `http://localhost:8090/api/exams/${editingExam.id}`,
        {
          title: editingExam.title,
          description: editingExam.description,
          totalPoints: editingExam.totalPoints ? parseFloat(editingExam.totalPoints) : 0,
        },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      alert("ÏãúÌóòÏù¥ ÏÑ±Í≥µÏ†ÅÏúºÎ°ú ÏàòÏ†ïÎêòÏóàÏäµÎãàÎã§!");
      setEditingExam(null);
      await fetchExams();
