import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import axios from "axios";
import "./AnnouncementsList.css";

const AnnouncementsList = () => {
  const { courseId } = useParams();
  const [announcements, setAnnouncements] = useState([]);
  const [token, setToken] = useState("");
  const [user, setUser] = useState(null);

  useEffect(() => {
    const storedToken = localStorage.getItem("token");
    if (storedToken) {
      setToken(storedToken);
    }
  }, []);

  useEffect(() => {
    if (!token) return;

    const fetchUserAndAnnouncements = async () => {
      try {
        const userResponse = await axios.get("http://localhost:8090/api/users/me", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setUser(userResponse.data);

        if (!courseId) return;

        const response = await axios.get(`http://localhost:8090/courses/${courseId}/announcements`, {
          headers: { Authorization: `Bearer ${token}` },
        });

        setAnnouncements(response.data);
      } catch (error) {
        console.error("üì¢ Í≥µÏßÄÏÇ¨Ìï≠ Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:", error);
      }
    };

    fetchUserAndAnnouncements();
  }, [courseId, token]);

  const handleDelete = async (announcementId) => {
    if (!user || !courseId) return;

    const confirmDelete = window.confirm("Ï†ïÎßêÎ°ú Ïù¥ Í≥µÏßÄÏÇ¨Ìï≠ÏùÑ ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå?");
    if (!confirmDelete) return;

    try {
      await axios.delete(`http://localhost:8090/api/courses/${courseId}/announcements/${announcementId}`, {
        headers: { Authorization: `Bearer ${token}` },
        params: { memberId: user.id },
      });

      setAnnouncements((prev) => prev.filter((a) => a.id !== announcementId));
      alert("Í≥µÏßÄÏÇ¨Ìï≠Ïù¥ ÏÇ≠Ï†úÎêòÏóàÏäµÎãàÎã§.");
    } catch (error) {
      console.error("‚ùå Í≥µÏßÄÏÇ¨Ìï≠ ÏÇ≠Ï†ú Ïã§Ìå®:", error);
      alert("Í≥µÏßÄÏÇ¨Ìï≠ ÏÇ≠Ï†úÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.");
    }
  };

  return (
    <div className="announcements-container">
      <h2 className="announcements-title">üì¢ Í≥µÏßÄÏÇ¨Ìï≠ Î™©Î°ù</h2>

      <table className="announcements-table">
        <thead>
          <tr>
            <th>Î≤àÌò∏</th>
            <th>Ï†úÎ™©</th>
            <th>ÏûëÏÑ±Ïûê</th>
            <th>ÏûëÏÑ±Ïùº</th>
            {user?.role === "PROFESSOR" || user?.role === "ADMIN" ? (
              <>
                <th>ÏàòÏ†ï</th>
                <th>ÏÇ≠Ï†ú</th>
              </>
            ) : null}
          </tr>
        </thead>
        <tbody>
          {announcements.length > 0 ? (
            announcements.map((announcement, index) => (
              <tr key={announcement.id}>
                <td>{index + 1}</td>
                <td>
                  <Link to={`/courses/${courseId}/announcements/${announcement.id}`} className="announcement-link">
                    {announcement.title || "Ï†úÎ™© ÏóÜÏùå"}
                  </Link>
                </td>
                <td>{announcement.authorName || "ÏûëÏÑ±Ïûê ÏóÜÏùå"}</td>
                <td>{announcement.createdAt ? new Date(announcement.createdAt).toLocaleDateString() : "ÎÇ†Ïßú ÏóÜÏùå"}</td>

                {/* ÍµêÏàò ÎòêÎäî Í¥ÄÎ¶¨ÏûêÎßå ÏàòÏ†ï/ÏÇ≠Ï†ú Î≤ÑÌäº ÌëúÏãú */}
                {(user?.role === "PROFESSOR" || user?.role === "ADMIN") && (
                  <>
                    <td className="announcement-edit-cell">
                      <Link to={`/courses/${courseId}/announcements/${announcement.id}/edit`} className="announcement-edit-btn">
                        ÏàòÏ†ï
                      </Link>
                    </td>
                    <td className="announcement-delete-cell">
                      <button className="announcement-delete-btn" onClick={() => handleDelete(announcement.id)}>
                        ÏÇ≠Ï†ú
                      </button>
                    </td>
                  </>
                )}
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan={user?.role === "PROFESSOR" || user?.role === "ADMIN" ? 6 : 4} style={{ textAlign: "center", padding: "20px" }}>
                üì¢ Îì±Î°ùÎêú Í≥µÏßÄÏÇ¨Ìï≠Ïù¥ ÏóÜÏäµÎãàÎã§.
              </td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default AnnouncementsList;
